INSERT INTO classes_comptes (nbr, typesclasse_id, name, description)
VALUES
('classe 1', 1 ,'Capitaux propres', 'Cette classe regroupe les comptes lies aux fonds propres de l''entreprise, y compris le capital social, les reserves, les benefices non distribues, etc. Elle reflete la partie de la valeur de l''entreprise detenue par ses actionnaires ou proprietaires.'),
('classe 2', 1, 'Immobilisations', 'Cette classe regroupe les comptes lies aux actifs immobilises de l''entreprise, tels que les biens immobiliers, les equipements, les machines, les vehicules, etc. Ces actifs sont generalement detenus a long terme et sont utilises dans les activites operationnelles de l''entreprise.'),
('classe 3', 1, 'Stocks et en cours', 'Cette classe regroupe les comptes lies aux stocks de marchandises, de matieres premieres ou de produits en cours de production. Elle reflete la valeur des biens detenus par l''entreprise en vue de la vente ou de la transformation ulterieure.'),
('classe 4', 1, 'Tiers', 'Cette classe regroupe les comptes lies aux relations de l''entreprise avec des tiers, tels que les clients, les fournisseurs, les partenaires commerciaux, etc. Elle comprend generalement les comptes clients, les comptes fournisseurs, les dettes a court terme, etc.'),
('classe 5', 1, 'Tresorerie', 'Cette classe regroupe les comptes lies aux liquidites de l''entreprise, y compris les comptes bancaires, la caisse, les placements a court terme, etc. Elle reflete la disponibilite immediate des ressources financieres de l''entreprise.'),
('classe 6', 1, 'Charges des activites ordinaires', 'Cette classe regroupe les comptes lies aux charges d''exploitation de l''entreprise, telles que les salaires, les loyers, les fournitures de bureau, les frais generaux, etc. Elle reflete les couts associes a la realisation des activites principales de l''entreprise.'),
('classe 7', 1, 'Produits des activites ordinaires', 'Cette classe regroupe les comptes lies aux revenus generes par les activites principales de l''entreprise, tels que les ventes de produits ou de services, les revenus de location, les interets percus, etc. Elle reflete les flux de revenus provenant des operations commerciales regulieres de l''entreprise.'),
('classe 8', 1, 'Autres charges et produits', 'Cette classe regroupe les comptes lies aux charges et aux produits qui ne sont pas directement lies aux activites ordinaires de l''entreprise. Elle peut inclure des elements tels que les charges financieres, les produits exceptionnels, les charges exceptionnelles, etc.'),
('classe 9', 1, 'Engagements hors bilan', 'Cette classe regroupe les comptes lies aux engagements de l''entreprise qui ne sont pas directement refletes dans son bilan, mais qui peuvent avoir un impact financier significatif. Elle comprend generalement les garanties, les contrats de location, les engagements de pension, etc.'),
('classe 1', 2 ,'Ressources durables', 'Cette classe regroupe les comptes lies aux ressources durables de l''entite a but non lucratif, tels que les fonds propres, les subventions permanentes, les dons affectes a long terme, etc. Elle reflete les ressources financieres qui sont destinees a etre utilisees sur le long terme pour soutenir les activites de l''entite.'),
('classe 2', 2 ,'Actif immobilise', 'Cette classe regroupe les comptes lies aux actifs immobilises detenus par l''entite a but non lucratif, tels que les terrains, les batiments, les equipements, les vehicules, etc. Ces actifs sont generalement detenus a long terme et utilises dans le cadre des activites operationnelles de l''entite.'),
('classe 3', 2 ,'Stocks', 'Cette classe regroupe les comptes lies aux stocks detenus par l''entite a but non lucratif, tels que les fournitures de bureau, les equipements de premiere necessite, les materiaux pour les programmes, etc. Elle reflete la valeur des biens detenus par l''entite pour une utilisation future dans ses activites.'),
('classe 4', 2 ,'Tiers', 'Cette classe regroupe les comptes lies aux relations de l''entite a but non lucratif avec des tiers, tels que les donateurs, les fournisseurs, les partenaires, etc. Elle comprend generalement les comptes de creances et de dettes a court terme, ainsi que les comptes de produits et de charges lies aux transactions avec des tiers.'),
('classe 5', 2 ,'Tresorerie resorerie', 'Cette classe regroupe les comptes lies a la tresorerie de l''entite a but non lucratif, y compris les comptes bancaires, la caisse, les placements a court terme, etc. Elle reflete la disponibilite immediate des ressources financieres de l''entite.'),
('classe 6', 2 ,'Charges des activites ordinaires', 'Cette classe regroupe les comptes lies aux charges d''exploitation de l''entite a but non lucratif, telles que les salaires, les frais administratifs, les frais de programme, les frais de collecte de fonds, etc. Elle reflete les couts associes a la realisation des activites principales de l''entite.'),
('classe 7', 2 ,'Produits des activites ordinaires', 'Cette classe regroupe les comptes lies aux revenus generes par les activites principales de l''entite a but non lucratif, tels que les dons, les subventions, les revenus des programmes, les revenus de collecte de fonds, etc. Elle reflete les flux de revenus provenant des operations regulieres de l''entite.'),
('classe 8', 2 ,'Autres charges et produits', 'Cette classe regroupe les comptes lies aux charges et aux produits qui ne sont pas directement lies aux activites ordinaires de l''entite a but non lucratif. Elle peut inclure des elements tels que les charges financieres, les produits exceptionnels, les charges exceptionnelles, etc.'),
('classe 9', 2 ,'Contributions volontaires', 'Cette classe regroupe les comptes lies aux contributions volontaires recues par l''entite a but non lucratif, telles que les dons non affectes, les legs, les dons en nature, etc. Elle reflete les contributions financieres ou materielles recues de tiers pour soutenir les activites de l''entite.');