INSERT INTO comptes (classes_comptes_id, name, nbr, description)
VALUES
(1, 'Capital', '10', 'Cet ensemble de comptes regroupe les comptes lies au capital social de l''entreprise, y compris les comptes pour les differentes classes d''actions emises, les primes d''emission, les ajustements de valeur, etc.'),
(1, 'Reserves', '11','Cet ensemble de comptes regroupe les comptes qui enregistrent les differentes reserves de l''entreprise, telles que les reserves legales, les reserves statutaires, les reserves pour investissement, les reserves pour actions propres, etc.' ),
(1, 'Report a nouveau','12', 'Cet ensemble de comptes regroupe les comptes qui refletent les soldes reportes d''une annee a l''autre, y compris les benefices non distribues ou les pertes accumulees des exercices precedents.'),
(1, 'Resultat net de l''exercices','13', 'Cet ensemble de comptes regroupe les comptes qui enregistrent le resultat net de l''entreprise pour la periode comptable en cours, comprenant les produits, les charges, les produits financiers, les charges financieres, etc.'),
(1, 'Subvention d''investissement', '14', ' Le groupe de compte (Subvention d''investissement) enregistre les fonds alloues pour financer des investissements specifiques, provenant souvent d''entites gouvernementales ou privees. Ces subventions sont initialement comptabilisees comme des produits et transferees progressivement vers les comptes d''actifs concernes lors de leur utilisation, assurant ainsi la transparence financiere de l''entreprise. '),
(1, 'Provision reglementees et fonds assimiles','15','Cet ensemble de comptes regroupe les comptes qui enregistrent les provisions reglementees et les fonds similaires constitues par l''entreprise pour se conformer a des exigences legales ou reglementaires specifiques.'),
(1, 'Emprunts et dettes assimiles', '16', 'Cet ensemble de comptes regroupe les comptes qui enregistrent les emprunts et les dettes contractees par l''entreprise, y compris les dettes a court terme, les dettes a long terme, les emprunts bancaires, les obligations emises, etc.'),
(1, 'Dettes de location aquisition', '17', 'Cet ensemble de comptes regroupe les comptes qui enregistrent les dettes contractees par l''entreprise dans le cadre de contrats de location acquisition, ou les biens loues sont acquis par l''entreprise a la fin de la periode de location.'),
(1, 'Dettes liees a des participations et comptes de liaison des etablissements et societes en participation', '18', 'Cet ensemble de comptes regroupe les comptes qui enregistrent les dettes contractees par l''entreprise en relation avec ses participations dans d''autres entreprises ou ses comptes de liaison avec des etablissements ou des societes en participation.'),
(1, 'Provisions pour risques et charges', '19', 'Cet ensemble de comptes regroupe les comptes qui enregistrent les provisions constituees par l''entreprise pour faire face a des risques ou a des charges probables mais incertains, tels que les provisions pour litiges, les provisions pour pertes sur creances douteuses, etc.'),
(2, 'Immobilisations incorporelles', '21', 'Ce compte regroupe les depenses d''acquisition ou de developpement d''actifs incorporels, tels que les brevets, les marques de commerce, les logiciels, les licences, les droits d''auteur, etc. Ces actifs n''ont pas de substance physique mais ont une valeur pour l''entreprise.'),
(2, 'Terrains', '22', 'Ce compte enregistre la valeur des terrains detenus par l''entreprise, y compris les terrains sur lesquels des batiments ou des installations sont construits, ainsi que les terrains detenus a des fins d''investissement ou de developpement futur.'),
(2, 'Batiment, intallations techniques et agencements', '23', 'Ce compte regroupe les couts d''acquisition, de construction ou d''amenagement des batiments, des installations techniques (telles que les installations de production, les equipements de traitement, etc.) et des agencements interieurs.'),
(2, 'Materiel, mobilier et actifs biologiques', '24', 'Ce compte enregistre la valeur des actifs materiels et mobiliers detenus par l''entreprise, tels que les equipements de bureau, les machines, les vehicules, les meubles, ainsi que les actifs biologiques tels que les animaux ou les cultures agricoles.'),
(2, 'Avances et acomptes verses sur immobilisations financieres', '25', 'Ce compte enregistre les paiements anticipes effectues par l''entreprise pour l''acquisition ou la construction d''immobilisations, mais dont la livraison ou la finalisation n''a pas encore eu lieu.'),
(2, 'Titres de participations', '26', 'Ce compte regroupe les investissements de l''entreprise dans des participations dans d''autres entreprises, generalement sous forme de actions ou de parts sociales. Il enregistre la valeur comptable de ces investissements.'),
(2, 'Autres immobilisations financieres', '27', 'Ce compte regroupe les autres investissements financiers a long terme de l''entreprise qui ne sont pas classes comme des titres de participation, tels que les prets a long terme, les depots de garantie, les creances a long terme, etc.'),
(2, 'Amortissements', '28', 'Ce compte enregistre les amortissements accumules sur les actifs immobilises de l''entreprise, representant la repartition systematique des couts d''acquisition ou de construction de ces actifs sur leur duree de vie utile estimee.'),
(2, 'Depreciation des immobilisations', '29', 'Ce compte enregistre les depreciations accumulees sur les actifs immobilises de l''entreprise, refletant la diminution de la valeur comptable de ces actifs en raison de l''obsolescence, de la deterioration, ou de toute autre raison jugee necessaire.'),
(3, 'Marchandise', '31', 'Ce compte enregistre la valeur des marchandises achetees par l''entreprise pour la revente dans le cours normal de ses activites commerciales. Il comprend les biens destines a etre revendus tels quels, sans transformation significative.'),
(3, 'Matieres premieres et fournitures', '32', 'Ce compte regroupe les matieres premieres et les fournitures utilisees par l''entreprise dans le processus de production de biens ou de services. Il comprend les matieres premieres qui sont incorporees dans les produits finis ainsi que les fournitures consommables necessaires a la production.'),
(3, 'Autres approvisionnements', '33', 'Ce compte enregistre les autres types d''approvisionnements ou de fournitures utilises par l''entreprise dans ses activites, tels que les pieces de rechange, les consommables de maintenance, les emballages, etc.'),
(3, 'Produit en cours', '34', 'Ce compte enregistre la valeur des produits en cours de fabrication ou de transformation dans l''entreprise. Il s''agit des biens qui sont partiellement acheves mais qui ne sont pas encore consideres comme des produits finis prets a etre vendus.'),
(3, 'Services en cours', '35', 'Ce compte enregistre la valeur des services en cours d''execution par l''entreprise pour ses clients. Il s''agit des services qui ont ete commences mais qui ne sont pas encore acheves a la fin de la periode comptable.'),
(3, 'Produits finis', '36', 'Ce compte enregistre la valeur des produits entierement acheves par l''entreprise et prets a etre vendus a des clients. Il represente la valeur des biens fabriques ou assembles qui sont disponibles pour la vente.'),
(3, 'Produits intermediaires et residuels', '37', 'Ce compte regroupe les produits intermediaires ou les residus qui sont generes dans le processus de production de l''entreprise. Il peut s''agir de sous produits, de dechets ou de materiaux recyclables issus de la production principale de l''entreprise.'),
(3, 'Stocks en cours de route, en consignation ou en depot', '38', 'Ce compte enregistre la valeur des stocks detenus par l''entreprise qui sont en transit, en consignation chez des tiers ou en depot dans des entrepots externes. Il represente les biens qui sont detenus temporairement en dehors des installations de l''entreprise.'),
(3, 'Depreciation des stockes en cours de production', '39', 'Ce compte enregistre les depreciations accumulees sur les stocks en cours de production de l''entreprise, refletant la diminution de la valeur comptable de ces stocks en raison de l''obsolescence, de la deterioration ou de toute autre raison jugee necessaire.'),
(4, 'Fournisseur et comptes rattaches', '40', 'Ce compte enregistre les dettes de l''entreprise envers ses fournisseurs pour l''achat de biens ou de services. Il inclut egalement les comptes rattaches tels que les dettes non echues, les escomptes accordes par les fournisseurs, les avoirs a recevoir, etc.'),
(4, 'Clients et compte rattaches', '41', 'Ce compte enregistre les creances de l''entreprise sur ses clients pour la vente de biens ou de services. Il comprend egalement les comptes rattaches tels que les creances non echues, les escomptes consentis aux clients, les remises a accorder, etc.'),
(4, 'Personnel', '42', 'Ce compte regroupe les comptes relatifs aux transactions financieres impliquant le personnel de l''entreprise, y compris les salaires et les traitements, les charges sociales, les avantages sociaux, les indemnites de conges payes, etc.'),
(4, 'Organisation sociaux', '43', 'Ce compte enregistre les transactions financieres de l''entreprise avec des organisations sociales telles que les syndicats, les caisses de retraite, les fonds de securite sociale, etc.'),
(4, 'Etat et collectivites publiques', '44', 'Ce compte regroupe les transactions financieres de l''entreprise avec l''etat et les collectivites publiques, y compris les impots, les taxes, les cotisations sociales, les amendes, les subventions gouvernementales, etc.'),
(4, 'organismes internationaux', '45', 'Ce compte enregistre les transactions financieres de l''entreprise avec des organismes internationaux tels que l''Union europeenne, les Nations unies, la Banque mondiale, etc.'),
(4, 'Apporteurs, associes et groupe', '46', 'Ce compte regroupe les comptes lies aux transactions financieres de l''entreprise avec ses actionnaires, ses associes ou d''autres entites du meme groupe economique. Il inclut les capitaux propres, les dividendes a payer, les participations dans d''autres societes, etc.'),
(4, 'Debiteurs et crediteur divers', '47', 'Ce compte enregistre les creances et les dettes de l''entreprise qui ne sont pas classees dans d''autres categories specifiques. Il peut inclure des transactions diverses telles que les avances et les acomptes, les depots de garantie, les interets courus, etc.'),
(4, 'Creances et dettes Hors Activites Ordinaires (h.a.o)', '48', 'Ce compte regroupe les creances et les dettes de l''entreprise qui ne sont pas liees a ses activites commerciales ordinaires. Il peut inclure des transactions telles que les prets accordes ou recus, les investissements a long terme, les operations de financement hors bilan, etc.'),
(4, 'Depreciation et provisions pour risques a court terme', '49', 'Ce compte enregistre les depreciations et les provisions constituees par l''entreprise pour faire face a des risques ou a des pertes probables a court terme lies a des tiers, tels que les creances douteuses, les litiges en cours, les garanties a honorer, etc.'),
(5, 'Titres de deplacement', '50', 'Ce compte enregistre les investissements de l''entreprise dans des titres financiers a court ou a long terme qui ne sont pas detenus en vue d''etre revendus rapidement. Il comprend des titres tels que les actions, les obligations, les certificats de depot, les fonds communs de placement, etc.'),
(5, 'Valeurs a encaisser', '51', 'Ce compte regroupe les effets de commerce, les cheques, les lettres de change et autres valeurs qui sont encaissables par l''entreprise mais qui n''ont pas encore ete deposees ou encaissees aupres des banques.'),
(5, 'Banques', '52', 'Ce compte enregistre les soldes des comptes bancaires detenus par l''entreprise, y compris les comptes courants, les comptes d''epargne, les comptes de depot a terme, etc.'),
(5, 'Etablissements financiers et assimiles', '53', 'Ce compte regroupe les soldes des comptes detenus par l''entreprise aupres d''etablissements financiers autres que des banques, tels que les societes de credit, les cooperatives de credit, les compagnies d''assurance, etc.'),
(5, 'Instruments de tresorerie', '54', 'Ce compte enregistre les instruments financiers a court terme utilises par l''entreprise pour gerer sa tresorerie, tels que les bons du Tresor, les certificats de depot, les billets a ordre, etc.'),
(5, 'Instruments de monnaie electronique', '55', 'Ce compte enregistre les transactions financieres de l''entreprise realisees au moyen d''instruments de monnaie electronique, tels que les cartes de credit, les cartes de debit, les virements electroniques, etc.'),
(5, 'Banques, credits de tresorerie et descompte', '56', 'Ce compte regroupe les operations financieres de l''entreprise liees aux credits de tresorerie accordes par les banques, ainsi qu''aux operations d''escompte de traites ou de billets a ordre.'),
(5, 'Caisse', '57', 'Ce compte enregistre les especes detenues physiquement par l''entreprise, generalement dans sa caisse ou son coffre fort, et utilisees pour les transactions courantes et les petites depenses.'),
(5, 'Regies d''avance, accreditifs et virements internes', '58', 'Ce compte enregistre les avances consenties par l''entreprise a ses employes ou a des tiers, ainsi que les operations de virements internes entre differents comptes de l''entreprise.'),
(5, 'Depreciation et provisions pour risques a court terme', '59', 'Ce compte enregistre les depreciations et les provisions constituees par l''entreprise pour faire face a des risques ou a des pertes probables a court terme, tels que les creances douteuses, les pertes sur investissements, les litiges en cours, etc.'),
(6, 'Achats et variations de stocks', '60', 'Ce compte enregistre le cout d''achat des biens destines a la revente ou a la production, ainsi que les variations de stocks de marchandises, de matieres premieres et de produits en cours sur une periode donnee.'),
(6, 'Transports', '61', 'Ce compte regroupe les couts lies au transport des biens achetes ou vendus par l''entreprise, y compris les frais de transport, les frais de douane, les frais d''expedition, etc.'),
(6, 'Services Exterieurs', '62', 'Ce compte enregistre les depenses engagees par l''entreprise pour l''acquisition de services fournis par des tiers, tels que les honoraires professionnels, les frais de consultation, les frais de maintenance, etc.'),
(6, 'Autres services exterieurs', '63', 'Ce compte regroupe les autres depenses engagees par l''entreprise pour l''acquisition de services fournis par des tiers, qui ne sont pas incluses dans le compte "Services exterieurs". Il peut inclure des frais tels que les frais de publicite, les frais de communication, les frais de formation, etc.'),
(6, 'Impot et Taxes', '64', 'Ce compte enregistre les impots et les taxes payes par l''entreprise au cours de l''exercice financier, y compris les impots sur le revenu, les taxes de vente, les taxes foncieres, les taxes sur la valeur ajoutee, etc.'),
(6, 'Autres charges', '65', 'Ce compte regroupe les charges qui ne sont pas classees dans d''autres categories specifiques de charges. Il peut inclure des depenses telles que les pertes sur creances douteuses, les charges exceptionnelles, les frais de demenagement, etc.'),
(6, 'Charges de personnel', '66', 'Ce compte enregistre les depenses liees a la remuneration et aux avantages sociaux du personnel de l''entreprise, y compris les salaires, les cotisations sociales, les indemnites de conges payes, les avantages sociaux, etc.'),
(6, 'Frais financiers et charges assimilees', '67', 'Ce compte regroupe les frais financiers engages par l''entreprise, tels que les interets sur les emprunts, les frais bancaires, les commissions sur les transactions financieres, etc.'),
(6, 'Dotation aux amortissements', '68', 'Ce compte enregistre les montants alloues par l''entreprise pour amortir ses actifs immobilises sur une periode donnee, afin de refleter leur depreciation progressive due a l''usage et a l''obsolescence.'),
(6, 'Dotations aux provisions et aux depreciations', '69', 'Ce compte regroupe les montants alloues par l''entreprise pour constituer des provisions destinees a couvrir des risques ou des pertes probables, ainsi que des depreciations d''actifs pour refleter leur valeur reduite ou leur obsolescence.'),
(7, 'Vente', '70', 'Ce compte enregistre le produit des ventes de biens ou de services effectuees par l''entreprise au cours de l''exercice financier. Il inclut le chiffre d''affaires realise par l''entreprise a partir de ses activites principales.'),
(7, 'Subventions d''exploitation', '71', 'Ce compte enregistre les subventions recues par l''entreprise pour financer ses activites operationnelles courantes. Ces subventions peuvent provenir d''organismes gouvernementaux, d''organisations internationales ou d''autres entites.'),
(7, 'Production immobilisee', '72', 'Ce compte enregistre la valeur des biens produits par l''entreprise pour son propre usage ou sa propre consommation, plutot que pour la vente sur le marche. Il peut s''agir, par exemple, de biens fabriques pour etre utilises dans le processus de production de l''entreprise.'),
(7, 'Variation de stocks de biens et de services produits', '73', 'Ce compte enregistre les variations de stocks de biens et de services produits par l''entreprise au cours de l''exercice financier. Il reflete les changements dans la quantite et la valeur des stocks detenus par l''entreprise en raison de la production ou de la consommation.'),
(7, 'Autres produits', '75', 'Ce compte regroupe les revenus de l''entreprise qui ne sont pas lies aux ventes de biens ou de services, ni aux subventions d''exploitation. Il peut inclure des revenus tels que les revenus de location, les interets sur les placements, les gains sur les cessions d''actifs, etc.'),
(7, 'Revenus financieres et assimiles', '77', 'Ce compte enregistre les revenus de l''entreprise provenant de ses activites financieres, telles que les interets sur les placements, les dividendes sur les actions detenues, les gains sur les operations de change, etc.'),
(7, 'Transferts de charges', '78', 'Ce compte enregistre les transferts de charges entre differents secteurs ou departements de l''entreprise. Il permet de repartir les couts entre les differentes entites ou activites de l''entreprise.'),
(7, 'Reprises de provision, de depecriations et autres', '79', 'Ce compte enregistre les montants repris par l''entreprise des provisions et des depreciations precedemment constituees, ainsi que d''autres ajustements de comptabilisation qui n''entrent pas dans d''autres categories specifiques de revenus.'),
(8, 'Valeurs comptables des cessions d''immobilisations', '81', 'Ce compte enregistre la valeur comptable des immobilisations cedees par l''entreprise au cours de l''exercice financier. Il reflete le montant net obtenu par l''entreprise apres avoir deduit les amortissements cumules et eventuellement les depreciations enregistrees sur ces immobilisations.'),
(8, 'Produits des cessions d''immobilisations', '82', 'Ce compte enregistre les produits generes par les ventes d''immobilisations de l''entreprise au cours de l''exercice financier. Il inclut les gains realises sur ces cessions, calcules comme la difference entre le produit de la vente et la valeur comptable de l''immobilisation cedee.'),
(8, 'Charges hors activites ordinaires', '83', 'Ce compte regroupe les charges exceptionnelles ou extraordinaires engagees par l''entreprise en dehors de ses activites ordinaires. Il peut inclure des depenses telles que les pertes sur creances irrecouvrables, les amendes et penalites, les frais de restructuration, etc.'),
(8, 'Produits hors activites ordinaires', '84', 'Ce compte enregistre les produits exceptionnels ou extraordinaires generes par l''entreprise en dehors de ses activites ordinaires. Il peut inclure des revenus tels que les gains sur la vente d''actifs non courants, les indemnites de sinistre, les subventions non liees a l''exploitation, etc.'),
(8, 'Dotation hors activites ordinaires', '85', 'Ce compte enregistre les dotations aux amortissements ou aux provisions effectuees par l''entreprise pour des raisons exceptionnelles ou extraordinaires, qui ne sont pas liees a ses activites ordinaires. Il peut s''agir, par exemple, de dotations pour depreciation d''actifs non courants, de provisions pour litiges, etc.'),
(8, 'Reprises hors activites ordinaires', '86', 'Ce compte enregistre les montants repris par l''entreprise des dotations aux amortissements ou aux provisions precedemment constituees pour des raisons exceptionnelles ou extraordinaires. Il permet de corriger les estimations anterieures qui ne sont plus valides.'),
(8, 'Participations des travailleurs', '87', 'Ce compte enregistre les montants verses par l''entreprise aux travailleurs ou aux salaries sous forme de participation aux benefices, de participation aux resultats, de primes d''interessement, ou d''autres formes de remuneration variable liees aux performances de l''entreprise.'),
(8, 'Subventions d''equilibre', '88', 'Ce compte enregistre les subventions accordees a l''entreprise pour compenser des pertes ou des desequilibres financiers resultant de facteurs externes ou de circonstances exceptionnelles. Ces subventions visent a soutenir la viabilite economique de l''entreprise dans des situations difficiles.'),
(8, 'Impots sur le resultat', '89', 'Ce compte enregistre les impots sur le resultat payes par l''entreprise au titre de l''exercice financier, y compris l''impot sur les societes, les impots differes, les impots locaux, etc. Il reflete la charge fiscale supportee par l''entreprise sur ses benefices imposables.'),
(9, 'Engagements obtenus et engagements accordes', '90', 'Ce compte enregistre les engagements contractes par l''entreprise envers des tiers (engagements obtenus) ainsi que les engagements pris par des tiers envers l''entreprise (engagements accordes). Il peut inclure des engagements financiers, juridiques, commerciaux, etc.'),
(9, 'Contreparties des engagements', '91', 'Ce compte enregistre les contreparties des engagements contractes par l''entreprise. Il reflete les obligations ou les droits lies aux engagements pris ou recus par l''entreprise vis a vis des tiers.'),
(9, 'Comptes reflechis', '92', 'Ce compte regroupe les comptes comptables utilises pour refleter des operations ou des evenements qui ne correspondent pas a des transactions reelles, mais qui sont necessaires pour des raisons de presentation ou de comptabilisation. Ils sont generalement utilises pour des ajustements ou des reclassements comptables.'),
(9, 'Comptes de reclassments', '93', 'Ce compte regroupe les comptes comptables utilises pour reclasser des operations ou des elements comptables d''un compte a un autre. Ils sont utilises pour corriger des erreurs de comptabilisation, pour ajuster des soldes de comptes, ou pour regrouper des operations de meme nature.'),
(9, 'Comptes de couts', '94', 'Ce compte enregistre les couts supportes par l''entreprise dans le cadre de ses activites operationnelles. Il peut inclure les couts de production, les couts d''achat, les couts de distribution, les couts administratifs, etc.'),
(9, 'Comptes de stocks', '95', 'Ce compte enregistre la valeur des stocks detenus par l''entreprise, tels que les matieres premieres, les produits en cours de production, les produits finis, etc. Il reflete la valeur des biens detenus en vue de la vente ou de l''utilisation dans le processus de production.'),
(9, 'Comptes d''ecarts sur couts preetablis', '96', 'Ce compte enregistre les ecarts constates entre les couts reels et les couts preetablis ou prevus par l''entreprise. Il permet d''analyser les variations de couts et d''identifier les ecarts par rapport aux previsions ou aux normes etablies.'),
(9, 'Comptes de differences de traitement comptable', '97', 'Ce compte enregistre les differences de traitement comptable entre les principes comptables utilises par l''entreprise et les normes comptables generalement acceptees. Il est utilise pour corriger les erreurs comptables ou pour ajuster les soldes de comptes afin de se conformer aux regles comptables applicables.'),
(9, 'Comptes de resultats', '98', 'Ce compte regroupe les comptes comptables utilises pour enregistrer les resultats financiers de l''entreprise, tels que les produits, les charges, les profits, les pertes, etc. Il permet de calculer le resultat net de l''entreprise pour une periode donnee.'),
(9, 'Comptes de liaisons internes', '99', 'Ce compte enregistre les transactions ou les soldes resultant d''operations entre differentes entites ou departements au sein de la meme entreprise. Il est utilise pour suivre les flux financiers ou comptables entre les differentes entites internes de l''entreprise.'),
(10, 'Dotation', '10', 'Ce compte enregistre les dotations aux amortissements, aux depreciations ou aux provisions effectuees par l''entreprise au cours de l''exercice financier. Il permet de constater la depreciation des actifs ou de constituer des reserves pour faire face a des risques ou a des charges futures.'),
(10, 'Reserves', '11', 'Ce compte regroupe les differentes reserves constituees par l''entreprise, telles que les reserves legales, les reserves statutaires, les reserves facultatives, etc. Il permet de capitaliser une partie des benefices de l''entreprise en vue de renforcer sa structure financiere ou de financer des investissements futurs.'),
(10, 'Report a nouveau', '12', 'Ce compte enregistre le solde reporte des resultats non distribues d''un exercice financier a l''autre. Il reflete la partie du resultat net de l''exercice precedent qui n''a pas ete distribuee aux actionnaires ou reinvestie dans l''entreprise.'),
(10, 'Resultat net de l''exercice', '13', 'Ce compte enregistre le resultat net de l''exercice financier, c''est a dire la difference entre les produits et les charges de l''entreprise pour la periode consideree. Il reflete le benefice ou la perte realise par l''entreprise au cours de l''exercice.'),
(10, 'Subvention d''investissement', '14', 'Ce compte enregistre les subventions recues par l''entreprise pour financer des investissements en immobilisations. Il permet de suivre les fonds affectes specifiquement a des projets d''investissement et de les distinguer des autres sources de financement.'),
(10, 'Provisions reglements et fonds assimiles', '15', 'Ce compte regroupe les provisions reglementees ou les fonds assimiles constitues par l''entreprise conformement a des dispositions legales ou reglementaires. Il peut inclure des provisions pour risques specifiques, des fonds de garantie, des fonds de retraite, etc.'),
(10, 'Fonds affectes et provenant de dons ou legs d''immobilisations', '16', 'Ce compte enregistre les fonds affectes ou specifiquement destines a des fins particulieres par des donateurs ou des legs. Il permet de suivre les ressources financieres dediees a des projets ou a des activites specifiques, souvent lies a des dons ou des heritages.'),
(10, 'Fonds reportes', '17', 'Ce compte enregistre les fonds reportes d''un exercice financier a l''autre, qui n''ont pas ete utilises ou affectes a des fins specifiques. Il permet de suivre les ressources financieres non utilisees ou non affectees et de les reporter pour une utilisation ulterieure.'),
(10, 'Emprunts et dettes assimiles', '18', 'Ce compte regroupe les emprunts contractes par l''entreprise aupres de tiers, tels que des banques, des institutions financieres ou des creanciers. Il permet de suivre les engagements financiers de l''entreprise lies a ses activites operationnelles ou a ses investissements.'),
(10, 'Provisions pour risques et charges', '19', 'Ce compte enregistre les provisions constituees par l''entreprise pour faire face a des risques ou a des charges futures dont l''issue ou le montant est incertain. Il permet de provisionner des montants pour couvrir des pertes potentielles ou des engagements eventuels.'),
(11, 'Immobilisations destines a la vente provenant de dons et les non encore recus Usufruit', '20', 'Ce compte regroupe les immobilisations detenues par l''entreprise qui sont destinees a etre vendues dans un avenir proche. Il peut egalement inclure les biens recus par dons ou legs mais qui n''ont pas encore ete officiellement recus, ainsi que les biens pour lesquels l''entreprise detient un usufruit temporaire.'),
(11, 'Immobilisation incorporelles', '21', 'Ce compte regroupe les immobilisations qui ont une valeur pour l''entreprise mais qui ne sont pas des biens materiels. Il peut inclure des elements tels que les brevets, les licences, les marques de commerce, les logiciels, etc.'),
(11, 'Terrains', '22', 'Ce compte enregistre la valeur des terrains detenus par l''entreprise dans le cadre de ses activites. Il inclut le cout d''acquisition des terrains ainsi que les frais connexes tels que les frais de notaire, les frais d''amenagement, etc.'),
(11, 'Batiments, intsallations techniques et agencements', '23', 'Ce compte regroupe la valeur des batiments, des installations techniques et des agencements detenus par l''entreprise pour soutenir ses activites operationnelles. Il comprend les couts lies a la construction, a l''achat ou a l''amenagement de ces biens.'),
(11, 'Materiel, mobilier, et actifs biologiques', '24', 'Ce compte enregistre la valeur du materiel, du mobilier et des actifs biologiques detenus par l''entreprise dans le cadre de ses operations. Il inclut les equipements, les vehicules, le mobilier de bureau, ainsi que les actifs biologiques tels que les cultures, les animaux d''elevage, etc.'),
(11, 'Avances et accomptes verses sur immobilisations', '25', 'Ce compte enregistre les avances ou les acomptes verses par l''entreprise pour l''acquisition ou la construction d''immobilisations. Il permet de suivre les montants payes a l''avance pour des biens ou des services qui seront recus ulterieurement.'),
(11, 'Titres de participation', '26', 'Ce compte regroupe la valeur des titres de participation detenus par l''entreprise dans d''autres entreprises. Il inclut les actions ou les parts sociales detenues dans des societes affiliees ou des filiales, ainsi que les participations dans des entreprises associees.'),
(11, 'autres immobilisation finanieres', '27', 'Ce compte regroupe les autres immobilisations financieres detenues par l''entreprise qui ne sont pas des titres de participation. Il peut inclure des investissements a long terme dans des obligations, des prets a des tiers, des depots a terme, etc.'),
(11, 'Amortissements', '28', 'Ce compte enregistre les montants d''amortissement comptabilises par l''entreprise pour refleter la depreciation des immobilisations incorporelles, corporelles ou financieres. Il permet de repartir le cout des immobilisations sur leur duree d''utilisation estimee.'),
(11, 'Depreciations des immobilisations', '29', 'Ce compte enregistre les depreciations comptabilisees par l''entreprise pour refleter la perte de valeur des immobilisations qui resulte d''evenements ou de circonstances specifiques. Il permet de constater la reduction de la valeur recouvrable des actifs de l''entreprise.'),
(12, 'Biens lies a l''activites', '31', 'Ce compte regroupe les biens detenus par l''entreprise qui sont directement lies a ses activites principales. Il peut inclure les equipements, les outils, les machines, les installations, etc., utilises pour produire des biens ou fournir des services.'),
(12, 'Marchandises, matieres premieres et fournitures liees', '32', 'Ce compte enregistre la valeur des marchandises, des matieres premieres et des fournitures necessaires a la production de biens ou a la prestation de services par l''entreprise. Il comprend les materiaux bruts, les composants, les pieces detachees, etc.'),
(12, 'autres approvisionnements', '33', 'Ce compte regroupe d''autres types d''approvisionnements ou de stocks detenus par l''entreprise qui ne sont pas specifiquement des marchandises ou des matieres premieres. Il peut inclure des fournitures de bureau, des emballages, des consommables, etc.'),
(12, 'Dons en Nature', '34', 'Ce compte enregistre la valeur des dons recus par l''entreprise sous forme de biens plutot que d''argent liquide. Il peut inclure des equipements, des fournitures, des produits, etc., offerts par des tiers a l''entreprise.'),
(12, 'Produits et services en cours', '35', 'Ce compte regroupe les produits et services en cours de production ou de prestation par l''entreprise. Il represente la valeur des biens qui sont en train d''etre fabriques ou des services qui sont en cours de realisation mais qui ne sont pas encore acheves.'),
(12, 'Produits finis', '36', 'Ce compte enregistre la valeur des biens produits par l''entreprise et prets a etre vendus ou distribues a des clients. Il inclut les articles qui ont ete entierement fabriques ou assembles et qui sont prets a etre livres ou mis sur le marche.'),
(12, 'Stocks en cours de route, en consignation ou en depot', '37', 'Ce compte regroupe les stocks qui sont en transit, en consignation chez des tiers ou en depot dans des entrepots ou des magasins. Il represente les biens qui sont en mouvement entre differents lieux ou qui sont temporairement entreposes avant d''etre distribues ou vendus.'),
(12, 'Dons en Nature (hao)', '38', 'Ce compte enregistre les dons en nature recus par l''entreprise qui sont specifiquement destines a des activites hors activites ordinaires (hao). Il peut s''agir de biens ou de services offerts a des fins specifiques, telles que des projets de developpement, des actions caritatives, etc.'),
(12, 'Depreciations des stocks et des production en cours', '39', 'Ce compte enregistre les depreciations comptabilisees par l''entreprise pour refleter la perte de valeur des stocks et des productions en cours. Il permet de constater la reduction de la valeur recouvrable des stocks detenus par l''entreprise.'),
(13, 'Fournisseurs et comptes rattaches', '40', 'Ce compte enregistre les dettes de l''entreprise envers ses fournisseurs pour l''achat de biens ou de services. Il inclut les factures non payees, les achats a credit, les dettes commerciales et tout autre montant du aux fournisseurs. Les comptes rattaches peuvent inclure des charges liees aux achats, des frais de transport, des taxes a recuperer, etc.'),
(13, 'Adherents, clients usagers et comptes rattaches', '41', 'Ce compte regroupe les creances de l''entreprise sur ses adherents, clients ou usagers pour la vente de biens ou la prestation de services. Il inclut les montants dus par les clients pour des ventes a credit, des abonnements, des cotisations, des loyers, etc. Les comptes rattaches peuvent inclure des produits lies aux ventes, des interets sur creances, des remises a accorder, etc.'),
(13, 'Personnel', '42', 'Ce compte enregistre les transactions financieres liees au personnel de l''entreprise, telles que les salaires, les avantages sociaux, les charges sociales, les primes, les indemnites, les frais de formation, etc.'),
(13, 'Organismes sociaux', '43', 'Ce compte regroupe les transactions financieres liees aux organismes sociaux, tels que les cotisations sociales, les contributions aux regimes de securite sociale, les taxes sur les salaires, etc.'),
(13, 'Etat et collectivites publiques', '44', 'Ce compte enregistre les transactions financieres de l''entreprise avec l''etat et les collectivites publiques, telles que les impots sur les benefices, les taxes sur la valeur ajoutee (TVA), les taxes foncieres, les taxes locales, les redevances, etc.'),
(13, 'Fondateur, apporteurs et comptes courants', '45', 'Ce compte regroupe les transactions financieres de l''entreprise avec ses fondateurs, ses actionnaires ou ses associes, ainsi que les comptes courants detenus par ces parties prenantes. Il peut inclure les apports en capital, les avances de tresorerie, les prets consentis, les comptes de regularisation, etc.'),
(13, 'Bailleurs, Etat et autres organismes, donfs d''administration', '46', 'Ce compte enregistre les transactions financieres de l''entreprise avec les bailleurs de fonds, l''etat, les organismes publics ou prives, ainsi que les fonds d''administration. Il peut inclure les emprunts contractes, les subventions recues, les aides financieres, les dons, etc.'),
(13, 'Debiteurs et creduteurs divers', '47', 'Ce compte regroupe les autres debiteurs et creanciers de l''entreprise qui ne sont pas specifiquement classes dans d''autres categories. Il peut inclure des partenaires commerciaux, des tiers, des personnes physiques ou morales, des institutions financieres, etc.'),
(13, 'Creances et dettes dons Hors Activites Ordinaires (h.a.o)', '48', 'Ce compte enregistre les creances et les dettes de l''entreprise qui sont hors du cadre de ses activites ordinaires. Il peut inclure des operations non courantes, des transactions exceptionnelles, des engagements specifiques, des evenements extraordinaires, etc.'),
(13, 'Depreciations et provisions pour risques et charges a court terme (TIERS)', '49', 'Ce compte regroupe les depreciations et les provisions comptabilisees par l''entreprise pour couvrir les risques et les charges a court terme lies a des tiers. Il permet de constater les pertes potentielles ou les engagements financiers anticipes associes a des tiers, tels que des clients en difficulte, des litiges en cours, des garanties a honorer, etc.'),
(14, 'Titres de placement', '50', 'Ce compte enregistre les investissements de l''entreprise dans des titres financiers tels que des actions, des obligations, des fonds communs de placement, des certificats de depot, etc. Ces titres sont detenus par l''entreprise dans le but de realiser un profit a court ou a long terme.'),
(14, 'Valeurs a encaisser', '51', 'Ce compte regroupe les valeurs mobilieres et les instruments financiers facilement convertibles en especes. Il peut inclure des cheques encaissables, des effets de commerce, des billets a ordre, des traites, des lettres de change, etc.'),
(14, 'Banques', '52', 'Ce compte enregistre les fonds deposes par l''entreprise dans ses comptes bancaires. Il inclut les soldes de compte courant, les depots a terme, les comptes d''epargne, les comptes de cheques, etc.'),
(14, 'Etablissements financieres et assimiles', '53', 'Ce compte regroupe les transactions financieres de l''entreprise avec des institutions financieres autres que des banques, telles que des societes de credit, des compagnies d''assurance, des caisses de retraite, des societes de financement, etc.'),
(14, 'Instruments de monnaie electronique', '55', 'Ce compte enregistre les fonds detenus par l''entreprise sous forme de monnaie electronique ou de paiements electroniques. Il peut inclure des soldes de portefeuilles electroniques, des fonds sur des cartes prepayees, des comptes de paiement en ligne, etc.'),
(14, 'Banques, credits de tresorerie', '56', 'Ce compte regroupe les emprunts contractes par l''entreprise aupres des banques ou d''autres institutions financieres pour financer ses activites de tresorerie a court terme. Il peut inclure des credits de caisse, des facilites de decouvert, des lignes de credit renouvelables, etc.'),
(14, 'Caisse', '57', 'Ce compte enregistre les fonds liquides detenus physiquement par l''entreprise, tels que la monnaie, les pieces de monnaie et les billets de banque, conserves dans sa caisse ou son coffre fort.'),
(14, 'Virement internes', '58', 'Ce compte est utilise pour enregistrer les transferts de fonds entre differents comptes ou entites au sein de la meme entreprise. Il peut inclure des transferts entre comptes bancaires, des deplacements de fonds entre divisions ou filiales, des reallocations de ressources internes, etc.'),
(14, 'Depreciations et provisions pour risques et charges a court terme', '59', 'Ce compte regroupe les depreciations et les provisions comptabilisees par l''entreprise pour couvrir les risques et les charges a court terme. Il permet de constater les pertes potentielles ou les engagements financiers anticipes qui ne sont pas lies a des tiers.'),
(15, 'Achats et variation de stocks', '60', 'Ce compte enregistre le cout d''achat des marchandises et des matieres premieres destinees a la vente ou a la production, ainsi que les variations de stock sur une periode donnee. Il permet de suivre les couts d''approvisionnement et les fluctuations de stockage de l''entreprise.'),
(15, 'Transports', '61', 'Ce compte regroupe les depenses liees au transport des marchandises, des matieres premieres, des produits finis ou des personnes. Il inclut les frais de transport routier, ferroviaire, maritime, aerien ou par tout autre moyen de transport.'),
(15, 'Services exterieurs', '62', 'Ce compte enregistre les depenses liees aux services fournis a l''entreprise par des tiers. Il inclut les honoraires professionnels, les services de conseil, les frais de sous traitance, les services publics, etc.'),
(15, 'Autres services exterieurs', '63', 'Ce compte regroupe les depenses pour des services externes autres que ceux specifiquement mentionnes dans d''autres comptes. Il peut inclure une gamme variee de services necessaires au fonctionnement de l''entreprise mais qui ne rentrent pas dans des categories specifiques.'),
(15, 'Impots et taxes', '64', 'Ce compte enregistre les impots et taxes payes par l''entreprise au gouvernement ou aux autorites fiscales. Il inclut les impots sur le revenu, les taxes sur la valeur ajoutee (TVA), les taxes foncieres, les taxes sur les salaires, etc.'),
(15, 'Autres charges', '65', 'Ce compte regroupe les charges qui ne sont pas specifiquement liees a des categories precises de depenses. Il peut inclure divers frais generaux, charges administratives, frais d''exploitation, etc.'),
(15, 'Charges de personnel', '66', 'Ce compte enregistre les couts lies a la remuneration et aux avantages sociaux du personnel de l''entreprise. Il inclut les salaires, les traitements, les charges sociales, les cotisations de retraite, les indemnites, etc.'),
(15, 'Frais financiers et charges assimilees', '67', 'Ce compte regroupe les frais financiers engages par l''entreprise, tels que les interets sur les emprunts, les frais de gestion de la dette, les frais bancaires, les commissions financieres, etc.'),
(15, 'Dotations aux amortissements', '68', 'Ce compte enregistre les provisions comptables pour l''amortissement des immobilisations de l''entreprise. Il permet de constater la depreciation progressive de la valeur des actifs fixes sur leur duree de vie utile.'),
(15, 'Dotations aux provisions et aux depreciations', '69', 'Ce compte regroupe les provisions comptables pour faire face a des pertes potentielles ou a des risques identifies, ainsi que les depreciations des actifs de l''entreprise. Il permet de constituer des reserves pour couvrir des engagements futurs incertains.'),
(16, 'Revenus', '70', 'Ce compte enregistre les recettes et les gains realises par l''entreprise au cours de ses activites commerciales normales. Il inclut les ventes de produits, les prestations de services, les revenus locatifs, les commissions, etc.'),
(16, 'Subventions d''exploitation', '71', 'Ce compte enregistre les subventions recues par l''entreprise pour soutenir ses activites operationnelles. Il peut s''agir de subventions gouvernementales, de subventions d''organisations internationales ou d''autres formes de financement non remboursable.'),
(16, 'Production immobilisee', '72', 'Ce compte concerne la valeur des biens produits par l''entreprise pour son propre usage ou pour etre incorpores dans ses actifs immobilises. Il enregistre la valeur des biens produits et non vendus sur le marche.'),
(16, 'Variations des stocks de biens produits', '73', 'Ce compte enregistre les variations de valeur des stocks de biens produits par l''entreprise. Il inclut les augmentations ou les diminutions de valeur des stocks en cours de production, des produits finis ou des produits intermediaires.'),
(16, 'Autres produits', '75', 'Ce compte regroupe les revenus qui ne sont pas specifiquement lies a des categories particulieres de recettes. Il peut inclure des revenus exceptionnels, des gains sur des operations non courantes, des remboursements d''impots, etc.'),
(16, 'Revenus financiers et produits assimiles', '77', 'Ce compte enregistre les revenus generes par les investissements financiers de l''entreprise. Il inclut les interets percus sur les placements, les dividendes recus, les gains de change, etc.'),
(16, 'Transfert de charges', '78', 'Ce compte concerne les transferts de charges entre differentes sections ou departements de l''entreprise. Il permet de repartir les couts indirects ou communs entre les differentes entites ou activites de l''entreprise.'),
(16, 'Reprises de provosion, de depreciation et autres', '79', 'Ce compte enregistre les montants repris sur les provisions ou les depreciations precedemment constatees. Il est utilise lorsque les circonstances qui ont justifie la constitution de ces provisions ou depreciations ne sont plus presentes.'),
(17, 'Valeurs comptables des cesions d''immobilisations', '81', 'Ce compte enregistre les valeurs comptables des biens immobiliers cedes par l''entreprise. Il reflete le montant des ventes ou des cessions des actifs immobilises, deduction faite de leur amortissement ou de leur depreciation accumulee.'),
(17, 'Produits des cessions d''immobilisations', '82', 'Ce compte enregistre les produits realises par l''entreprise lors de la cession ou de la vente d''actifs immobilises. Il inclut les gains realises sur la vente de terrains, de batiments, d''equipements, etc.'),
(17, 'Charges hors activites ordinaires', '83', 'Ce compte regroupe les charges qui ne sont pas directement liees aux activites operationnelles habituelles de l''entreprise. Il peut inclure des charges exceptionnelles, des pertes sur des operations non courantes, des frais de restructuration, etc.'),
(17, 'Revenus hors activites ordinaires', '84', 'Ce compte regroupe les revenus qui ne sont pas generes par les activites commerciales principales de l''entreprise. Il peut inclure des gains exceptionnels, des revenus sur des operations non courantes, des subventions non liees a l''exploitation, etc.'),
(17, 'Dotations hors activites ordinaires', '85', 'Ce compte enregistre les dotations aux provisions ou aux depreciations qui ne sont pas directement liees aux activites ordinaires de l''entreprise. Il peut inclure des dotations pour des risques exceptionnels, des pertes sur des creances douteuses, etc.'),
(17, 'Reprises d''amortissments, provisions et depreciations (hao)', '86', 'Ce compte enregistre les montants repris sur les amortissements, les provisions ou les depreciations anterieurement constatees et qui ne sont plus necessaires. Il est utilise lorsque les circonstances qui ont justifie ces constatations ne sont plus presentes.'),
(17, 'Variations de stocks de dons en nature (hao)', '87', 'Ce compte enregistre les variations de valeur des stocks constitues de dons en nature. Il concerne les biens recus en don par l''entreprise et qui sont destines a etre utilises dans ses activites.'),
(17, 'Subventions d''equilibre', '88', 'Ce compte enregistre les subventions recues par l''entreprise pour compenser des desequilibres financiers ou pour assurer son equilibre financier. Ces subventions peuvent etre accordees par des organismes publics ou prives dans le cadre de politiques de soutien ou de redressement.'),
(18, 'Emplois des contributions volontaires en nature', '90', 'Ce compte enregistre les utilisations ou les emplois des contributions volontaires recues sous forme de biens ou de services. Il permet de suivre l''affectation des ressources fournies par des donateurs ou des partenaires sous forme non monetaire.'),
(18, 'Contributions volontaires en nature', '91', 'Ce compte enregistre les dons ou les contributions recus par l''entreprise sous forme de biens ou de services plutot que d''argent liquide. Il peut s''agir de dons de produits, de materiaux, d''equipements, de services professionnels, etc.'),
(18, 'Comptes reflechis', '92', 'Ce terme peut designer des comptes fictifs ou des comptes temporaires utilises pour des operations de calcul ou d''analyse interne. Ces comptes peuvent etre crees pour simplifier le processus de comptabilite ou pour effectuer des ajustements temporaires.'),
(18, 'Comptes de reclassement', '93', 'Les comptes de reclassifications sont utilises pour effectuer des ajustements ou des reclassements temporaires dans les etats financiers. Ils permettent de corriger des erreurs comptables ou d''isoler des operations specifiques avant leur integration definitive dans les comptes finaux.'),
(18, 'Comptes de couts', '94', 'Les comptes de couts enregistrent les depenses directes et indirectes associees a la production de biens ou de services. Ils permettent de suivre les couts lies aux differents aspects de l''activite de l''entreprise, tels que les couts de production, les couts de distribution, les couts administratifs, etc.'),
(18, 'Comptes de stocks', '95', 'Les comptes de stocks enregistrent la valeur des biens ou des produits detenus par l''entreprise en vue de la vente ou de l''utilisation ulterieure. Ils incluent les stocks de matieres premieres, de produits en cours de fabrication, de produits finis, etc.'),
(18, 'Comptes d''ecarts sur couts preetablis', '96', 'Ces comptes enregistrent les ecarts entre les couts reels et les couts preetablis ou budgetes. Ils permettent d''analyser les variations de couts et d''identifier les ecarts par rapport aux previsions ou aux normes etablies.'),
(18, 'Comptes de defferences de traitement comptable', '97', 'Ces comptes sont utilises pour enregistrer les differences resultant de variations dans les methodes comptables ou dans le traitement des operations entre les exercices comptables. Ils permettent de rendre compte des ajustements necessaires pour assurer la comparabilite des etats financiers.'),
(18, 'Comptes de resultats', '98', 'Les comptes de resultats enregistrent les produits, les charges, les produits financiers et les charges financieres de l''entreprise pour une periode donnee, generalement un exercice comptable. Ils permettent de calculer le resultat net de l''entreprise et de presenter sa performance financiere.'),
(18, 'Comptes de liaisons internes', '99', 'Ces comptes sont utilises pour enregistrer les transactions internes ou les transferts de fonds entre differentes entites ou departements au sein de la meme entreprise. Ils facilitent le suivi des flux financiers et des relations economiques internes.');