INSERT INTO sous_comptes (comptes_id, name, nbr, description)
VALUES
(1, 'Capital social', '101', 'Ce compte enregistre le montant total du capital investi par les actionnaires ou les proprietaires dans l''entreprise. Il represente la valeur nominale des actions emises par l''entreprise et constitue une partie importante de ses fonds propres.'),
(1, 'Capital par adoption', '102', 'Ce compte peut etre utilise pour enregistrer les montants investis dans l''entreprise par des partenaires ou des associes en cas de reprise ou d''adoption d''une entreprise existante. Il represente les fonds apportes par les nouveaux proprietaires lors de l''acquisition de l''entreprise.'),
(1, 'Capital personnel', '103', 'Ce compte enregistre les fonds investis dans l''entreprise par le proprietaire ou les dirigeants de l''entreprise a titre personnel. Il peut s''agir de capitaux propres apportes par les fondateurs ou les actionnaires principaux de l''entreprise.'),
(1, 'Comptes de l''exploitant', '104', 'Ce compte regroupe les transactions financieres personnelles du proprietaire ou de l''exploitant individuel de l''entreprise. Il permet de distinguer les finances de l''entreprise de celles de son proprietaire et d''assurer une separation claire des actifs et des passifs.'),
(1, 'Primes liees au capital social', '105', 'Ce compte enregistre les primes ou les montants supplementaires verses par les actionnaires lors de l''emission de nouvelles actions ou de l''augmentation du capital social de l''entreprise. Il represente les sommes payees au-dessus de la valeur nominale des actions.'),
(1, 'Ecart de reevaluation', '106', 'Ce compte est utilise pour enregistrer les ecarts resultant de la reevaluation des actifs ou des passifs de l''entreprise, generalement a la hausse. Il represente la difference entre la valeur comptable des actifs ou des passifs et leur valeur de marche actualisee.'),
(1, 'Apporteurs, Capital souscrit, non appele', '109', 'Ce compte enregistre les montants du capital social souscrit par les actionnaires ou les associes mais qui n''ont pas encore ete appeles ou verses a l''entreprise. Il represente les engagements financiers des actionnaires envers l''entreprise qui doivent encore etre honores.'),
(2, 'Reserves Legales', '111', 'Ce compte enregistre les montants reserves legalement par l''entreprise en vertu de la reglementation en vigueur. Ces reserves sont generalement constituees pour assurer la stabilite financiere de l''entreprise et garantir le respect des obligations legales.'),
(2, 'Reserves Statutaires ou contractuelles', '112', 'Ce compte regroupe les montants reserves par l''entreprise conformement a ses statuts ou a des accords contractuels specifiques. Ces reserves sont generalement constituees pour repondre a des besoins particuliers de l''entreprise ou pour anticiper des depenses futures.'),
(2, 'Reserves reglementees', '113', 'Ce compte enregistre les montants reserves par l''entreprise conformement a des dispositions legales ou reglementaires specifiques. Ces reserves peuvent etre utilisees pour des activites ou des investissements specifiques autorises par la loi.'),
(2, 'Autres reserves', '118', 'Ce compte regroupe les montants reserves par l''entreprise pour diverses raisons qui ne correspondent pas aux categories specifiques de reserves legales, statutaires ou reglementees. Il peut s''agir de reserves constituees pour des projets d''expansion, des provisions pour risques ou des besoins futurs non specifies.'),
(3, 'Report a nouveau crediteur', '121', 'Ce compte enregistre les soldes crediteurs reportes a partir de l''exercice precedent. Il represente les benefices accumules de l''entreprise qui n''ont pas ete distribues aux actionnaires ou reaffectes a d''autres comptes de reserve.'),
(3, 'Report a nouveau debiteur', '129', 'Ce compte enregistre les soldes debiteurs reportes a partir de l''exercice precedent. Il represente les pertes accumulees de l''entreprise qui n''ont pas ete compensees ou absorbees par les benefices futurs.'),
(4, 'Resultat en instance d''affectation', '130', 'Ce compte represente le resultat de l''exercice qui est en attente d''affectation, c''est-a-dire le benefice ou la perte nette qui doit etre reparti entre les actionnaires ou utilise conformement aux politiques de distribution des benefices de l''entreprise.'),
(4, 'Resultat Net : benefice', '131', 'Ce compte enregistre le benefice net realise par l''entreprise au cours de l''exercice, resultant de la difference positive entre les produits et les charges.'),
(4, 'Marge commerciale (M.C)', '132', 'La marge commerciale represente la difference entre le chiffre d''affaires et le cout d''achat des marchandises vendues. C''est un indicateur de la rentabilite des activites commerciales de l''entreprise.'),
(4, 'Valeur Ajoutee (V.A)', '133', 'La valeur ajoutee mesure la richesse creee par l''entreprise au cours de son processus de production. Elle correspond a la valeur de la production moins la consommation intermediaire.'),
(4, 'Exedent Brut d''Exploitation (E.B.E)', '134', 'L''excedent brut d''exploitation represente le resultat de l''exploitation de l''entreprise avant deduction des charges financieres et des impots. Il est calcule en ajoutant aux produits d''exploitation les subventions d''exploitation et en retranchant les charges d''exploitation.'),
(4, 'Resultat d''Exploitation (R.E.)', '135', 'Le resultat d''exploitation correspond au resultat obtenu apres deduction de toutes les charges d''exploitation (hors charges financieres et exceptionnelles) des produits d''exploitation. Il reflete la rentabilite des activites principales de l''entreprise.'),
(4, 'Resultat Financier (R.F.)', '136', 'Le resultat financier represente le resultat obtenu apres deduction des charges financieres des produits financiers. Il reflete la performance de l''entreprise dans ses activites financieres telles que les placements, les emprunts, etc.'),
(4, 'Resultat des Activites Ordinaires (R.A.O.)', '137', 'Le resultat des activites ordinaires correspond au resultat obtenu apres deduction de toutes les charges (exploitation, financieres, exceptionnelles) des produits, a l''exception des activites extraordinaires.'),
(4, 'Resultat Hors Activite Ordinaires (R.H.A.O)', '138', 'Le resultat hors activite ordinaires represente le resultat obtenu apres deduction des charges et produits exceptionnels des activites ordinaires. Il englobe les evenements et transactions qui ne sont pas lies aux activites principales de l''entreprise.'),
(4, 'Resultat Net : Perte', '139', 'Ce compte enregistre la perte nette realisee par l''entreprise au cours de l''exercice, resultant de la difference negative entre les produits et les charges.'),
(5, 'Subvention d''equipement', '141', 'Ce compte enregistre les subventions octroyees a l''entreprise pour financer l''acquisition ou la modernisation d''equipements. Ces subventions sont generalement destinees a encourager les investissements productifs et a soutenir le developpement economique.'),
(5, 'Autres subvention d''investissement', '148', 'Ce compte regroupe les subventions d''investissement autres que celles liees a l''equipement. Il peut inclure des subventions pour des projets specifiques, des subventions regionales ou sectorielles, etc. Ces subventions sont destinees a soutenir les investissements de l''entreprise dans divers domaines.'),
(6, 'Amortissments derogations', '151', 'Ce compte enregistre les amortissements pratiques de maniere derogatoire par rapport aux regles comptables habituelles. Ils peuvent etre autorises dans certaines circonstances exceptionnelles, par exemple lorsqu''une reevaluation des actifs conduit a des amortissements differents de ceux calcules selon les methodes comptables traditionnelles.'),
(6, 'Plus-Values de Cession a Reinvestir', '152', 'Ce compte concerne les benefices realises lors de la cession d''actifs immobilises, qui sont reinvestis dans l''entreprise plutot que distribues aux actionnaires. Ces plus-values sont generalement reaffectees a des investissements futurs ou a des projets de developpement.'),
(6, 'Fonds Reglementes', '153', 'Ce compte regroupe les fonds specifiquement reglementes par la legislation ou la reglementation en vigueur. Il peut s''agir de fonds destines a des fins specifiques, tels que des fonds de garantie, des fonds de reserve obligatoires, etc.'),
(6, 'Provisions speciales de Reevaluation', '154', 'Ce compte concerne les provisions constituees pour refleter les ajustements de valeurs des actifs ou des passifs de l''entreprise, realises dans le cadre d''une reevaluation speciale. Ces provisions sont generalement etablies pour compenser les fluctuations de valeur des elements du bilan.'),
(6, 'Provisions Reglementes relatives aux immobilisations', '155', 'Ce compte regroupe les provisions constituees conformement a des dispositions reglementaires specifiques, relatives aux immobilisations de l''entreprise. Ces provisions peuvent etre obligatoires ou facultatives selon la nature des actifs et les exigences legales.'),
(6, 'Provisions Reglementee Relatives aux Stocks', '156', 'Ce compte concerne les provisions constituees conformement a des dispositions reglementaires specifiques, relatives aux stocks de l''entreprise. Ces provisions sont generalement etablies pour couvrir les pertes potentielles liees a la depreciation, a l''obsolescence ou a d''autres risques associes aux stocks.'),
(6, 'Provisions pour Investissement', '157', 'Ce compte regroupe les provisions constituees en vue de financer des investissements futurs de l''entreprise. Ces provisions sont generalement etablies pour accumuler des fonds destines a des projets d''expansion, de modernisation ou de developpement.'),
(6, 'Autres provisions et fonds reglementes', '158', 'Ce compte regroupe les provisions et les fonds reglementes qui ne sont pas specifiquement categorises ailleurs. Il peut inclure diverses provisions et fonds etablis pour se conformer a des exigences legales ou reglementaires specifiques.'),
(7, 'Emprunts obligataires', '161', 'Ce compte enregistre les emprunts contractes par l''entreprise sous forme d''obligations emises sur les marches financiers. Les obligations sont des titres de creance a long terme qui garantissent generalement un paiement d''interets periodique et le remboursement du capital a l''echeance.'),
(7, 'Emprunts et dettes aupres des etablissements de credit', '162', 'Ce compte concerne les emprunts et les dettes contractes par l''entreprise aupres des etablissements de credit tels que les banques commerciales. Il peut inclure des lignes de credit, des prets a court, moyen ou long terme, des decouverts bancaires, etc.'),
(7, 'Avances recues de l''etat', '163', 'Ce compte enregistre les avances financieres accordees a l''entreprise par l''Etat ou les autorites publiques. Ces avances peuvent etre destinees a financer des projets specifiques, a soutenir l''activite economique ou a repondre a des besoins de tresorerie temporaires.'),
(7, 'Avances recues et comptes courants bloques', '164', 'Ce compte regroupe les avances financieres recues par l''entreprise qui sont bloquees ou soumises a des conditions particulieres d''utilisation. Il peut egalement inclure les comptes courants bloques aupres des associes ou des actionnaires de l''entreprise.'),
(7, 'Depots et cautionnements recus', '165', 'Ce compte concerne les depots de garantie et les cautionnements recus par l''entreprise dans le cadre de transactions commerciales ou contractuelles. Ces depots sont generalement remboursables sous certaines conditions, telles que la fin d''un contrat ou l''execution satisfaisante d''une obligation.'),
(7, 'Interets courus', '166', 'Ce compte enregistre les interets accumules mais non encore payes sur les emprunts et les dettes de l''entreprise. Les interets courus representent une charge pour l''entreprise et doivent etre comptabilises dans ses etats financiers, meme s''ils n''ont pas encore ete regles.'),
(7, 'Avances assorties de conditions particulieres', '167', 'Ce compte regroupe les avances financieres recues par l''entreprise qui sont assorties de conditions specifiques ou de restrictions quant a leur utilisation. Ces avances peuvent etre soumises a des obligations contractuelles ou reglementaires particulieres.'),
(7, 'Autres emprunts et dettes', '168', 'Ce compte regroupe les emprunts et les dettes qui ne sont pas specifiquement categorises ailleurs. Il peut inclure divers types d''engagements financiers, tels que des contrats de location-financement, des emprunts obligataires convertibles, des dettes non bancaires, etc.'),
(8, 'Dettes de location - acquisition ou credit-bail immobilier', '172', 'Ce compte concerne les dettes contractees par l''entreprise dans le cadre de contrats de location-acquisition ou de credit-bail immobilier. Ces dettes resultent de l''utilisation d''actifs immobiliers tels que des terrains, des batiments ou des installations techniques, pour lesquels l''entreprise paie des loyers avec une option d''achat a terme.'),
(8, 'Dettes de location - acquisition ou credit-bail mobilier', '173', 'Ce compte regroupe les dettes liees a des contrats de location-acquisition ou de credit-bail mobilier. Il concerne l''utilisation d''actifs mobiliers tels que des equipements, des machines, des vehicules ou du materiel informatique, pour lesquels l''entreprise paie des loyers avec une option d''achat a terme.'),
(8, 'Dettes de location- acquisition ou location vente', '174', 'Ce compte concerne les dettes contractees dans le cadre de contrats de location-vente ou de location-acquisition avec option d''achat. Il englobe les dettes resultant de la location d''actifs avec une possibilite d''acquisition a terme, que ce soit pour des biens immobiliers ou mobiliers.'),
(8, 'Interets courus', '176', 'Ce compte enregistre les interets accumules mais non encore payes sur les dettes de l''entreprise. Les interets courus representent une charge financiere pour l''entreprise et doivent etre comptabilises dans ses etats financiers, meme s''ils n''ont pas encore ete regles.'),
(8, 'Autres dettes de location - acquisition', '178', 'Ce compte regroupe les autres dettes liees a des contrats de location-acquisition ou de credit-bail, qui ne sont pas specifiquement categorisees ailleurs. Il peut inclure des dettes resultant de la location de biens d''equipement, de logiciels, de droits d''utilisation ou d''autres actifs, avec une option d''achat ou des conditions de location particulieres.'),
(9, 'Dettes liees a des participation', '181', 'Ce compte concerne les dettes contractees par l''entreprise en relation avec ses participations dans d''autres entreprises. Il peut s''agir de dettes resultant d''investissements dans des filiales, des coentreprises ou d''autres formes de participation, ou l''entreprise est tenue responsable de certaines obligations financieres.'),
(9, 'Dettes liees a des societes en participation', '182', 'Ce compte regroupe les dettes specifiquement liees aux societes en participation auxquelles l''entreprise est associee. Les dettes peuvent resulter de transactions commerciales ou d''engagements financiers contractes par ces societes, pour lesquels l''entreprise est conjointement responsable.'),
(9, 'Interets courus sur dettes liees a des participations', '183', 'Ce compte enregistre les interets accumules mais non encore payes sur les dettes contractees en relation avec les participations de l''entreprise dans d''autres societes. Les interets courus representent une charge financiere pour l''entreprise et doivent etre comptabilises dans ses etats financiers.'),
(9, 'Comptes permanents bloques des etablissements et succursales', '184', 'Ce compte concerne les comptes permanents bloques des etablissements et succursales de l''entreprise. Il s''agit de comptes bancaires ou financiers qui sont soumis a des restrictions de retrait ou de transfert imposees par l''entreprise ou les autorites reglementaires.'),
(9, 'Comptes permanents non bloques des etablissements et succursales', '185', 'Ce compte regroupe les comptes permanents non soumis a des restrictions de retrait ou de transfert des etablissements et succursales de l''entreprise. Il s''agit generalement de comptes bancaires ou financiers utilises pour les operations courantes de l''entreprise.'),
(9, 'Comptes de liaison charges', '186', 'Ce compte est utilise pour enregistrer les operations de transfert de charges entre differentes divisions ou departements de l''entreprise. Il permet de suivre les couts supportes par chaque division tout en conservant une vue d''ensemble des charges totales de l''entreprise.'),
(9, 'Comptes de liaison produits', '187', 'Ce compte est utilise pour enregistrer les operations de transfert de produits entre differentes divisions ou departements de l''entreprise. Il permet de suivre les revenus generes par chaque division tout en conservant une vue d''ensemble des produits totaux de l''entreprise.'),
(9, 'Comptes de liaison des societes en participation', '188', 'Ce compte est utilise pour enregistrer les transactions et les soldes entre l''entreprise et ses societes en participation. Il permet de consolider les operations financieres entre les entites liees et de refleter correctement la situation financiere de l''entreprise consolidee.'),
(10, 'Provisions pour litiges', '191', 'Ce compte est utilise pour enregistrer les montants mis de cote par l''entreprise pour faire face a des litiges ou des procedures judiciaires en cours ou anticipees. Les provisions pour litiges sont constituees afin de couvrir les eventuelles pertes financieres decoulant de ces litiges.'),
(10, 'Provisions pour garanties donnees aux clients', '192', 'Ce compte regroupe les montants provisionnes par l''entreprise pour couvrir les couts potentiels lies aux garanties accordees aux clients sur ses produits ou services. Les provisions sont etablies en fonction des estimations des couts futurs associes a ces garanties.'),
(10, 'Provisions pour pertes sur marches a achevement futur', '193', 'Ce compte concerne les provisions etablies par l''entreprise pour couvrir les pertes potentielles sur des contrats a achevement futur ou des projets en cours qui pourraient ne pas etre acheves selon les termes prevus. Les provisions sont basees sur des evaluations des risques associes a ces projets.'),
(10, 'Provisions pour pertes de change', '194', 'Ce compte est utilise pour enregistrer les montants provisionnes par l''entreprise pour faire face a des pertes potentielles decoulant de fluctuations defavorables des taux de change. Les provisions pour pertes de change sont constituees pour attenuer l''impact financier de ces fluctuations sur les operations de l''entreprise.'),
(10, 'Provisions pour impots', '195', 'Ce compte regroupe les montants mis de cote par l''entreprise pour couvrir les impots futurs qu''elle devra payer. Les provisions pour impots sont etablies en fonction des estimations des obligations fiscales futures de l''entreprise, afin de garantir qu''elle dispose des fonds necessaires pour les regler.'),
(10, 'Provisions pour pensions et obligations similaires', '196', 'Ce compte concerne les provisions etablies par l''entreprise pour couvrir les obligations futures liees aux regimes de retraite et autres avantages sociaux offerts a ses employes. Les provisions pour pensions sont constituees pour garantir que l''entreprise dispose des fonds necessaires pour honorer ses engagements envers ses employes.'),
(10, 'Provisions pour restructuration', '197', 'Ce compte est utilise pour enregistrer les montants mis de cote par l''entreprise pour faire face aux couts de restructuration, tels que les indemnites de depart des employes, les couts de fermeture d''installations et autres charges liees a des changements importants dans la structure ou les operations de l''entreprise.'),
(10, 'Autres provisions pour risques et charges', '198', 'Ce compte regroupe les provisions etablies par l''entreprise pour couvrir d''autres types de risques et de charges qui ne sont pas specifiquement repertories dans d''autres comptes de provisions. Il peut s''agir de provisions pour des evenements imprevus ou exceptionnels qui pourraient avoir un impact financier sur l''entreprise.'),
(11, 'Frais de developpement', '211', 'Ce compte concerne les couts engages par l''entreprise pour developper de nouveaux produits, services ou technologies. Les frais de developpement comprennent les depenses liees a la recherche, a la conception, a la mise en oeuvre et a la mise sur le marche de ces developpements.'),
(11, 'Brevets, licences, concessions, et droits similaires', '212', 'Ce compte enregistre la valeur des brevets, licences, concessions et autres droits similaires detenus par l''entreprise. Ces actifs incorporels sont generalement acquis pour leur utilisation dans les operations commerciales de l''entreprise ou pour proteger ses droits de propriete intellectuelle.'),
(11, 'Logiciels et sites internet', '213', 'Ce compte regroupe les actifs incorporels acquis par l''entreprise sous forme de brevets, licences, concessions ou autres droits similaires. Il s''agit des droits legaux exclusifs permettant a l''entreprise d''exploiter certains actifs ou technologies.'),
(11, 'Marques', '214', 'Ce compte concerne les actifs incorporels representes par les logiciels informatiques et les sites internet detenus par l''entreprise. Il inclut les couts d''acquisition ou de developpement de logiciels et de sites internet utilises dans les operations commerciales de l''entreprise.'),
(11, 'Fonds commercial', '215', 'Ce compte regroupe la valeur des actifs incorporels lies a la reputation, a la clientele, a la notoriete de la marque et a d''autres aspects immateriels de l''entreprise qui contribuent a sa rentabilite et a sa capacite a generer des revenus.'),
(11, 'Droit au bail', '216', 'Ce compte concerne les droits legaux acquis par l''entreprise pour occuper un bien immobilier en vertu d''un contrat de bail. Il represente la valeur des droits d''occupation de l''entreprise sur le bien immobilier loue.'),
(11, 'Investissements de creation', '217', 'Ce compte concerne les investissements effectues par l''entreprise pour creer de nouveaux actifs incorporels, tels que des droits de propriete intellectuelle, des logiciels personnalises ou d''autres actifs crees en interne.'),
(11, 'Autres droits et valeurs incorporels', '218', 'Ce compte regroupe les actifs incorporels qui ne sont pas specifiquement repertories dans d''autres comptes. Il peut inclure une variete de droits et de valeurs immateriels detenus par l''entreprise, tels que des droits de publication, des droits d''utilisation de brevets tiers, etc.'),
(11, 'Immobilisations incorporelles en cours', '219', 'Ce compte concerne les couts engages par l''entreprise pour developper ou acquerir des actifs incorporels qui ne sont pas encore prets a etre mis en service. Il s''agit des couts de developpement ou d''acquisition de futurs actifs incorporels de l''entreprise.'),
(12, 'Terrains agricoles et forestiers', '221', 'Ce compte concerne les terrains detenus par l''entreprise destines a des activites agricoles ou forestieres. Il englobe les terres utilisees pour la culture, l''elevage ou la sylviculture, ainsi que les forets ou les bois exploites par l''entreprise.'),
(12, 'Terrains nus', '222', 'Ce compte regroupe les terrains detenus par l''entreprise qui ne sont pas amenages ou affectes a une activite specifique. Il peut s''agir de terrains non developpes, non construits ou non utilises a des fins particulieres.'),
(12, 'Terrains batis', '223', 'Ce compte concerne les terrains detenus par l''entreprise sur lesquels des batiments, des installations ou des structures ont ete eriges. Il englobe les terrains occupes par des locaux commerciaux, des usines, des entrepots ou des infrastructures similaires.'),
(12, 'Travaux de mise en valeur des terrains', '224', 'Ce compte concerne les couts engages par l''entreprise pour preparer et amenager des terrains en vue de leur utilisation future. Il inclut les depenses liees a l''assainissement, au nivellement, a la viabilisation ou a d''autres travaux de developpement des terrains.'),
(12, 'Terrains de carrieres-trefonds', '225', 'Ce compte concerne les terrains detenus par l''entreprise et exploites pour l''extraction de materiaux tels que la pierre, le sable, le gravier, etc. Il englobe les carrieres, les gravieres ou d''autres sites d''extraction de materiaux mineraux.'),
(12, 'Terrains amenages', '226', 'Ce compte regroupe les terrains detenus par l''entreprise qui ont ete specifiquement amenages ou equipes pour une utilisation particuliere. Il peut inclure des terrains amenages pour le sport, les loisirs, les parcs industriels, etc.'),
(12, 'Terrains mis en concession', '227', 'Ce compte concerne les terrains detenus par l''entreprise qui ont ete concedes ou loues a des tiers pour une utilisation specifique. Il englobe les terrains loues pour des activites commerciales, residentielles, agricoles ou industrielles.'),
(12, 'Autres terrains', '228', 'Ce compte regroupe les terrains detenus par l''entreprise qui ne sont pas specifiquement repertories dans d''autres comptes. Il peut inclure une variete de terrains detenus a des fins diverses mais qui ne correspondent pas aux categories specifiques enumerees.'),
(12, 'Amenagements de terrains en cours', '229', 'Ce compte concerne les couts engages par l''entreprise pour amenager des terrains qui ne sont pas encore prets a etre utilises. Il inclut les depenses liees a la preparation, a la viabilisation ou a d''autres travaux de developpement en cours sur les terrains de l''entreprise.'),
(13, 'Batiments industriels, agricoles, administratifs et commerciaux sur sol propre', '231', 'Ce compte enregistre la valeur des batiments industriels, agricoles, administratifs et commerciaux possedes par l''entreprise et construits sur un terrain appartenant a l''entreprise elle-meme.'),
(13, 'Batiments industriels, agricoles, administratifs et commerciaux sur sol d''autrui', '232', 'Ce compte enregistre la valeur des batiments industriels, agricoles, administratifs et commerciaux possedes par l''entreprise mais construits sur un terrain appartenant a un tiers.'),
(13, 'Ouvrages d''infrastructures', '233', 'Ce compte concerne les ouvrages de grande envergure construits par l''entreprise pour des besoins d''infrastructures. Il englobe les constructions telles que les routes, les ponts, les barrages, les lignes electriques, etc., destinees a un usage public ou prive.'),
(13, 'Amenagements, agencements et installations techniques', '234', 'Ce compte regroupe les amenagements interieurs et les installations techniques realises par l''entreprise pour repondre a ses besoins operationnels. Il inclut les agencements de locaux, les installations electriques, les systemes de climatisation, etc.'),
(13, 'Amenagements de bureaux', '235', 'Ce compte concerne les amenagements specifiques realises par l''entreprise dans ses locaux administratifs. Il englobe les travaux d''agencement, de decoration et d''equipement des bureaux et des espaces de travail.'),
(13, 'Batiments industriels, agricoles et commerciaux mis en concession', '237', 'Ce compte concerne les batiments detenus par l''entreprise mais construits sur des terrains concedes ou loues a des tiers. Il englobe les structures industrielles, agricoles ou commerciales situees sur des terrains concedes a l''entreprise pour une periode determinee.'),
(13, 'Autres installations et agencements', '238', 'Ce compte regroupe les installations et les agencements realises par l''entreprise qui ne sont pas specifiquement repertories dans d''autres comptes. Il peut inclure une variete d''amenagements interieurs ou exterieurs repondant a des besoins particuliers de l''entreprise.'),
(13, 'Batiments amenagements, agencements et installations en cours', '239', 'Ce compte concerne les couts engages par l''entreprise pour la construction ou la renovation de batiments, d''amenagements interieurs, d''agencements ou d''installations techniques en cours de realisation. Il inclut les travaux en cours sur les infrastructures de l''entreprise.'),
(14, 'Materiel et outillage industriel et commercial', '241', 'Ce compte regroupe le materiel et l''outillage utilises par l''entreprise dans ses activites industrielles et commerciales. Il inclut les equipements, les machines, les outils et les dispositifs necessaires a la production, a la transformation ou a la vente des biens ou des services de l''entreprise.'),
(14, 'Materiel et outillage agricole', '242', 'Ce compte concerne le materiel et l''outillage specifiquement utilises dans les activites agricoles de l''entreprise. Il englobe les machines agricoles, les equipements de culture, de recolte et de traitement des cultures, ainsi que tout autre equipement necessaire a l''exploitation agricole.'),
(14, 'Materiel d''emballage recuperable et identifiable', '243', 'Ce compte concerne le materiel d''emballage utilise par l''entreprise pour conditionner ses produits et marchandises. Il inclut les emballages reutilisables et identifiables, tels que les palettes, les conteneurs et les caisses, qui peuvent etre recuperes et reutilises dans le processus logistique de l''entreprise.'),
(14, 'Materiel et mobilier', '244', 'Ce compte regroupe le materiel et le mobilier utilises par l''entreprise dans ses activites administratives, commerciales ou operationnelles. Il inclut les equipements de bureau, les meubles, les appareils electroniques, les equipements de securite, etc., necessaires au fonctionnement de l''entreprise.'),
(14, 'Materiel de transport', '245', 'Ce compte concerne les vehicules et les moyens de transport utilises par l''entreprise dans le cadre de ses activites. Il englobe les voitures, les camions, les vehicules utilitaires, les engins de manutention, ainsi que tout autre moyen de transport utilise pour le deplacement des biens ou des personnes.'),
(14, 'Actifs biologiques', '246', 'Ce compte concerne les actifs biologiques detenus par l''entreprise, tels que les animaux, les cultures permanentes, les plantations forestieres, etc. Il englobe les biens vivants utilises ou detenus par l''entreprise dans le cadre de ses activites agricoles, forestieres ou de production biologique.'),
(14, 'Agencements, amenagements du materiel et actifs biologiques', '247', 'Ce compte regroupe les couts lies a l''agencement et a l''amenagement du materiel et des actifs biologiques de l''entreprise. Il inclut les depenses engagees pour l''installation, la modification ou l''amelioration des equipements, des installations et des infrastructures associes au materiel et aux actifs biologiques.'),
(14, 'Autres materiels et mobiliers', '248', 'Ce compte concerne les autres types de materiel et de mobilier utilises par l''entreprise mais qui ne sont pas specifiquement repertories dans d''autres comptes. Il englobe les equipements et les biens mobiliers necessaires aux activites de l''entreprise et qui ne sont pas classes ailleurs dans le plan comptable.'),
(14, 'Materiels et actifs biologiques en cours', '249', 'Ce compte concerne les couts engages par l''entreprise pour l''acquisition, la production ou la transformation de materiel et d''actifs biologiques en cours de realisation. Il inclut les depenses liees a la construction, a la fabrication ou a la croissance des biens materiels ou biologiques en cours de developpement.'),
(15, 'Avances et acomptes verses sur immobilisations incorporelles', '251', 'Ce compte enregistre les paiements anticipes effectues par l''entreprise pour l''acquisition ou le developpement d''immobilisations incorporelles. Il comprend les avances financieres versees a des tiers ou a des fournisseurs en vue de l''acquisition de droits incorporels tels que des brevets, des licences logicielles, des droits d''auteur, etc.'),
(15, 'Avances et acomptes verses sur immobilisations corporelles', '252', 'Ce compte concerne les avances financieres versees par l''entreprise pour l''acquisition ou la construction d''immobilisations corporelles telles que des terrains, des batiments, des machines, des equipements, etc. Il enregistre les acomptes verses a des fournisseurs ou des entrepreneurs pour la realisation de travaux ou la livraison d''equipements.'),
(16, 'Titres de participation dans des societes sous controle exclusif', '261', 'Ce compte represente les participations detenues par l''entreprise dans des societes ou elle exerce un controle exclusif, c''est-a-dire une participation majoritaire lui permettant de diriger les activites de la societe controlee.'),
(16, 'Titres de participation dans des societes sous controle conjoint', '262', 'Ce compte concerne les participations detenues par l''entreprise dans des societes ou elle exerce un controle conjoint avec d''autres parties prenantes. Il s''agit generalement de participations minoritaires ou plusieurs actionnaires ont un pouvoir de decision partage.'),
(16, 'Titres de participation dans des societes conferant une influence notable', '263', 'Ce compte enregistre les participations detenues par l''entreprise dans des societes ou elle n''a pas de controle majoritaire mais dispose d''une influence notable sur les decisions strategiques ou operationnelles.'),
(16, 'Participations dans des organismes professionnels', '265', 'Ce compte concerne les participations detenues par l''entreprise dans des organismes ou des associations professionnelles ou elle est membre.'),
(16, 'Parts dans des groupements d''interet economique (G.I.E)', '266', 'Ce compte represente les parts detenues par l''entreprise dans des groupements d''interet economique, qui sont des structures permettant a plusieurs entites de collaborer dans le cadre de projets communs tout en conservant leur independance juridique.'),
(16, 'Autres titres de participation', '268', 'Ce compte regroupe d''autres types de titres de participation qui ne sont pas specifiquement couverts par les comptes precedents. Il peut inclure des participations dans des fonds d''investissement, des entreprises non cotees en bourse, etc.'),
(17, 'Prets et creances', '271', 'Ce compte represente les sommes pretees par l''entreprise a des tiers et les creances resultant de ventes de biens ou de prestations de services realisees a credit. Il inclut egalement les avances accordees et d''autres formes de creances detenues par l''entreprise.'),
(17, 'Prets au personnel', '272', 'Ce compte enregistre les montants pretes par l''entreprise a son personnel, tels que les avances sur salaire ou les prets consentis aux employes dans le cadre de politiques de prets internes.'),
(17, 'Creances sur l''etat', '273', 'Ce compte concerne les creances detenues par l''entreprise sur l''Etat, telles que les remboursements d''impots, les subventions ou les paiements dus par l''administration publique.'),
(17, 'Titres immobilises', '274', 'Ce compte regroupe les titres financiers detenus par l''entreprise a long terme, c''est-a-dire ceux qui ne sont pas destines a etre rapidement vendus ou negocies sur un marche actif. Il peut s''agir d''actions ou d''obligations d''autres entreprises detenues a des fins d''investissement.'),
(17, 'Depots et cautionnements verses', '275', 'Ce compte represente les depots de garantie et les cautionnements verses par l''entreprise a des tiers, tels que les depots de garantie verses a des fournisseurs ou les cautionnements exiges dans le cadre de contrats ou de transactions.'),
(17, 'Interets courus', '276', 'Ce compte enregistre les interets qui ont ete accumules mais qui n''ont pas encore ete payes ou recus a la date de cloture de l''exercice financier.'),
(17, 'Creances rattachees a des participations et avances a des (G.I.E.)', '277', 'Ce compte concerne les creances detenues par l''entreprise sur des participations dans des societes ou des avances accordees a des groupements d''interet economique (G.I.E.) dont elle est membre.'),
(17, 'Immobilisations financieres diverses', '278', 'Ce compte regroupe d''autres types d''immobilisations financieres qui ne sont pas specifiquement couverts par les comptes precedents. Il peut inclure des investissements dans des fonds d''investissement, des participations dans des startups, etc.'),
(18, 'Amortissements des immobilisations incorporelles', '281', 'Ce compte enregistre les amortissements constates sur les immobilisations incorporelles de l''entreprise, telles que les logiciels, les brevets, les marques, etc. Les amortissements representent la repartition du cout de ces actifs sur leur duree d''utilisation prevue.'),
(18, 'Amortissements des terrains', '282', 'Ce compte concerne les amortissements constates sur les terrains detenus par l''entreprise. Il est rare que les terrains fassent l''objet d''amortissements car ils sont consideres comme des actifs non depreciables. Cependant, dans certains cas exceptionnels ou leur valeur diminue, des amortissements peuvent etre enregistres.'),
(18, 'Amortissements des batiments, installations techniques et agencements', '283', 'Ce compte regroupe les amortissements constates sur les batiments, les installations techniques et les agencements detenus par l''entreprise. Les amortissements sont comptabilises pour refleter la depreciation de la valeur de ces actifs au fil du temps, due a leur usure, a l''obsolescence ou a d''autres facteurs.'),
(18, 'Amortissements du materiel', '284', 'Ce compte concerne les amortissements constates sur le materiel et l''outillage industriel et commercial de l''entreprise. Il inclut les equipements, les machines, les outils et autres actifs similaires. Les amortissements sont enregistres pour refleter la perte de valeur de ces actifs au fur et a mesure de leur utilisation.'),
(19, 'Depreciations des immobilisations incorporelles', '291', 'Ce compte enregistre les depreciations constatees sur les immobilisations incorporelles de l''entreprise, telles que les logiciels, les brevets, les marques, etc. Les depreciations sont enregistrees lorsque la valeur recouvrable d''un actif est inferieure a sa valeur comptable nette.'),
(19, 'Depreciations des terrains', '292', 'Ce compte concerne les depreciations constatees sur les terrains detenus par l''entreprise. Les depreciations peuvent etre enregistrees lorsque la valeur des terrains diminue de facon durable et significative, par exemple en raison de changements dans l''environnement economique ou des conditions du marche.'),
(19, 'Depreciations des batiments, installations techniques et agencements', '293', 'Ce compte regroupe les depreciations constatees sur les batiments, les installations techniques et les agencements detenus par l''entreprise. Les depreciations sont enregistrees pour refleter la perte de valeur de ces actifs, qui peut resulter de l''usure, de l''obsolescence ou d''autres facteurs.'),
(19, 'Depreciations de materiel, du mobilier et de l''inactif biologique', '294', 'Ce compte concerne les depreciations constatees sur le materiel, le mobilier et les actifs biologiques de l''entreprise. Les depreciations sont enregistrees pour refleter la perte de valeur de ces actifs, qui peut resulter de l''usure, de l''obsolescence ou d''autres facteurs.'),
(19, 'Depreciations des avances et acomptes verses sur immobilisations', '295', 'Ce compte enregistre les depreciations constatees sur les avances et acomptes verses sur des immobilisations, telles que des avances sur des biens en construction ou en developpement. Les depreciations sont comptabilisees lorsque la valeur recouvrable des immobilisations concernees est inferieure a leur cout d''acquisition ou a leur valeur comptable nette.'),
(19, 'Depreciations des titres de participation', '296', 'Ce compte concerne les depreciations constatees sur les titres de participation detenus par l''entreprise dans d''autres societes. Les depreciations sont enregistrees lorsque la valeur de ces titres diminue de facon durable et significative, par exemple en raison de performances financieres mediocres ou de difficultes economiques rencontrees par les societes emettrices.'),
(19, 'Depreciations des autres immobilisations financieres', '297', 'Ce compte regroupe les depreciations constatees sur d''autres types d''immobilisations financieres detenues par l''entreprise, telles que des prets, des creances a long terme, des placements a long terme, etc. Les depreciations sont enregistrees lorsque la valeur recouvrable de ces actifs est inferieure a leur valeur comptable nette.'),
(20, 'Marchandises A', '311', 'Ce compte represente la valeur des marchandises de type A detenues par l''entreprise dans le cadre de ses activites ordinaires. Les marchandises de type A sont generalement celles qui sont destinees a etre revendues dans le cours normal des operations commerciales de l''entreprise. Elles peuvent inclure des produits finis, des produits en cours de production ou des matieres premieres necessaires a la fabrication de produits finis.'),
(20, 'Marchandises B', '312', 'Ce compte represente la valeur des marchandises de type B detenues par l''entreprise dans le cadre de ses activites ordinaires. Les marchandises de type B sont generalement celles qui sont destinees a etre revendues dans le cours normal des operations commerciales de l''entreprise, mais qui peuvent presenter des caracteristiques differentes ou etre destinees a un marche specifique par rapport aux marchandises de type A.'),
(20, 'Actifs biologiques', '313', 'Ce compte concerne la valeur des actifs biologiques detenus par l''entreprise, tels que des animaux vivants ou des cultures, qui sont utilises dans ses activites operationnelles. Les actifs biologiques peuvent etre evalues a leur cout d''acquisition ou a leur valeur venale, selon la methode comptable choisie par l''entreprise.'),
(20, 'Marchandises hors activites ordinaires (H.A.O)', '318', 'Ce compte represente la valeur des marchandises detenues par l''entreprise qui ne sont pas directement liees a ses activites ordinaires. Il peut s''agir de marchandises destinees a des projets specifiques, a des promotions ponctuelles ou a d''autres utilisations qui ne relevent pas du fonctionnement habituel de l''entreprise. Les marchandises enregistrees dans ce compte peuvent etre evaluees separement des autres stocks pour permettre une meilleure analyse des activites hors activites ordinaires.'),
(21, 'Matieres A', '321', 'Ce compte represente les matieres premieres de type A utilisees dans le processus de production.Il inclut les matieres premieres de qualite specifique necessaires a la fabrication des produits.'),
(21, 'Matieres B', '322', 'Ce compte englobe les matieres premieres de type B requises dans la chaine de production.Il comprend les matieres premieres secondaires ou alternatives utilisees dans la fabrication des biens.'),
(21, 'Fournitures (A, B)', '323', 'Ce compte regroupe les fournitures necessaires a la production, incluant a la fois les fournitures de type A et de type B.'),
(22, 'Matieres consommables', '331', 'Ce compte represente les matieres premieres qui sont utilisees dans le processus de production et qui sont consommees au cours de ce processus.'),
(22, 'Fournitures d''atelier et d''usine', '332', 'Il englobe les fournitures utilisees dans les ateliers et les usines pour entretenir les equipements, les outils et les machines.'),
(22, 'Fournitures de magasin', '333', ' Ces fournitures sont utilisees dans les magasins pour l''entreposage des produits et pour assurer le bon fonctionnement des operations de stockage.'),
(22, 'Fournitures de bureau', '334', 'Ce compte regroupe les fournitures necessaires pour les taches administratives, telles que le papier, les stylos, les cartouches d''encre, etc.'),
(22, 'Emballages', '335', 'Il inclut les materiaux d''emballage utilises pour proteger les produits pendant le transport, le stockage et la vente.'),
(22, 'Autres matieres', '338', 'Ce compte est utilise pour enregistrer toutes les autres matieres qui ne sont pas specifiquement couvertes par les autres comptes.'),
(23, 'Produits en cours', '341', 'Ce compte enregistre la valeur des produits qui sont en cours de fabrication mais qui ne sont pas encore acheves a la fin de la periode comptable.'),
(23, 'Travaux en cours', '342', 'l represente la valeur des travaux qui sont en cours d''execution mais qui ne sont pas encore termines a la fin de la periode comptable.'),
(23, 'Produits intermediaires en cours', '343', 'Ce compte concerne les produits qui sont partiellement transformes ou assembles mais qui ne sont pas encore acheves.'),
(23, 'Produits residuels en cours', '344', 'Il englobe les produits qui resultent du processus de production mais qui ne sont pas conformes aux specifications finales et qui necessitent des ajustements ou des transformations supplementaires.'),
(23, 'Actifs biologiques en cours', '345', 'Ce compte est utilise pour enregistrer la valeur des actifs biologiques, tels que les cultures agricoles ou les animaux d''elevage, qui sont en cours de croissance ou de developpement.'),
(24, 'Etudes en cours', '351', 'Ce compte comptabilise les couts lies aux etudes et aux recherches en cours qui n''ont pas encore abouti a un produit ou un service finalise a la fin de la periode comptable.'),
(24, 'Prestations de services en cours', '352', 'Il enregistre la valeur des services qui sont en cours d''execution mais qui n''ont pas encore ete entierement fournis a la fin de la periode comptable.'),
(25, 'Produits finis A', '361', 'Ce compte represente la valeur des produits finis de categorie A qui sont prets a etre vendus a la fin de la periode comptable.'),
(25, 'Produits finis B', '362', 'Il enregistre la valeur des produits finis de categorie B qui sont prets a etre vendus a la fin de la periode comptable.'),
(25, 'Actifs biologiques', '363', 'Ce compte concerne les actifs biologiques, tels que les animaux ou les cultures, qui sont prets a etre recoltes ou exploites a la fin de la periode comptable.'),
(26, 'Produits intermediaires', '371', 'Ce compte represente la valeur des produits en cours de production qui sont en cours de transformation pour devenir des produits finis.'),
(26, 'Produits residuels', '372', 'Il enregistre la valeur des produits restants ou des sous-produits qui resultent du processus de production principale et qui ne sont pas destines a etre vendus independamment.'),
(26, 'Actifs biologiques', '373', 'Ce compte concerne les actifs biologiques, tels que les animaux ou les cultures, qui sont prets a etre recoltes ou exploites a la fin de la periode comptable.'),
(27, 'Marchandises en cours de route', '381', 'Ce compte enregistre la valeur des marchandises qui sont en transit vers leur destination finale, mais qui n''ont pas encore ete receptionnees.'),
(27, 'Matieres premieres et fournitures liees en cours de route', '382', 'Il represente la valeur des matieres premieres et des fournitures qui ont ete achetees mais qui n''ont pas encore ete livrees ou receptionnees.'),
(27, 'Autres approvisionnements en cours de route', '383', 'Ce compte concerne les autres types d''approvisionnements en transit vers leur destination finale.'),
(27, 'Produits finis en cours de route', '386', ' Il enregistre la valeur des produits finis qui ont ete fabriques mais qui n''ont pas encore ete livres a leurs clients.'),
(27, 'Stock en consignation ou en depot', '387', 'Ce compte represente les biens detenus par l''entreprise pour le compte d''un tiers, qui n''appartiennent pas directement a l''entreprise mais qui sont sous sa garde ou sa responsabilite.'),
(27, 'Stock provenant d''immobilisations mises hors service ou au rebut', '388', 'Il concerne les biens ou les stocks qui proviennent d''immobilisations qui ont ete mises hors service ou au rebut et qui sont toujours en possession de l''entreprise.'),
(28, 'Depreciations des stocks de marchandises', '391', 'Ce compte enregistre la valeur des depreciations appliquees aux stocks de marchandises detenus par l''entreprise, refletant la perte de valeur de ces marchandises.'),
(28, 'Depreciations des stocks de matieres premieres et fournitures liees', '392', 'Il represente les depreciations appliquees aux stocks de matieres premieres et de fournitures liees detenus par l''entreprise, refletant la perte de valeur de ces elements.'),
(28, 'Depreciations des stocks d''autres appovisionnements', '393', 'Ce compte concerne les depreciations appliquees aux stocks d''autres types d''approvisionnements detenus par l''entreprise, refletant la perte de valeur de ces approvisionnements.'),
(28, 'Depreciations des productions en cours', '394', 'Il enregistre les depreciations appliquees aux productions en cours de fabrication ou de transformation, refletant la perte de valeur de ces productions.'),
(28, 'Depreciations des services en cours', '395', 'Ce compte concerne les depreciations appliquees aux services en cours de realisation ou de fourniture, refletant la perte de valeur de ces services.'),
(28, 'Depreciations des stocks de produits finis', '396', ' Il represente les depreciations appliquees aux stocks de produits finis detenus par l''entreprise, refletant la perte de valeur de ces produits.'),
(28, 'Depreciations des stocks de produits intermediaires et residuels', '397', 'Ce compte enregistre les depreciations appliquees aux stocks de produits intermediaires et residuels detenus par l''entreprise, refletant la perte de valeur de ces produits.'),
(28, 'Depreciations des stocks en cours de route, en consignation ou en depot', '398', 'Il concerne les depreciations appliquees aux stocks en transit, en consignation ou en depot detenus par l''entreprise, refletant la perte de valeur de ces stocks.'),
(29, 'Fournisseurs, dettes en compte', '401', 'Ce compte enregistre les dettes dues aux fournisseurs pour des biens ou des services recus par l''entreprise, mais pour lesquels aucune facture n''a encore ete emise.'),
(29, 'Fournisseurs, effets a payer', '402', 'Il represente les dettes dues aux fournisseurs pour des biens ou des services recus par l''entreprise et pour lesquels des effets de commerce ont ete emis, mais qui n''ont pas encore ete payes.'),
(29, 'Fournisseurs, acquisitions courantes d''immobilisations', '404', 'Ce compte concerne les dettes dues aux fournisseurs pour l''acquisition courante d''immobilisations corporelles ou incorporelles par l''entreprise.'),
(29, 'Fournisseurs, acquisitions courantes d''immobilisations', '408', 'Il enregistre egalement les dettes dues aux fournisseurs pour l''acquisition courante d''immobilisations corporelles ou incorporelles par l''entreprise.'),
(29, 'Fournisseurs debiteurs', '409', 'Ce compte concerne les montants dus par les fournisseurs a l''entreprise en raison de retours de marchandises ou de credits accordes par les fournisseurs.'),
(30, 'Clients', '411', 'Ce compte represente les montants dus a l''entreprise par ses clients pour des biens ou des services fournis, mais qui n''ont pas encore ete payes.'),
(30, 'Clients, effets a recevoir en portefeuille', '412', 'Il concerne les montants dus a l''entreprise par ses clients, pour lesquels des effets de commerce ont ete emis et qui sont en attente de paiement a l''echeance.'),
(30, 'Clients, cheques, effets et autres valeurs impayes', '413', 'Ce compte enregistre les montants dus a l''entreprise par ses clients sous forme de cheques, d''effets de commerce ou d''autres valeurs monetaires qui sont encore impayes.'),
(30, 'Creances sur cessions courantes d''immobilisations', '414', 'Il represente les montants dus a l''entreprise par ses clients pour des cessions courantes d''immobilisations, telles que des ventes d''actifs.'),
(30, 'Clients, effets escomptes non echus', '415', 'Ce compte concerne les montants dus a l''entreprise par ses clients pour lesquels des effets de commerce ont ete escomptes mais dont l''echeance n''est pas encore arrivee.'),
(30, 'Creances clients litigieuses ou douteuses', '416', 'Il enregistre les montants dus a l''entreprise par des clients pour lesquels il existe un litige ou une incertitude quant a leur recouvrement.'),
(30, 'Clients, produits a recevoir', '418', 'Ce compte represente les montants dus a l''entreprise par ses clients pour des biens ou des services qui ont ete factures mais qui n''ont pas encore ete livres ou fournis.'),
(30, 'Clients crediteurs', '419', 'Il concerne les montants dus par les clients a l''entreprise en raison de retours de marchandises ou de credits accordes par l''entreprise.'),
(31, 'Personnel, avances et acomptes', '421', 'Ce compte enregistre les avances et les acomptes verses au personnel pour des depenses futures ou des avances sur salaire.'),
(31, 'Personnel, remunerations dues', '422', 'Il concerne les montants dus par l''entreprise a son personnel pour les salaires, les traitements, les primes et autres remunerations.'),
(31, 'Personnel, oppositions, saisies-arrets', '423', 'Ce compte concerne les montants saisis ou retenus sur les remunerations du personnel en raison d''oppositions ou de saisies-arrets emises par des tiers.'),
(31, 'Personnel, oeuvres sociales internes', '424', 'Il represente les depenses engagees par l''entreprise pour les activites sociales et culturelles destinees a son personnel, telles que les activites sportives, les voyages, etc.'),
(31, 'Representants du personnel', '425', 'Ce compte enregistre les montants dus aux representants du personnel pour leur participation aux reunions et aux activites liees a la representation des salaries.'),
(31, 'Personnel, participation aux benefices et au capital', '426', 'Il concerne les montants dus au personnel au titre de sa participation aux benefices de l''entreprise ou a son capital, selon les dispositifs de participation mis en place.'),
(31, 'Personnel depots', '427', 'Ce compte enregistre les depots effectues par le personnel aupres de l''entreprise, tels que les cautionnements ou les depots de garantie.'),
(31, 'Personnel, charges a payer et produits a recevoir', '428', 'Il concerne les charges a payer et les produits a recevoir lies au personnel, tels que les conges payes a provisionner ou les cotisations sociales a regulariser.'),
(32, 'Securite sociale', '431', 'Ce compte concerne les montants dus a la securite sociale pour le paiement des cotisations sociales obligatoires liees a la protection sociale des salaries.'),
(32, 'Caisses de retraite complementaire', '432', 'Il represente les montants dus aux caisses de retraite complementaire pour le financement des regimes de retraite supplementaires des salaries.'),
(32, 'Autres organismes sociaux', '433', 'Ce compte enregistre les montants dus a d''autres organismes sociaux que ceux lies a la securite sociale ou aux retraites complementaires, tels que les organismes de mutuelle ou d''assurance complementaire.'),
(32, 'Organismes sociaux, charges a payer et produits a recevoir', '438', 'Il concerne les charges a payer et les produits a recevoir lies aux organismes sociaux, tels que les regularisations de cotisations ou les remboursements de prestations.'),
(33, 'Etat, impot sur les benefices', '441', 'Ce compte represente les montants dus a l''Etat au titre de l''impot sur les benefices realises par l''entreprise.'),
(33, 'Etat, autres impots et taxes', '442', 'Il concerne les montants dus a l''Etat pour d''autres types d''impots et taxes, tels que la taxe fonciere, la taxe professionnelle, ou d''autres taxes locales.'),
(33, 'Etat, T.V.A. facturee', '443', 'Ce compte enregistre le montant de la taxe sur la valeur ajoutee facturee aux clients sur les ventes de biens ou de services.'),
(33, 'Etat, T.V.A. due ou credit de T.V.A.', '444', ' concerne la TVA due a l''Etat sur les operations realisees par l''entreprise, ainsi que les credits de TVA recuperables.'),
(33, 'Etat, T.V.A. recuperable', '445', 'Ce compte enregistre les montants de TVA supportes par l''entreprise sur ses achats de biens et services et pouvant etre recuperes aupres de l''Etat.'),
(33, 'Etat, autres taxes sur le chiffre daffaires', '446', 'Il concerne les autres taxes liees au chiffre d''affaires de l''entreprise, en dehors de la TVA, telles que la contribution sociale de solidarite des societes (C3S).'),
(33, 'Etat, impots retenus a la source', '447', 'Ce compte concerne les montants d''impots retenus a la source sur les revenus verses par l''entreprise a ses salaries ou a des tiers.'),
(33, 'Etat, charges a payer et produits a recevoir', '448', 'Il enregistre les charges fiscales a payer a l''Etat et les produits fiscaux a recevoir de l''Etat, autres que ceux lies specifiquement aux impots sur les benefices et a la TVA.'),
(33, 'Etat, creances et dettes diverses', '449', 'Ce compte regroupe les creances et les dettes de l''entreprise envers l''Etat qui ne sont pas specifiquement liees a des impots ou a la TVA.'),
(34, 'Operations avec les organismes africains', '451', 'Ce compte enregistre les transactions financieres et commerciales effectuees par l''entreprise avec des organismes bases en Afrique, tels que des partenaires commerciaux, des institutions financieres ou des organisations non gouvernementales operant sur le continent africain.'),
(34, 'Operations avec les autres organismes internationaux', '452', 'Il concerne les operations financieres et commerciales de l''entreprise impliquant des organismes internationaux autres que ceux specifiquement africains, comme des institutions internationales, des organisations intergouvernementales ou des organisations non gouvernementales operant en dehors du continent africain.'),
(34, 'Organismes internationaux, fonds de dotation et subventions a recevoir', '458', 'Ce compte enregistre les fonds de dotation et les subventions que l''entreprise attend de recevoir des organismes internationaux, tels que des agences de developpement, des fondations philanthropiques ou des programmes d''aide internationale.'),
(35, 'Apporteurs, operations sur le capital', '461', 'Ce compte enregistre les operations financieres liees aux apports des actionnaires ou des associes a la societe, telles que les augmentations de capital, les reductions de capital ou d''autres transactions affectant la structure du capital social de l''entreprise.'),
(35, 'Associes (2), comptes courants', '462', 'Il enregistre les transactions financieres effectuees entre les associes et la societe via des comptes courants, notamment les avances de fonds, les prets consentis ou les remboursements effectues dans le cadre des relations financieres entre les associes et l''entreprise.'),
(35, 'Associes (2), operations faites en commun et GIE', '463', 'Ce compte concerne les operations financieres realisees en commun entre les associes ou dans le cadre d''un groupement d''interet economique (GIE), telles que les investissements communs, les activites commerciales conjointes ou les projets collaboratifs.'),
(35, 'Associes (1), dividendes a payer', '465', 'Il enregistre les montants dus aux associes au titre des dividendes a payer, c''est-a-dire les benefices distribuables de l''entreprise qui doivent etre verses aux actionnaires ou aux associes.'),
(35, 'Groupe, comptes courants', '466', 'Ce compte comptabilise les transactions financieres effectuees entre les differentes entites appartenant au meme groupe, telles que les prets, les avances de fonds ou d''autres transactions internes entre les filiales et la societe mere.'),
(35, 'Apporteurs, restant du sur capital appele', '467', 'Il enregistre les montants restant dus par les actionnaires ou les associes sur le capital appele, c''est-a-dire la partie du capital social pour laquelle ils se sont engages a contribuer mais qui n''a pas encore ete entierement versee.'),
(36, 'Debiteurs et crediteurs divers', '471', 'Ce compte enregistre les creances et les dettes qui ne relevent pas des autres categories specifiques de comptes, telles que les creances et les dettes resultant de transactions diverses avec des tiers qui ne sont pas classees ailleurs dans le plan comptable.'),
(36, 'Creances et dettes sur titres de placement', '472', 'Il concerne les creances et les dettes resultant de transactions liees a des titres de placement, tels que les actions, les obligations ou d''autres instruments financiers negociables.'),
(36, 'Intermediaires - operations faites pour comptes de tiers', '473', 'Ce compte est utilise pour enregistrer les operations realisees par l''entreprise en tant qu''intermediaire pour le compte de tiers, telles que les transactions de courtage, les operations de commission ou les mandats executes pour le compte de clients.'),
(36, 'Comptes de repartition periodique des charges et des produits', '474', ' Il est utilise pour repartir les charges et les produits sur plusieurs periodes comptables lorsque les montants ne peuvent pas etre affectes directement a une seule periode, par exemple, les charges ou les produits constates d''avance qui doivent etre repartis sur plusieurs exercices.'),
(36, 'Compte transitoire, ajustement special lie a la revision du syscohada', '475', 'Ce compte est utilise pour enregistrer les ajustements speciaux lies a la revision du systeme comptable OHADA (Organisation pour l''Harmonisation en Afrique du Droit des Affaires), notamment pour les entreprises soumises a ce regime comptable.'),
(36, 'Charges constatees d''avance', '476', ' Il enregistre les charges supportees par l''entreprise mais qui n''ont pas encore ete consommees ou utilisees, et qui doivent donc etre reportees sur les exercices futurs en tant que depenses a constater ulterieurement.'),
(36, 'Produits constates d''avance', '477', 'Ce compte comptabilise les produits ou les revenus percus par l''entreprise mais qui ne sont pas encore gagnes ou realises, et qui doivent donc etre reportes sur les exercices futurs en tant que produits a constater ulterieurement.'),
(36, 'Ecarts de conversion actif', '478', 'Il enregistre les ecarts de conversion lies a la conversion des elements d''actif libelles dans une devise etrangere en devise locale, selon les principes de comptabilite internationale.'),
(36, 'Ecarts de conversion passif', '479', 'Ce compte concerne les ecarts de conversion resultant de la conversion des elements de passif libelles dans une devise etrangere en devise locale, conformement aux normes comptables internationales.'),
(37, 'Fournisseurs d''investissements', '481', 'Ce compte reflete les dettes a court ou a long terme envers les fournisseurs pour les investissements effectues. Il est souvent utilise dans les entreprises qui acquierent des actifs importants et qui ont besoin de payer les fournisseurs sur une periode prolongee, souvent en plusieurs versements echelonnes dans le temps.'),
(37, 'Fournisseurs d''investissements, effets a payer', '482', 'Ce compte enregistre les dettes envers les fournisseurs liees aux investissements de l''entreprise, notamment les achats d''actifs immobilises ou d''autres elements necessaires a son fonctionnement futur, qui sont payables par effet de commerce.'),
(37, 'Autres dettes hors activites ordinaires (H.A.O.)', '484', 'Il concerne les dettes qui ne sont pas directement liees aux activites ordinaires de l''entreprise, telles que les passifs resultant d''evenements exceptionnels ou extraordinaires qui ne se produisent pas regulierement dans le cadre de son exploitation habituelle.'),
(37, 'Creances sur cessions d''immobilisations', '485', 'Ce compte enregistre les creances de l''entreprise decoulant de la vente ou de la cession d''immobilisations corporelles ou incorporelles a des tiers, qui sont generalement payables par des versements echelonnes sur une periode de temps determinee.'),
(37, 'Autres creances hors activites ordinaires (H.A.O.)', '488', 'Il s''agit des creances qui ne sont pas directement liees aux activites operationnelles courantes de l''entreprise, telles que les creances resultant d''evenements exceptionnels ou extraordinaires qui ne se produisent pas regulierement dans le cadre de son exploitation habituelle.'),
(38, 'Depreciations des comptes fournisseurs', '490', 'Ce compte enregistre les depreciations constatees sur les comptes fournisseurs, qui representent une estimation des pertes potentielles resultant de l''insolvabilite ou du defaut de paiement des fournisseurs.'),
(38, 'Depreciations des comptes clients', '491', 'Il concerne les depreciations constatees sur les comptes clients, representant une estimation des pertes potentielles resultant de l''insolvabilite ou du defaut de paiement des clients.'),
(38, 'Depreciations des comptes personnel', '492', 'Ce compte enregistre les depreciations constatees sur les comptes lies au personnel, comme les avances sur salaire ou les prets accordes aux employes, en raison de la possibilite de non-remboursement.'),
(38, 'Depreciations des comptes organismes sociaux', '493', 'Il concerne les depreciations constatees sur les comptes lies aux organismes sociaux, tels que les cotisations de securite sociale ou les contributions aux regimes de retraite complementaire, en raison de risques potentiels de non-paiement.'),
(38, 'Depreciations des comptes etat et collectivites publiques', '494', 'Ce compte enregistre les depreciations constatees sur les comptes lies a l''Etat et aux collectivites publiques, comme les impots ou les taxes a payer, en raison de risques potentiels de non-paiement.'),
(38, 'Depreciations des comptes organismes internationaux', '495', 'Il concerne les depreciations constatees sur les comptes lies aux organismes internationaux, comme les contributions aux organisations internationales, en raison de risques potentiels de non-paiement.'),
(38, 'Depreciations des comptes apporteurs associes et groupe', '496', 'Ce compte enregistre les depreciations constatees sur les comptes lies aux apporteurs, associes et au groupe, representant une estimation des pertes potentielles decoulant de leur insolvabilite ou de leur incapacite a honorer leurs obligations.'),
(38, 'Depreciations des comptes debiteurs divers', '497', 'Il concerne les depreciations constatees sur les comptes debiteurs autres que les clients, fournisseurs ou organismes sociaux, en raison de risques potentiels de non-paiement.'),
(38, 'Depreciations des comptes de creances h.a.o.', '498', 'Ce compte enregistre les depreciations constatees sur les comptes de creances hors activites ordinaires, representant une estimation des pertes potentielles resultant de l''insolvabilite ou du defaut de paiement dans le cadre d''evenements exceptionnels ou extraordinaires.'),
(38, 'Provisions pour risques a court terme', '499', 'Il s''agit de provisions constituees pour couvrir les risques identifies a court terme, tels que les litiges en cours, les garanties a accorder aux clients ou les pertes de change prevues, afin de garantir une bonne gestion financiere et de preserver la stabilite de l''entreprise.'),
(39, 'Titres du tresor et bons de caisse a court terme', '501', 'Ce compte enregistre les titres du tresor et bons de caisse a court terme detenus par l''entreprise, qui representent des investissements financiers a court terme avec une echeance de remboursement generalement inferieure a un an.'),
(39, 'Actions', '502', 'Il concerne les actions detenues par l''entreprise en tant qu''investissement financier, representant une part de propriete dans une societe, avec des dividendes potentiels et une valeur fluctuante sur le marche.'),
(39, 'Obligations', '503', 'Ce compte enregistre les obligations detenues par l''entreprise en tant qu''investissement financier, representant des titres de creance emis par des societes ou des entites gouvernementales, avec un paiement d''interets regulier et un remboursement du capital a l''echeance.'),
(39, 'Bons de souscription', '504', 'Il concerne les bons de souscription detenus par l''entreprise, representant des instruments financiers donnant le droit a leur detenteur d''acquerir des actions supplementaires a un prix fixe pendant une periode determinee.'),
(39, 'Titres negociables hors region', '505', 'Ce compte enregistre les titres negociables hors region detenus par l''entreprise, representant des investissements financiers dans des titres negociables emis en dehors de la region geographique de l''entreprise.'),
(39, 'Interets courus', '506', 'Il concerne les interets courus sur les titres de placement detenus par l''entreprise, representant les interets accumules mais non encore payes a la date de cloture des comptes.'),
(39, 'Autres titres de placement et creances assimilees', '508', 'Ce compte enregistre d''autres titres de placement et creances assimilees detenus par l''entreprise, qui ne sont pas classes dans les categories precedentes et qui representent des investissements financiers diversifies.'),
(40, 'Effets a encaisser', '511', 'Ce compte enregistre les effets a encaisser detenus par l''entreprise, representant des instruments financiers sous forme de traites ou de billets a ordre qui doivent encore etre encaisses.'),
(40, 'Effets a l''encaissement', '512', 'Il concerne les effets a l''encaissement detenus par l''entreprise, representant des instruments financiers sous forme de traites ou de billets a ordre qui ont ete remis a la banque pour encaissement.'),
(40, 'Cheques a encaisser', '513', 'Ce compte enregistre les cheques a encaisser detenus par l''entreprise, representant des moyens de paiement emis par des tiers qui doivent encore etre deposes sur le compte bancaire de l''entreprise.'),
(40, 'Cheques a l''encaissement', '514', 'Il concerne les cheques a l''encaissement detenus par l''entreprise, representant des moyens de paiement emis par des tiers et deja deposes sur le compte bancaire de l''entreprise en attente d''encaissement.'),
(40, 'Cartes de credit a encaisser', '515', 'Ce compte enregistre les cartes de credit a encaisser detenues par l''entreprise, representant des paiements effectues par les clients a l''aide de cartes de credit qui doivent encore etre traites et encaisses.'),
(40, 'Autres valeurs a l''encaissement', '518', 'Il concerne d''autres valeurs a l''encaissement detenues par l''entreprise, telles que des mandats, des virements ou d''autres formes de paiement qui doivent encore etre encaisses.'),
(41, 'Banques locales', '521', 'Ce compte enregistre les soldes detenus par l''entreprise dans des banques locales, representant les fonds disponibles aupres des institutions bancaires situees dans la region ou le pays ou l''entreprise opere.'),
(41, 'Banques autres etats region', '522', 'Il concerne les soldes detenus par l''entreprise dans des banques situees dans d''autres Etats de la meme region geographique, representant les fonds disponibles dans des juridictions voisines ou similaires.'),
(41, 'Banques autres etats zone monetaire', '523', 'Ce compte enregistre les soldes detenus par l''entreprise dans des banques situees dans d''autres Etats de la meme zone monetaire, representant les fonds disponibles dans des pays partageant la meme monnaie ou soumis a des accords monetaires communs.'),
(41, 'Banques hors zone monetaire', '524', 'Il concerne les soldes detenus par l''entreprise dans des banques situees en dehors de la zone monetaire de reference, representant les fonds disponibles dans des pays utilisant une monnaie differente ou n''ayant pas de lien monetaire particulier avec le pays d''origine de l''entreprise.'),
(41, 'Banques depot a terme', '525', 'Ce compte enregistre les depots a terme effectues par l''entreprise aupres des banques, representant les fonds places pour une periode determinee a un taux d''interet fixe convenu avec la banque.'),
(41, 'Banques, interets courus', '526', 'Il concerne les interets courus sur les soldes detenus par l''entreprise dans les banques, representant les interets accumules mais non encore payes par les banques sur les soldes des comptes bancaires.'),
(42, 'Cheques postaux', '531', 'Ce compte concerne les fonds detenus par l''entreprise aupres des services postaux, representant les fonds disponibles aupres des operateurs postaux pour les services bancaires.'),
(42, 'Tresor', '532', 'Il concerne les fonds detenus par l''entreprise aupres du Tresor public ou d''une institution similaire, representant les fonds disponibles aupres de l''autorite fiscale ou financiere centrale du pays.'),
(42, 'Societes de gestion et d''intermediation (S.G.I.)', '533', 'Ce compte enregistre les fonds detenus par l''entreprise aupres des societes de gestion et d''intermediation financieres, representant les fonds places ou geres par des societes specialisees dans la gestion d''actifs financiers.'),
(42, 'Etablissements financiers, interets courus', '536', 'Il concerne les interets courus sur les fonds detenus par l''entreprise aupres des etablissements financiers, representant les interets accumules mais non encore payes par ces etablissements sur les soldes des comptes.'),
(42, 'Autres organismes financiers', '538', 'Ce compte enregistre les fonds detenus par l''entreprise aupres d''autres organismes financiers non specifiquement repertories, representant les fonds disponibles aupres d''institutions financieres autres que les banques, le Tresor ou les societes de gestion.'),
(43, 'Options de taux d''interet', '541', 'Ce compte concerne les options financieres liees aux taux d''interet, representant les contrats financiers donnant le droit, mais non l''obligation, d''acheter ou de vendre un actif sous-jacent a un prix fixe a l''avance a une date ulterieure, base sur le taux d''interet.'),
(43, 'Options de taux de change', '542', 'Il concerne les options financieres liees aux taux de change, representant les contrats financiers donnant le droit, mais non l''obligation, d''acheter ou de vendre une devise a un prix fixe a l''avance a une date ulterieure, base sur le taux de change.'),
(43, 'Options de taux boursiers', '543', 'Ce compte enregistre les options financieres liees aux taux boursiers, representant les contrats financiers donnant le droit, mais non l''obligation, d''acheter ou de vendre un titre financier a un prix fixe a l''avance a une date ulterieure, base sur le taux boursier.'),
(43, 'Instruments de marches a terme', '544', 'Il concerne les instruments financiers des marches a terme, representant les contrats standardises d''achat ou de vente d''un actif sous-jacent a un prix fixe a l''avance pour une livraison future a une date determinee.'),
(43, 'Avoirs d''or et autres metaux precieux', '545', 'Ce compte enregistre les avoirs d''or et d''autres metaux precieux detenus par l''entreprise, representant les investissements ou les reserves de metaux precieux detenus pour des raisons de diversification ou de protection contre l''inflation et l''instabilite des devises.'),
(44, 'Monnaie electronique-carte carburant', '551', 'Ce compte enregistre les transactions liees a l''utilisation de la monnaie electronique via des cartes carburant, permettant le paiement electronique des achats de carburant pour les vehicules de l''entreprise.'),
(44, 'Monnaie electronique -telephone portable', '552', 'Il concerne les transactions effectuees a l''aide de la monnaie electronique via des telephones portables, permettant le paiement electronique des services et des biens a l''aide du telephone mobile.'),
(44, 'Monnaie electronique -carte peage', '553', 'Ce compte enregistre les operations liees a l''utilisation de la monnaie electronique via des cartes peage, permettant le paiement electronique des frais de peage sur les autoroutes et les routes a peage.'),
(44, 'Porte-monnaie electronique', '554', 'Ce compte concerne les operations effectuees a l''aide du porte-monnaie electronique, permettant le stockage et l''utilisation de fonds electroniques pour effectuer des transactions electroniques, des paiements et des transferts d''argent.'),
(44, 'Autres instruments monnaies electroniques', '558', 'Il s''agit d''autres instruments de monnaie electronique utilises pour effectuer des paiements electroniques, des transferts de fonds ou des transactions financieres en ligne, en dehors des categories specifiques mentionnees.'),
(45, 'Credits de tresorerie', '561', 'Ce compte enregistre les montants des credits accordes par les institutions financieres pour repondre aux besoins de tresorerie de l''entreprise, leur permettant de financer les operations courantes et de faire face aux fluctuations temporaires de liquidites.'),
(45, 'Escompte de credits de campagne', '564', 'Il concerne les escomptes accordes sur les credits specifiquement utilises pour financer des campagnes saisonnieres ou des activites saisonnieres de l''entreprise, permettant de reduire les couts d''emprunt pendant ces periodes.'),
(45, 'Escompte de credits ordinaires', '565', 'Ce compte enregistre les escomptes consentis sur les credits bancaires reguliers utilises par l''entreprise pour financer ses operations quotidiennes et ses investissements courants, contribuant ainsi a reduire les charges financieres.'),
(45, 'Banques, credits de tresorerie, interets courus', '566', 'Il s''agit des interets courus sur les credits de tresorerie accordes par les banques, enregistres ici pour refleter les charges d''interets qui doivent encore etre payees sur ces emprunts, mais qui n''ont pas encore ete reglees.'),
(46, 'Caisse siege social', '571', 'Ce compte represente les fonds en especes disponibles au siege social de l''entreprise, utilises pour effectuer des paiements immediats ou des transactions en especes.'),
(46, 'Caisse succursale A', '572', 'Il s''agit des fonds en especes disponibles dans la succursale A de l''entreprise, utilises pour effectuer des paiements sur place ou des transactions en especes specifiques a cette succursale.'),
(46, 'Caisse succursale B', '573', 'Ce compte represente les fonds en especes disponibles dans la succursale B de l''entreprise, utilises pour effectuer des paiements immediats ou des transactions en especes propres a cette succursale.'),
(47, 'Regies d''avance', '581', 'Ce compte enregistre les avances accordees au personnel ou a des tiers pour des depenses futures, telles que les frais de deplacement ou les achats de fournitures. Il permet de suivre et de controler l''utilisation des fonds avances.'),
(47, 'Accreditifs', '582', 'Il s''agit des autorisations accordees par l''entreprise a ses clients pour effectuer des achats a credit. Ce compte enregistre les montants accordes et les paiements recus au titre de ces credits.'),
(47, 'Virements de fonds', '585', 'Ce compte enregistre les transferts de fonds effectues entre differents comptes bancaires ou entre differentes entites de l''entreprise. Il permet de suivre les mouvements de tresorerie internes.'),
(47, 'Autres virements internes', '588', 'Ce compte enregistre les autres types de virements internes qui ne sont pas specifiquement lies aux operations bancaires. Il peut inclure des transferts entre comptes internes a l''entreprise ou des mouvements de fonds entre differentes entites.'),
(48, 'Depreciations des titres de placement', '590', 'Ce compte enregistre les depreciations constatees sur la valeur des titres de placement detenus par l''entreprise. Ces depreciations sont comptabilisees pour refleter une perte de valeur probable des investissements.'),
(48, 'Depreciations des titres et valeurs a encaisser', '591', 'Ce compte enregistre les depreciations constatees sur les titres et valeurs a encaisser detenus par l''entreprise. Il reflete une estimation de la perte de valeur de ces actifs.'),
(48, 'Depreciations des comptes banques', '592', 'Ce compte enregistre les depreciations constatees sur les comptes bancaires detenus par l''entreprise. Ces depreciations sont comptabilisees pour prendre en compte les pertes probables liees a ces comptes.'),
(48, 'Depreciations des comptes etablissements financiers et assimiles', '593', 'Ce compte enregistre les depreciations constatees sur les comptes d''etablissements financiers et assimiles detenus par l''entreprise. Il reflete une estimation des pertes probables associees a ces comptes.'),
(48, 'Depreciations des comptes d''instruments de tresorerie', '594', 'Ce compte enregistre les depreciations constatees sur les instruments de tresorerie detenus par l''entreprise, tels que les cheques et les effets a encaisser. Il permet de prendre en compte les pertes probables liees a ces instruments.'),
(48, 'Provisions pour risque a court terme a caractere financier', '599', 'Ce compte enregistre les provisions constituees pour couvrir les risques financiers identifies a court terme, tels que les pertes sur creances douteuses ou les fluctuations de valeur des actifs financiers. Ces provisions sont comptabilisees pour garantir une bonne gestion financiere et preserver la stabilite de l''entreprise.'),
(49, 'Achats de marchandises', '601', 'Ce compte enregistre le montant total des achats de marchandises effectues par l''entreprise sur la periode. Il inclut le cout d''acquisition des biens destines a etre revendus sans transformation.'),
(49, 'Achats de matieres premieres et fournitures liees', '602', 'Ce compte enregistre le montant total des achats de matieres premieres et de fournitures directement liees a la production de biens ou a la prestation de services par l''entreprise. Il inclut le cout d''acquisition des matieres premieres utilisees dans le processus de fabrication.'),
(49, 'Variations des stocks de biens achetes', '603', 'Ce compte enregistre les variations de valeur des stocks de biens achetes par l''entreprise au cours de la periode comptable. Il inclut les ajustements de valeur des stocks de marchandises, de matieres premieres et d''autres biens achetes.'),
(49, 'Achats stockes de matieres et fournitures consommables', '604', 'Ce compte enregistre le montant total des achats de matieres et fournitures consommables qui sont stockes en vue d''une utilisation future dans le processus de production ou dans les operations de l''entreprise. Il inclut le cout d''acquisition des biens destines a etre consommes sur plusieurs periodes.'),
(49, 'Autres achats', '605', 'Ce compte enregistre le montant total des autres achats effectues par l''entreprise au cours de la periode comptable, qui ne sont pas specifiquement lies aux achats de marchandises ou de matieres premieres. Il peut inclure des achats de services, des frais generaux ou d''autres types d''achats.'),
(49, 'Achats d''emballages', '608', 'Ce compte enregistre le montant total des achats d''emballages effectues par l''entreprise au cours de la periode. Il inclut le cout d''acquisition des emballages utilises pour conditionner les produits vendus par l''entreprise.'),
(50, 'Transports sur ventes', '612', 'Ce compte enregistre les frais de transport associes aux ventes de biens ou de services effectuees par l''entreprise au cours de la periode comptable. Il inclut les couts de livraison des produits vendus aux clients.'),
(50, 'Transports pour le compte de tiers', '613', 'Ce compte enregistre les frais de transport engages par l''entreprise pour le compte de tiers au cours de la periode comptable. Il inclut les couts de livraison des biens ou services commandes par des tiers.'),
(50, 'Transports du personnel', '614', 'Ce compte enregistre les frais de transport lies au deplacement du personnel de l''entreprise dans le cadre de ses activites professionnelles. Il inclut les couts de deplacement des employes pour se rendre sur leur lieu de travail ou pour effectuer des missions professionnelles.'),
(50, 'Transports de plis', '616', 'Ce compte enregistre les frais de transport associes a l''envoi de plis ou de documents par l''entreprise au cours de la periode comptable. Il inclut les couts d''affranchissement et de livraison de courrier.'),
(50, 'Autres frais de transport', '618', 'Ce compte enregistre les autres frais de transport engages par l''entreprise au cours de la periode comptable et qui ne sont pas specifiquement lies aux ventes, aux deplacements du personnel ou a l''envoi de plis. Il peut inclure divers frais de transport, tels que les frais de deplacement pour des reunions professionnelles ou les frais de transport de materiel.'),
(51, 'Sous-traitance generale', '621', 'Ce compte enregistre les depenses liees a la sous-traitance generale des activites de l''entreprise. Il inclut les couts des prestations de services realisees par des entreprises exterieures pour le compte de l''entreprise, telles que la sous-traitance de production, de maintenance ou de services administratifs.'),
(51, 'Locations, charges locatives', '622', 'Ce compte enregistre les depenses liees a la location de biens immobiliers ou mobiliers utilises par l''entreprise dans le cadre de son activite. Il inclut les loyers payes pour les locaux commerciaux, les bureaux, les equipements ou les vehicules loues.'),
(51, 'Redevances de location acquisition', '623', 'Ce compte enregistre les redevances versees pour la location-acquisition de biens immobiliers ou mobiliers utilises par l''entreprise. Il inclut les frais lies a la location avec option d''achat de certains actifs, tels que des equipements ou des vehicules.'),
(51, 'Entretien, reparations, remise en etat et maintenance', '624', 'Ce compte enregistre les depenses liees a l''entretien, aux reparations, a la remise en etat et a la maintenance des biens de l''entreprise. Il inclut les couts des travaux de maintenance preventive et corrective realises sur les equipements, les machines, les vehicules ou les installations.'),
(51, 'Primes d''assurance', '625', 'Ce compte enregistre les primes d''assurance payees par l''entreprise pour couvrir les risques lies a son activite. Il inclut les cotisations d''assurance pour les assurances responsabilite civile, les assurances dommages aux biens, les assurances vehicules, etc.'),
(51, 'Etudes, recherches et documentations', '626', 'Ce compte enregistre les depenses liees aux etudes, aux recherches et a la documentation realisees par l''entreprise. Il inclut les couts des etudes de marche, des etudes de faisabilite, des recherches et developpements, ainsi que les depenses liees a l''acquisition de documentation technique ou professionnelle.'),
(51, 'Publicite, publications, relations publiques', '627', 'Ce compte enregistre les depenses liees a la publicite, aux publications et aux relations publiques de l''entreprise. Il inclut les couts des campagnes publicitaires, des annonces dans les medias, des publications promotionnelles, ainsi que les frais lies a l''organisation d''evenements promotionnels ou institutionnels.'),
(51, 'Frais de telecommunications', '628', 'Ce compte enregistre les depenses liees aux frais de telecommunications de l''entreprise. Il inclut les couts des abonnements telephoniques, des services internet, des services de messagerie, ainsi que les frais de communication mobiles ou de teleconference.'),
(52, 'Frais bancaires', '631', 'Ce compte enregistre les frais factures par les institutions financieres pour les services bancaires rendus a l''entreprise. Il inclut les frais de tenue de compte, les commissions sur les operations bancaires, les frais de virement, ainsi que les frais lies a l''utilisation de services bancaires specifiques.'),
(52, 'Remunerations d''intermediaires et de conseils', '632', 'Ce compte enregistre les frais de remuneration verses a des intermediaires ou des conseils pour des services rendus a l''entreprise. Il inclut les honoraires des consultants, des avocats, des experts-comptables, des courtiers, ainsi que les commissions versees aux agents commerciaux ou aux apporteurs d''affaires.'),
(52, 'Frais de formation du personnel', '633', 'Ce compte enregistre les depenses liees a la formation du personnel de l''entreprise. Il inclut les couts des formations internes ou externes, des seminaires, des ateliers de travail, ainsi que les frais de documentation pedagogique et de materiel pedagogique.'),
(52, 'Redevances pour brevets, licences, logiciels, concessions et droits et valeurs similaires', '634', 'Ce compte enregistre les redevances versees pour l''utilisation de brevets, de licences, de logiciels, de concessions et de droits et valeurs similaires. Il inclut les paiements effectues pour l''utilisation de droits de propriete intellectuelle, de marques deposees, de licences d''exploitation, ainsi que les redevances pour l''acces a des bases de donnees ou a des contenus proteges.'),
(52, 'Cotisations', '635', 'Ce compte enregistre les cotisations versees par l''entreprise a des organismes professionnels ou a des associations sectorielles. Il inclut les cotisations patronales ou salariales versees aux caisses de retraite complementaire, aux organismes de prevoyance, aux mutuelles de sante, ainsi que les cotisations aux chambres de commerce, aux organisations patronales ou aux syndicats professionnels.'),
(52, 'Remunerations de personnel exterieur a l''entite', '637', 'Ce compte enregistre les remunerations versees a du personnel exterieur a l''entite pour des services rendus a l''entreprise. Il inclut les salaires, les honoraires, les commissions et les primes verses a des travailleurs temporaires, a des interimaires, a des consultants independants ou a des prestataires de services exterieurs.'),
(52, 'Autres charges externes', '638', 'Ce compte enregistre les autres charges externes supportees par l''entreprise et qui ne sont pas classees dans les categories precedentes. Il inclut les depenses diverses telles que les frais de deplacement, les frais de representation, les frais postaux et de courrier, les frais de demenagement, ainsi que les autres depenses professionnelles externes.'),
(53, 'Impots et taxes directs', '641', 'Ce compte enregistre les impots et taxes preleves directement sur le benefice de l''entreprise ou sur les revenus des actionnaires. Il inclut l''impot sur les benefices, l''impot sur les societes, l''impot sur le revenu des personnes physiques, ainsi que les taxes locales et regionales sur les benefices ou sur le capital.'),
(53, 'Impots et taxes indirects', '645', 'Ce compte enregistre les impots et taxes preleves indirectement sur les activites economiques de l''entreprise ou sur la consommation de biens et services. Il inclut la taxe sur la valeur ajoutee (TVA), la taxe sur les produits petroliers (TIPP), la taxe sur les services (TSA), ainsi que les autres taxes indirectes.'),
(53, 'Droits d''enregistrement', '646', 'Ce compte enregistre les droits d''enregistrement payes lors de l''acquisition ou de la cession de biens immobiliers ou mobiliers, ainsi que lors de la conclusion de contrats ou d''actes juridiques. Il inclut les droits de mutation, les droits de timbre, les droits de greffe, ainsi que les droits d''enregistrement sur les titres.'),
(53, 'Penalites, amendes fiscales', '647', 'Ce compte enregistre les penalites et amendes fiscales infligees a l''entreprise en cas de non-respect des obligations fiscales ou en cas de manquement aux regles fiscales en vigueur. Il inclut les penalites de retard, les amendes pour defaut de declaration, ainsi que les sanctions pour fraude fiscale ou pour dissimulation de revenus.'),
(53, 'Autres impots et taxes', '648', 'Ce compte enregistre les autres impots et taxes qui ne sont pas classes dans les categories precedentes. Il inclut les contributions sociales, les taxes foncieres, les droits de douane, ainsi que les autres prelevements obligatoires.'),
(54, 'Pertes sur creances clients et autres debiteurs', '651', 'Ce compte enregistre les pertes occasionnees par l''impossibilite de recouvrer les creances sur les clients et les autres debiteurs. Il inclut les pertes sur les creances irrecouvrables, les creances en litige, ainsi que les debiteurs en situation de faillite ou de redressement judiciaire.'),
(54, 'Quote-part de resultat sur operations faites en commun', '652', 'Ce compte enregistre la part de resultat provenant des operations realisees en commun avec d''autres entites ou partenaires commerciaux. Il inclut les benefices ou les pertes resultant des partenariats, des coentreprises ou des joint-ventures.'),
(54, 'Valeurs comptables des cessions courantes d''immobilisations', '654', 'Ce compte enregistre les valeurs comptables des immobilisations cedees dans le cadre d''operations courantes de l''entreprise. Il inclut les valeurs nettes comptables des biens cedes, apres deduction des amortissements et des depreciations eventuelles.'),
(54, 'Pertes de change sur creances et dettes commerciales', '656', 'Ce compte enregistre les pertes financieres resultant de la variation des taux de change sur les creances et les dettes commerciales libellees dans une devise etrangere. Il inclut les pertes de change sur les creances clients a l''exportation, ainsi que sur les dettes fournisseurs en devises etrangeres.'),
(54, 'Penalites et amendes penales', '657', 'Ce compte enregistre les penalites et amendes infligees a l''entreprise en raison de violations des lois penales ou des reglementations en vigueur. Il inclut les amendes pour non-respect des normes de securite, pour infraction environnementale, ainsi que pour toute autre infraction passible de sanctions penales.'),
(54, 'Charges diverses', '658', 'Ce compte enregistre les charges qui ne peuvent pas etre classees dans les categories precedentes en raison de leur nature diverse. Il inclut les charges exceptionnelles, les charges non recurrentes, ainsi que les autres charges qui ne sont pas specifiquement rattachees a une activite ou a un secteur particulier.'),
(54, 'Charges pour depreciations et provisions pour risques a court terme d''exploitation', '659', 'Ce compte enregistre les charges liees aux depreciations d''actifs et aux provisions constituees pour faire face aux risques a court terme d''exploitation. Il inclut les charges pour depreciations d''immobilisations, les provisions pour risques et charges, ainsi que les pertes anticipees sur des engagements ou des litiges.'),
(55, 'Remunerations directes versees au personnel national', '661', 'Ce compte enregistre les remunerations directes versees au personnel national de l''entreprise. Il inclut les salaires, les traitements, les primes, les gratifications et toutes autres remunerations versees aux employes de nationalite locale pour leur travail au sein de l''entreprise.'),
(55, 'Remunerations directes versees au personnel non national', '662', 'Ce compte enregistre les remunerations directes versees au personnel non national de l''entreprise. Il inclut les salaires, les traitements, les primes, les gratifications et toutes autres remunerations versees aux employes etrangers pour leur travail au sein de l''entreprise.'),
(55, 'Indemnites forfaitaires versees au personnel', '663', 'Ce compte enregistre les indemnites forfaitaires versees au personnel de l''entreprise. Il inclut les allocations forfaitaires versees en compensation de frais de deplacement, de repas, de logement ou autres depenses engagees par les employes dans le cadre de leurs fonctions.'),
(55, 'Charges sociales', '664', 'Ce compte enregistre les charges sociales supportees par l''entreprise pour ses employes. Il inclut les cotisations patronales aux regimes de securite sociale, aux regimes de retraite complementaire, ainsi que les contributions aux mutuelles et aux fonds de prevoyance.'),
(55, 'Remunerations et charges sociales de l''exploitant individuel', '666', 'Ce compte enregistre les remunerations et les charges sociales liees a l''exploitant individuel ou au chef d''entreprise qui exerce en son nom propre. Il inclut les remunerations versees a l''exploitant, ainsi que les cotisations sociales obligatoires qu''il doit acquitter.'),
(55, 'Remuneration transferee de personnel exterieur', '667', 'Ce compte enregistre les remunerations transferees de personnel exterieur a l''entreprise. Il inclut les frais de personnel externalise ou les honoraires verses a des consultants, des prestataires de services ou des travailleurs independants pour des missions realisees au benefice de l''entreprise.'),
(55, 'Autres charges sociales', '668', 'Ce compte enregistre les autres charges sociales qui ne sont pas specifiquement rattachees aux remunerations directes ou aux cotisations patronales. Il inclut les charges sociales exceptionnelles, les contributions aux fonds de solidarite, ainsi que toute autre charge sociale non classee ailleurs.'),
(56, 'Interets des emprunts', '671', 'Ce compte enregistre les interets supportes par l''entreprise sur ses emprunts. Il inclut les interets verses sur les emprunts bancaires, les obligations, les dettes obligataires et toute autre forme de financement externe sous forme d''interets.'),
(56, 'Interets dans loyers de location acquisition', '672', 'Ce compte enregistre les interets incorpores dans les loyers de location-acquisition. Il concerne les interets inclus dans les paiements de loyers pour des biens loues avec option d''achat.'),
(56, 'Escomptes accordes', '673', 'Ce compte enregistre les escomptes accordes par l''entreprise a ses clients ou partenaires commerciaux. Il concerne les reductions de prix octroyees en echange du reglement anticipe d''une creance ou d''une transaction commerciale.'),
(56, 'Autres interets', '674', 'Ce compte enregistre les autres interets non specifiquement classes dans les comptes precedents. Il inclut les interets sur les prets accordes par l''entreprise a des tiers, ainsi que les interets percus sur des depots et autres placements.'),
(56, 'Escomptes des effets de commerce', '675', 'Ce compte enregistre les escomptes accordes sur les effets de commerce. Il concerne les reductions consenties pour le reglement anticipe de traites, billets a ordre ou autres effets de commerce.'),
(56, 'Pertes de change financieres', '676', 'Ce compte enregistre les pertes de change subies par l''entreprise sur ses transactions financieres. Il concerne les pertes resultant de la fluctuation des taux de change entre la monnaie nationale et les devises etrangeres.'),
(56, 'Pertes sur titres de placement', '677', 'Ce compte enregistre les pertes subies par l''entreprise sur la vente ou la cession de titres de placement. Il concerne les pertes enregistrees lorsque la valeur de marche des titres est inferieure a leur valeur comptable.'),
(56, 'Pertes et charges sur risques financiers', '678', 'Ce compte enregistre les pertes et charges liees aux risques financiers encourus par l''entreprise. Il inclut les provisions pour pertes sur creances douteuses, les pertes sur investissements financiers et autres risques financiers identifies.'),
(56, 'Charges pour depreciations et provisions pour risques a court terme financieres', '679', 'Ce compte enregistre les charges pour depreciations et provisions constituees par l''entreprise pour couvrir les risques financiers a court terme. Il inclut les provisions pour risques de change, pour pertes sur creances clients et autres provisions financieres.'),
(57, 'Dotations aux amortissements d''exploitation', '681', 'Ce compte enregistre les dotations aux amortissements relatives aux immobilisations corporelles et incorporelles affectees a l''activite operationnelle de l''entreprise. Ces amortissements sont calcules pour refleter la depreciation progressive des actifs utilises dans le cadre de l''exploitation et sont generalement repartis sur leur duree d''utilite economique.'),
(58, 'Dotations aux provisions et aux depreciations d''exploitation', '691', 'Ce compte enregistre les dotations aux provisions et aux depreciations liees aux risques et charges d''exploitation de l''entreprise. Il comprend notamment les provisions pour litiges, les provisions pour creances douteuses, ainsi que les depreciations des stocks et des creances clients.'),
(58, 'Dotations aux provisions et aux depreciations financieres', '697', 'Ce compte enregistre les dotations aux provisions et aux depreciations destinees a couvrir les risques et pertes financieres de l''entreprise. Il comprend notamment les provisions pour depreciation des titres de placement, les provisions pour pertes de change, ainsi que les provisions pour risques a court terme a caractere financier.'),
(59, 'Ventes de marchandises', '701', 'Ce compte enregistre le montant des ventes de marchandises effectuees par l''entreprise au cours de la periode comptable.'),
(59, 'Ventes de produits finis', '702', 'Ce compte enregistre le montant des ventes de produits finis realisees par l''entreprise au cours de la periode comptable.'),
(59, 'Ventes de produits intermediaires', '703', 'Ce compte enregistre le montant des ventes de produits intermediaires realisees par l''entreprise au cours de la periode comptable.'),
(59, 'Ventes de produits residuels', '704', 'Ce compte enregistre le montant des ventes de produits residuels realisees par l''entreprise au cours de la periode comptable.'),
(59, 'Travaux factures', '705', 'Ce compte enregistre le montant des travaux factures par l''entreprise au cours de la periode comptable.'),
(59, 'Services vendus', '706', 'Ce compte enregistre le montant des services vendus par l''entreprise au cours de la periode comptable.'),
(59, 'Produits accessoires', '707', 'Ce compte enregistre le montant des produits accessoires vendus par l''entreprise au cours de la periode comptable.'),
(60, 'Sur produits a l''exportation', '711', 'Ce compte enregistre les sommes percues sur les produits exportes par l''entreprise.'),
(60, 'Sur produits a l''importation', '712', 'Ce compte enregistre les sommes percues sur les produits importes par l''entreprise.'),
(60, 'Sur produits de perequation', '713', 'Ce compte enregistre les sommes percues sur les produits de perequation par l''entreprise.'),
(60, 'Indemnites et subventions d''exploitation (entite agricole)', '714', 'Ce compte enregistre les indemnites et subventions d''exploitation percues par l''entite agricole.'),
(60, 'Autres subventions d''exploitation', '718', 'Ce compte enregistre les autres subventions d''exploitation percues par l''entreprise.'),
(61, 'Immobilisations incorporelles', '721', 'Ce compte enregistre la valeur des actifs immateriels detenus par l''entreprise, tels que les brevets, les licences et les logiciels.'),
(61, 'Immobilisations corporelles', '722', 'Ce compte enregistre la valeur des biens physiques detenus par l''entreprise, tels que les batiments, les machines et le materiel.'),
(61, 'Production autoconsommee', '724', 'Ce compte enregistre la valeur des biens produits par l''entreprise et utilises pour ses propres besoins.'),
(61, 'Immobilisations financieres', '726', 'Ce compte enregistre la valeur des investissements financiers a long terme de l''entreprise, tels que les participations dans d''autres entreprises.'),
(62, 'Variations de stocks des produits en cours', '734', 'Ce compte enregistre les variations de valeur des produits en cours de production.'),
(62, 'Variations des en-cours de services', '735', 'Ce compte enregistre les variations de valeur des services en cours de realisation.'),
(62, 'Variations des stocks de produits finis', '736', 'Ce compte enregistre les variations de valeur des produits finis stockes par l''entreprise.'),
(62, 'Variations des stocks de produits intermediaires et residuels', '737', 'Ce compte enregistre les variations de valeur des produits intermediaires et residuels stockes par l''entreprise.'),
(63, 'Profits sur creances clients et autres debiteurs', '751', 'Ce compte enregistre les profits realises suite a la recuperation de creances clients ou d''autres debiteurs.'),
(63, 'Quote-part de resultat sur operations faites en commun', '752', 'Ce compte enregistre la part de resultat qui revient a l''entreprise dans les operations faites en commun avec d''autres entites.'),
(63, 'Produits des cessions courantes d''immobilisations', '754', 'Ce compte enregistre les produits generes par la vente reguliere d''immobilisations de l''entreprise.'),
(63, 'Gains de change sur creances et dettes commerciales', '756', 'Ce compte enregistre les gains realises sur les operations de change impliquant des creances et des dettes commerciales.'),
(63, 'Produits divers', '758', 'Ce compte enregistre les produits divers qui ne sont pas classes ailleurs.'),
(63, 'Reprises de charges pour depreciations et provisions pour risques a court terme d''exploitation', '759', 'Ce compte enregistre les reprises de charges liees aux depreciations et provisions pour risques a court terme d''exploitation.'),
(64, 'Interets de prets et creances diverses', '771', 'Ce compte enregistre les interets percus sur les prets et les creances diverses detenus par l''entreprise.'),
(64, 'Revenus de participations et autres titres immobilises', '772', 'Ce compte enregistre les revenus generes par les participations de l''entreprise dans d''autres entites, ainsi que les revenus provenant d''autres titres immobilises.'),
(64, 'Escomptes obtenus', '773', 'Ce compte enregistre les escomptes obtenus par l''entreprise, generalement lorsqu''elle paye ses fournisseurs plus rapidement que prevu.'),
(64, 'Revenus de placement', '774', 'Ce compte enregistre les revenus percus par l''entreprise a partir de ses placements financiers.'),
(64, 'Interets dans loyers de location acquisition', '775', 'Ce compte enregistre les interets percus sur les loyers provenant de contrats de location acquisition.'),
(64, 'Gains de change financiers', '776', 'Ce compte enregistre les gains realises sur les operations de change impliquant des actifs financiers.'),
(64, 'Gains sur cessions de titres de placement', '777', 'Ce compte enregistre les gains realises par l''entreprise lors de la vente de titres de placement.'),
(64, 'Gains sur risques financiers', '778', 'Ce compte enregistre les gains realises par l''entreprise sur les operations liees aux risques financiers.'),
(64, 'Reprises de charges pour depreciations et provisions a court terme financieres', '779', 'Ce compte enregistre les reprises de charges liees aux depreciations et provisions a court terme financieres.'),
(65, 'Transferts de charges d''exploitation', '781', 'Ce compte enregistre les transferts de charges entre differentes activites d''exploitation de l''entreprise.'),
(65, 'Transferts de charges financieres', '787', 'Ce compte enregistre les transferts de charges entre differentes activites financieres de l''entreprise.'),
(66, 'Reprises de provisions et depreciations d''exploitation', '791', 'Ce compte enregistre les reprises de provisions et de depreciations liees aux activites d''exploitation de l''entreprise.'),
(66, 'Reprises de provisions et depreciations financieres', '797', 'Ce compte enregistre les reprises de provisions et de depreciations liees aux activites financieres de l''entreprise.'),
(66, 'Reprises d''amortissements', '798', 'Ce compte enregistre les reprises d''amortissements effectuees sur les immobilisations de l''entreprise.'),
(66, 'Reprises de subventions d''investissement', '799', 'Ce compte enregistre les reprises de subventions d''investissement, qui peuvent etre constatees lorsque les conditions attachees a ces subventions sont remplies ou lorsque les biens subventionnes sont mis hors service.'),
(67, 'Immobilisations incorporelles', '811', 'Ce compte enregistre la valeur des actifs non physiques de l''entreprise, tels que les brevets, les licences, les marques et les logiciels.'),
(67, 'Immobilisations corporelles', '812', 'Ce compte enregistre la valeur des actifs physiques de l''entreprise, tels que les terrains, les batiments, les machines et les equipements.'),
(67, 'Immobilisations financieres', '816', 'Ce compte enregistre la valeur des investissements financiers a long terme de l''entreprise, tels que les participations dans d''autres societes et les prets a long terme.'),
(68, 'Immobilisations incorporelles', '821', 'Ce compte represente la valeur des actifs non physiques detenus par l''entreprise, comme les brevets, les marques et les logiciels.'),
(68, 'Immobilisations corporelles', '822', 'Ce compte enregistre la valeur des actifs physiques de l''entreprise, tels que les terrains, les batiments, les machines et les equipements.'),
(68, 'Immobilisations financieres', '826', 'Ce compte contient la valeur des investissements financiers a long terme de l''entreprise, tels que les actions et les obligations detenues pour une periode prolongee.'),
(69, 'Charges H.A.O. constatees', '831', 'Ce compte enregistre les charges exceptionnelles liees a des evenements hors activites ordinaires, telles que des amendes, des penalites ou des pertes imprevues.'),
(69, 'Charges liees aux operations de restructuration', '833', 'Ce compte contient les charges encourues lors de la restructuration de l''entreprise, telles que les frais de licenciement ou les couts de reorganisation.'),
(69, 'Pertes sur creances H.A.O.', '834', 'Ce compte enregistre les pertes sur creances resultant d''evenements hors activites ordinaires, tels que des creances irrecouvrables provenant de tiers.'),
(69, 'Dons et liberalites accordes', '835', 'Ce compte contient la valeur des dons et des liberalites accordes par l''entreprise a des tiers, comme des œuvres de charite ou des contributions sociales.'),
(69, 'Abandons de creances consentis', '836', 'Ce compte enregistre les creances abandonnees volontairement par l''entreprise, souvent dans le cadre d''actions sociales ou de gestes commerciaux.'),
(69, 'Charges liees aux operations de liquidation', '837', 'Ce compte contient les charges encourues lors de la liquidation de l''entreprise, telles que les frais juridiques ou les couts de fermeture de sites.'),
(69, 'Charges pour depreciations et provisions pour risques a court terme H.A.O.', '839', 'Ce compte enregistre les charges exceptionnelles constituees pour couvrir des risques identifies a court terme hors activites ordinaires, tels que des litiges en cours ou des garanties a accorder aux clients.'),
(70, 'Produits H.A.O. constates', '841', 'Ce compte enregistre les produits exceptionnels lies a des evenements hors activites ordinaires, tels que des indemnites recues ou des gains imprevus.'),
(70, 'Produits lies aux operations de restructuration', '843', 'Ce compte contient les produits generes lors de la restructuration de l''entreprise, tels que les gains sur la vente d''actifs ou les indemnites de depart.'),
(70, 'Indemnites et subventions H.A.O. (entite agricole)', '844', 'Ce compte enregistre les indemnites et subventions exceptionnelles recues par l''entreprise agricole pour des evenements hors activites ordinaires.'),
(70, 'Dons et liberalites obtenus', '845', 'Ce compte contient la valeur des dons et des liberalites recus par l''entreprise, comme des donations ou des legs.'),
(70, 'Abandons de creances obtenus', '846', 'Ce compte enregistre les creances abandonnees par des tiers au profit de l''entreprise, souvent dans le cadre de negociations ou d''accords commerciaux.'),
(70, 'Produits lies aux operations de liquidation', '847', 'Ce compte contient les produits generes lors de la liquidation de l''entreprise, tels que les gains sur la vente d''actifs ou les recuperations de dettes.'),
(70, 'Transferts de charges H.A.O.', '848', 'Ce compte enregistre les transferts de charges exceptionnels lies a des evenements hors activites ordinaires, comme des corrections comptables ou des ajustements de provisions.'),
(70, 'Reprises de charges pour depreciations et provisions pour risques a court terme H.A.O.', '849', 'Ce compte contient les reprises de charges exceptionnelles pour depreciations et provisions liees a des evenements hors activites ordinaires, telles que des reprises de provisions pour litiges ou de provisions pour garanties. '),
(71, 'Dotations aux provisions reglementees', '851', 'Ce compte enregistre les dotations annuelles effectuees dans le cadre de provisions reglementees, telles que les provisions pour conges payes ou les provisions pour retraite.'),
(71, 'Dotations aux amortissements H.A.O.', '852', 'Ce compte contient les dotations annuelles specifiques destinees a amortir les actifs affectes a des activites hors activites ordinaires de l''entreprise.'),
(71, 'Dotations aux depreciations H.A.O.', '853', 'Ce compte enregistre les dotations annuelles specifiques destinees a deprecier les actifs affectes a des activites hors activites ordinaires de l''entreprise.'),
(71, 'Dotations aux provisions pour risques et charges H.A.O.', '854', 'Ce compte contient les dotations annuelles specifiques destinees a constituer des provisions pour couvrir des risques et des charges lies a des activites hors activites ordinaires de l''entreprise.'),
(71, 'Autres dotations H.A.O.', '858', 'Ce compte regroupe les autres dotations annuelles specifiques effectuees dans le cadre des activites hors activites ordinaires de l''entreprise et qui ne sont pas couvertes par les comptes precedents.'),
(72, 'Reprises de provisions reglementees', '861', 'Ce compte enregistre les reprises annuelles effectuees dans le cadre des provisions reglementees, telles que les reprises de provisions pour conges payes ou les reprises de provisions pour retraite.'),
(72, 'Reprises d''amortissements H.A.O.', '862', 'Ce compte contient les reprises annuelles specifiques effectuees dans le cadre des activites hors activites ordinaires de l''entreprise concernant les amortissements des actifs.'),
(72, 'Reprises de depreciations H.A.O.', '863', 'Ce compte enregistre les reprises annuelles specifiques effectuees dans le cadre des activites hors activites ordinaires de l''entreprise concernant les depreciations des actifs.'),
(72, 'Reprises de provisions pour risques et charges H.A.O.', '864', 'Ce compte contient les reprises annuelles specifiques effectuees dans le cadre des activites hors activites ordinaires de l''entreprise concernant les provisions pour risques et charges.'),
(72, 'Autres reprises H.A.O.', '868', 'Ce compte regroupe les autres reprises annuelles specifiques effectuees dans le cadre des activites hors activites ordinaires de l''entreprise et qui ne sont pas couvertes par les comptes precedents.'),
(73, 'Participation legale aux benefices', '871', 'Ce compte enregistre la participation obligatoire des salaries aux benefices de l''entreprise, conformement aux dispositions legales en vigueur dans le pays ou l''entreprise opere.'),
(73, 'Participation contractuelle aux benefices', '874', 'Ce compte enregistre la participation volontaire des salaries aux benefices de l''entreprise, selon les accords contractuels etablis entre l''employeur et les employes.'),
(73, 'Autres participations', '878', 'Ce compte regroupe les autres formes de participation des salaries aux benefices de l''entreprise qui ne sont pas prevues par la legislation ou par les accords contractuels.'),
(74, 'Etat', '881', 'Ce compte enregistre les sommes dues a l''Etat, telles que les impots sur les benefices, les taxes, les droits d''enregistrement, ainsi que les sommes a recevoir de l''Etat, comme les subventions ou les remboursements de taxes.'),
(74, 'Collectivites publiques', '884', 'Ce compte enregistre les sommes dues aux collectivites publiques locales ou regionales, telles que les taxes foncieres ou les taxes sur les salaires, ainsi que les sommes a recevoir de ces collectivites, comme les subventions ou les remboursements de taxes.'),
(74, 'Groupe', '886', 'Ce compte enregistre les transactions financieres entre les differentes entites composant le groupe auquel appartient l''entreprise, telles que les transferts de fonds, les prets ou les avances, ainsi que les sommes a recevoir ou a payer entre ces entites.'),
(74, 'Autres', '888', 'Ce compte regroupe les transactions financieres avec des entites autres que l''Etat, les collectivites publiques ou les entites du groupe, telles que les autres entreprises, les associations ou les particuliers, ainsi que les sommes a recevoir ou a payer a ces entites.'),
(75, 'Impots sur les benefices de l''exercice', '891', 'Ce compte enregistre le montant des impots sur les benefices a payer pour l''exercice en cours, calcule sur la base du resultat fiscal de l''entreprise.'),
(75, 'Rappel d''impots sur resultats anterieurs', '892', 'Ce compte enregistre les montants d''impots a regulariser ou a payer pour des exercices anterieurs, suite a des rectifications fiscales ou des redressements effectues par l''administration fiscale.'),
(75, 'Impot minimum forfaitaire (I.M.F.)', '895', 'Ce compte enregistre le montant de l''impot minimum forfaitaire a payer, qui est une imposition calculee sur la base du chiffre d''affaires ou d''un autre critere defini par la legislation fiscale, independamment du resultat fiscal de l''entreprise.'),
(75, 'Degrevements et annulations d''impots sur resultats anterieurs', '899', 'Ce compte enregistre les montants d''impots sur resultats anterieurs qui ont fait l''objet de degrevements ou d''annulations, suite a des decisions de justice ou a des accords avec l''administration fiscale.'),
(76, 'Engagements obtenus 901 a 904', '900', 'Ce compte regroupe les engagements contractes par l''entreprise vis-a-vis de tiers, notamment les engagements de financement, les engagements de garantie et les engagements reciproques.'),
(76, 'Engagements de financement obtenus', '901', 'Ce compte enregistre les engagements de financement obtenus par l''entreprise aupres de tiers, tels que des prets bancaires, des lignes de credit ou des emprunts obligataires.'),
(76, 'Engagements de garantie obtenus', '902', 'Ce compte regroupe les engagements de garantie obtenus par l''entreprise aupres de tiers, qui peuvent inclure des garanties accordees pour des emprunts contractes ou pour des transactions commerciales.'),
(76, 'Engagements reciproques', '903', 'Ce compte enregistre les engagements reciproques contractes par l''entreprise avec des tiers, qui impliquent des obligations mutuelles entre les parties concernees.'),
(76, 'Autres engagements obtenus', '904', 'Ce compte regroupe d''autres types d''engagements obtenus par l''entreprise aupres de tiers et qui ne sont pas specifiquement lies aux financements ou aux garanties.'),
(76, 'Engagements de financement accordes', '905', 'Ce compte enregistre les engagements de financement accordes par l''entreprise a des tiers, tels que des prets consentis ou des credits octroyes.'),
(76, 'Engagements de garantie accordes', '906', 'Ce compte regroupe les engagements de garantie accordes par l''entreprise a des tiers, qui peuvent inclure des garanties fournies pour soutenir des tiers dans leurs transactions ou leurs activites commerciales.'),
(76, 'Engagements reciproques', '907', 'Ce compte enregistre les engagements reciproques accordes par l''entreprise a des tiers, impliquant des obligations mutuelles entre les parties concernees.'),
(76, 'Autres engagements accordes', '908', 'Ce compte regroupe d''autres types d''engagements accordes par l''entreprise a des tiers et qui ne sont pas specifiquement lies aux financements ou aux garanties.'),
(77, 'Contrepartie des engagements obtenus', '901', 'Ce compte enregistre les contreparties des engagements de financement obtenus par l''entreprise aupres de tiers.'),
(77, 'Contrepartie des engagements obtenus', '902', 'Ce compte regroupe les contreparties des engagements de garantie obtenus par l''entreprise aupres de tiers.'),
(77, 'Contrepartie des engagements obtenus', '903', 'Ce compte enregistre les contreparties des engagements reciproques contractes par l''entreprise avec des tiers.'),
(77, 'Contrepartie des engagements obtenus', '904', 'Ce compte regroupe les contreparties des autres engagements obtenus par l''entreprise aupres de tiers.'),
(77, 'Contreparties des engagements', '911', 'Ce compte enregistre les contreparties des engagements de financement accordes par l''entreprise a des tiers.'),
(77, 'Contreparties des engagements', '912', 'Ce compte regroupe les contreparties des engagements de garantie accordes par l''entreprise a des tiers.'),
(77, 'Contreparties des engagements', '913', 'Ce compte enregistre les contreparties des engagements reciproques accordes par l''entreprise a des tiers.'),
(77, 'Contreparties des engagements', '914', 'Ce compte regroupe les contreparties des autres engagements accordes par l''entreprise a des tiers.'),
(77, 'Contrepartie des engagements accordes, 905 a 908', '915', 'Ce compte enregistre les contreparties des engagements de financement accordes par l''entreprise a des tiers.'),
(77, 'Contrepartie des engagements accordes, 905 a 908', '916', 'Ce compte regroupe les contreparties des engagements de garantie accordes par l''entreprise a des tiers.'),
(77, 'Contrepartie des engagements accordes, 905 a 908', '917', 'Ce compte enregistre les contreparties des engagements reciproques accordes par l''entreprise a des tiers.'),
(77, 'Contrepartie des engagements accordes, 905 a 908', '918', 'Ce compte regroupe les contreparties des autres engagements accordes par l''entreprise a des tiers.'),
(86, 'Dotation non consomptible sans droit de reprise', '101', 'Ce compte enregistre les dotations constituees pour des charges non consomptibles sans droit de reprise ulterieure.'),
(86, 'Dotation non consomptible avec droit de reprise', '102', 'Ce compte regroupe les dotations constituees pour des charges non consomptibles avec droit de reprise ulterieure.'),
(86, 'Droit d''entree', '103', 'Ce compte enregistre les droits d''entree percus par l''entreprise.'),
(86, 'Dotation consomptible', '104', 'Ce compte enregistre les dotations constituees pour des charges consomptibles.'),
(86, 'Ecarts de reevaluation', '106', 'Ce compte regroupe les ecarts de reevaluation constates lors de la reevaluation des actifs.'),
(87, 'Reserves statutaires ou contractuelles', '112', 'Ce compte contient les montants reserves conformement aux dispositions legales, reglementaires ou contractuelles.'),
(87, 'Autres reserves', '118', 'Ce compte regroupe les montants reserves qui ne relevent pas des reserves statutaires ou contractuelles, mais qui sont destines a des fins specifiques ou a renforcer la situation financiere de l''entreprise.'),
(88, 'Report a nouveau des excedents', '121', 'Ce compte enregistre les excedents reportes d''exercices anterieurs, qui sont reaffectes dans le bilan de l''entreprise.'),
(88, 'Resultat net en instance d''affectation', '128', 'Ce compte represente le resultat net de l''entreprise qui est en attente d''affectation. Il est utilise pour enregistrer les benefices ou les pertes qui doivent etre distribues ou reportes.'),
(88, 'Report a nouveau des deficits', '129', 'Ce compte enregistre les deficits reportes d''exercices anterieurs, qui sont reportes dans le bilan de l''entreprise.'),
(89, 'Excedent de l''exercice', '131', 'Ce compte enregistre l''excedent de l''exercice en cours, representant les benefices realises par l''entreprise apres deduction de toutes les charges et impots.'),
(89, 'Deficit de l''exercice', '139', 'Ce compte enregistre le deficit de l''exercice en cours, representant les pertes subies par l''entreprise apres prise en compte de toutes les charges et impots.'),
(90, 'Subventions d''equipement', '141', 'Ce compte enregistre les subventions recues par l''entreprise pour financer des investissements en equipements. Ces subventions sont generalement octroyees par des organismes publics ou des institutions pour soutenir le developpement economique.'),
(91, 'Provisions speciales de reevaluation', '154', 'Ce compte contient les provisions speciales constituees lors de la reevaluation d''actifs, notamment des immobilisations corporelles et des immobilisations financieres. Ces provisions sont etablies pour refleter les ecarts entre la valeur comptable precedente et la nouvelle valeur reevaluee des actifs concernes.'),
(91, 'Autres provisions reglementees et fonds assimiles', '158', 'Ce compte regroupe les provisions reglementees et les fonds assimiles qui sont constitues en conformite avec les reglementations en vigueur. Ces provisions peuvent inclure des provisions pour risques et charges specifiques, telles que les provisions pour conges payes ou les provisions pour restructuration, ainsi que d''autres fonds destines a des fins specifiques, telles que les fonds de garantie ou les fonds de retraite du personnel.'),
(92, 'Fonds projet de developpement -avances de fonds a justifier', '161', 'Ce compte enregistre les avances de fonds destinees a financer des projets de developpement specifiques. Les avances sont fournies par divers bailleurs de fonds ou organismes de financement et doivent etre justifiees par des depenses conformes aux objectifs du projet de developpement.'),
(92, 'Fonds affectes aux investissements du projet de developpement bailleurs de fonds', '162', 'Ce compte represente les fonds affectes aux investissements dans des projets de developpement specifiques, provenant de bailleurs de fonds. Ces fonds sont destines a financer des initiatives visant a ameliorer les conditions socio-economiques, telles que des programmes d''infrastructure ou des projets communautaires.'),
(92, 'Fonds affectes aux investissements du projet de developpement l''Etat', '163', 'Ce compte enregistre les fonds affectes par l''Etat aux investissements dans des projets de developpement. Ces fonds proviennent du budget de l''Etat et sont alloues a des initiatives visant a promouvoir la croissance economique, l''amelioration des services publics ou le developpement social.'),
(92, 'Fonds affectes aux investissements des autres organismes de financement assimiles', '164', 'Ce compte contient les fonds affectes par d''autres organismes de financement assimiles aux investissements dans des projets de developpement. Ces organismes peuvent inclure des institutions financieres internationales, des fonds de developpement regionaux ou d''autres entites fournissant un soutien financier pour des initiatives de developpement.'),
(92, 'Fonds affectes a un projet specifique', '165', 'Ce compte enregistre les fonds affectes a un projet specifique de developpement. Ces fonds sont reserves pour financer des programmes ou des initiatives particuliers, qui peuvent etre lies a des secteurs specifiques, des regions geographiques ou des objectifs de developpement particuliers.'),
(92, 'Fonds provenant de dons et legs d''immobilisations', '167', 'Ce compte represente les fonds provenant de dons et de legs specifiquement destines a l''acquisition ou au developpement d''immobilisations. Ces dons peuvent provenir de particuliers, d''organisations caritatives ou d''autres entites et sont utilises pour financer des projets tels que la construction d''infrastructures ou l''achat d''equipements.'),
(92, 'Autres fonds affectes', '168', 'Ce compte contient d''autres fonds specifiquement affectes a des fins particulieres, mais qui ne sont pas classes dans les categories precedentes. Ces fonds peuvent inclure des subventions gouvernementales pour des projets specifiques, des contributions de partenaires de developpement ou d''autres formes de financement dediees a des initiatives particulieres.'),
(92, 'Fonds affectes a recevoir', '169', 'Ce compte enregistre les fonds affectes qui sont attendus ou a recevoir dans un avenir proche. Ces fonds peuvent provenir de diverses sources, telles que des engagements de bailleurs de fonds, des promesses de dons ou des transferts gouvernementaux, et sont prevus pour etre utilises a des fins specifiques conformement aux conditions de financement.'),
(93, 'Donation temporaire d''usufruit', '171', 'Ce compte enregistre les donations temporaires d''usufruit, ou le donateur accorde temporairement le droit d''utiliser et de percevoir les revenus d''un actif a un beneficiaire, sans transfert de propriete. Cette donation peut etre accordee pour une duree determinee et expire a l''echeance specifiee.'),
(93, 'Donation et legs non encore recus d''immobilisations destinees a la vente', '172', 'Ce compte represente les donations et legs qui ont ete promis ou legues a l''entite, mais qui n''ont pas encore ete recus, et qui sont destines a etre utilises pour acquerir des immobilisations destinees a la vente. Ces dons peuvent provenir de particuliers, d''organisations caritatives ou d''autres entites et sont prevus pour financer des projets specifiques ou soutenir les activites de l''entite.'),
(94, 'Emprunts obligataires', '181', 'Ce compte enregistre les emprunts contractes par l''entite sous forme d''obligations emises sur le marche financier. Ces obligations representent des dettes a long terme de l''entite envers les investisseurs obligataires, qui sont generalement remboursees a l''echeance specifiee avec des interets periodiques.'),
(94, 'Emprunts et dettes aupres des etablissements de credit', '182', 'Ce compte represente les emprunts et les dettes contractes par l''entite aupres des etablissements de credit tels que les banques commerciales et autres institutions financieres. Ces dettes peuvent prendre la forme de prets a court, moyen ou long terme et peuvent etre utilisees pour financer diverses activites de l''entite.'),
(94, 'Avances recues de l''''Etat', '183', 'Ce compte enregistre les avances financieres recues de l''''Etat ou des autorites gouvernementales. Ces avances sont souvent accordees pour financer des projets specifiques ou pour fournir un soutien financier temporaire a l''''entite.'),
(94, 'Depots et cautionnements recus', '185', 'Ce compte represente les depots et cautionnements recus par l''''entite dans le cadre de transactions commerciales ou contractuelles. Ces depots sont generalement remboursables a l''''expiration du contrat ou lors de la realisation des conditions convenues entre les parties.'),
(94, 'Interets courus', '186', 'Ce compte enregistre les interets accumules mais non encore payes sur les dettes de l''''entite. Ces interets courus representent une obligation future de l''''entite envers ses creanciers et sont generalement comptabilises comme une charge dans les etats financiers.'),
(94, 'Dettes de location-acquisition', '187', 'Ce compte represente les obligations contractees par l''''entite dans le cadre de contrats de location-acquisition, ou l''''entite loue un actif avec une option d''''achat a la fin de la periode de location. Ces dettes sont generalement traitees comme des passifs a long terme dans les etats financiers de l''''entite.'),
(95, 'Provisions pour litiges', '191', 'Ce compte enregistre les provisions constituees par l''entite pour couvrir les couts potentiels associes a des litiges en cours ou anticipes. Ces litiges peuvent resulter de differends commerciaux, de responsabilites legales ou d''autres situations ou l''entite est impliquee dans des procedures judiciaires.'),
(95, 'Provision pour charges sur donations et legs', '192', 'Ce compte represente les provisions constituees par l''entite pour couvrir les couts potentiels associes a des charges futures sur les donations et legs recus. Ces provisions sont etablies en fonction des estimations de l''entite concernant les obligations futures decoulant des donations et legs.'),
(95, 'Provisions pour pertes de change', '194', 'Ce compte enregistre les provisions etablies par l''entite pour couvrir les pertes potentielles resultant de fluctuations defavorables des taux de change. Ces provisions sont constituees pour attenuer l''impact negatif des variations de change sur les transactions en devises etrangeres.'),
(95, 'Provisions pour pensions et obligations similaires', '196', 'Ce compte represente les provisions constituees par l''entite pour couvrir les obligations futures liees aux regimes de retraite et autres avantages du personnel. Ces provisions sont etablies en fonction des obligations estimees de l''entite envers ses employes pour les prestations de retraite et autres avantages sociaux.'),
(95, 'Autres provisions pour risques et charges', '198', 'Ce compte enregistre les provisions constituees par l''entite pour couvrir d''autres risques et charges potentiels qui ne sont pas specifiquement couverts par d''autres comptes de provisions. Ces provisions sont etablies en fonction des estimations de l''entite concernant les obligations futures.'),
(96, 'Immobilisations incorporelles', '201', 'Ce compte enregistre la valeur des actifs incorporels detenus par l''entite, tels que les brevets, les licences, les marques de commerce et les logiciels. Les immobilisations incorporelles sont des actifs non physiques qui ont une valeur economique pour l''entite et peuvent contribuer a sa performance future.'),
(96, 'Terrains', '202', 'Ce compte represente la valeur des terrains detenus par l''entite et utilises dans ses activites commerciales. Les terrains sont des actifs fixes qui ne sont pas sujets a l''amortissement car leur valeur est consideree comme durable et non depreciable.'),
(96, 'Batiments', '203', 'Ce compte enregistre la valeur des batiments detenus par l''entite et utilises dans ses activites commerciales. Les batiments sont des actifs fixes qui sont generalement soumis a l''amortissement car leur valeur diminue avec le temps en raison de l''usure et de l''obsolescence.'),
(96, 'Materiels', '204', 'Ce compte represente la valeur des equipements et des machines detenus par l''entite et utilises dans ses operations commerciales. Les materiels comprennent une gamme d''actifs physiques tels que des machines, des outils et des vehicules utilises pour la production ou la prestation de services.'),
(96, 'Titres de participations', '205', 'Ce compte enregistre la valeur des participations detenues par l''entite dans d''autres societes. Les titres de participations representent des investissements a long terme dans des entreprises ou l''entite detient une participation significative et exerce une influence sur les decisions de gestion.'),
(97, 'Brevets, licences, concessions et droits similaires', '212', 'Ce compte represente la valeur des brevets, des licences, des concessions et des droits similaires detenus par l''entite. Ces actifs incorporels sont souvent acquis par l''entite pour proteger ses inventions, ses processus ou ses droits d''utilisation de propriete intellectuelle.'),
(97, 'Logiciels et sites internet', '213', 'Ce compte enregistre la valeur des logiciels et des sites internet detenus par l''entite. Les logiciels et les sites internet sont des actifs incorporels qui ont une valeur economique pour l''entite et peuvent contribuer a ses activites operationnelles, de marketing ou de communication.'),
(97, 'Marques', '214', 'Ce compte represente la valeur des marques detenues par l''entite. Les marques sont des actifs incorporels qui representent la reputation, la notoriete et la valeur de la marque de l''entite sur le marche. Elles peuvent inclure des marques deposees, des marques commerciales ou des noms commerciaux.'),
(97, 'Autres droits et valeurs incorporels', '218', 'Ce compte enregistre la valeur des autres droits et valeurs incorporels detenus par l''entite, qui ne sont pas specifiquement couverts par d''autres comptes. Il peut s''agir, par exemple, de droits de propriete litteraire et artistique, de droits de propriete industrielle ou de droits contractuels.'),
(97, 'Immobilisations incorporelles en cours', '219', 'Ce compte represente la valeur des immobilisations incorporelles en cours de developpement ou de construction par l''entite. Il s''agit generalement de projets en cours qui ne sont pas encore acheves ou qui n''ont pas encore ete mis en service.'),
(98, 'Terrains agricoles et forestiers', '221', 'Ce compte represente la valeur des terrains detenus par l''entite a des fins agricoles ou forestieres. Il peut inclure des terres utilisees pour la culture de cultures agricoles, l''elevage du betail ou la plantation d''arbres a des fins commerciales.'),
(98, 'Terrains nus', '222', 'Ce compte enregistre la valeur des terrains detenus par l''entite qui ne sont pas developpes ou utilises. Ces terrains peuvent etre acquis en vue de projets futurs ou comme investissement.'),
(98, 'Terrains batis', '223', 'Ce compte represente la valeur des terrains detenus par l''entite sur lesquels des batiments ou des structures ont ete construits. Il peut inclure des terrains utilises pour les installations industrielles, commerciales ou residentielles.'),
(98, 'Travaux de mise en valeur des terrains', '224', 'Ce compte enregistre les couts engages par l''entite pour mettre en valeur des terrains acquis. Il peut inclure des depenses telles que la preparation du sol, la construction de routes d''acces ou l''installation de services publics.'),
(98, 'Terrains amenages', '226', 'Ce compte represente la valeur des terrains detenus par l''entite qui ont ete amenages ou ameliores pour repondre a des besoins specifiques. Il peut inclure des terrains equipes de services publics, de parkings ou d''autres installations.'),
(98, 'Autres terrains', '228', 'Ce compte enregistre la valeur des terrains detenus par l''entite qui ne sont pas specifiquement couverts par d''autres comptes. Il peut s''agir, par exemple, de terrains acquis pour des projets de developpement futurs ou pour des besoins de stockage.'),
(98, 'Amenagements de terrains en cours', '229', 'Ce compte represente la valeur des amenagements de terrains en cours de developpement ou de construction par l''entite. Il peut inclure des projets d''amenagement tels que l''installation de services, la preparation du sol ou la construction de routes.'),
(99, 'Batiments industriels, agricoles, administratifs, commerciaux, religieux et autres sur sol propre', '231', 'Ce compte enregistre la valeur des batiments detenus par l''entite et construits sur des terrains appartenant a l''entite elle-meme. Il peut inclure des batiments utilises a des fins industrielles, agricoles, administratives, commerciales, religieuses ou autres.'),
(99, 'Batiments industriels, agricoles, administratifs, commerciaux, religieux et autres sur sol d''autrui', '232', 'Ce compte represente la valeur des batiments detenus par l''entite mais construits sur des terrains appartenant a des tiers. Il peut inclure des batiments loues ou utilises dans le cadre de contrats de location.'),
(99, 'Ouvrages d''infrastructure', '233', 'Ce compte enregistre la valeur des ouvrages d''infrastructure detenus par l''entite. Il peut inclure des ponts, des routes, des tunnels, des reseaux d''adduction d''eau, des reseaux d''assainissement ou d''autres infrastructures similaires.'),
(99, 'Amenagements, agencements et installations techniques', '234', 'Ce compte represente la valeur des amenagements, agencements et installations techniques detenus par l''entite. Il peut inclure des amenagements interieurs, des equipements de production, des installations de chauffage, de ventilation, de climatisation ou d''autres equipements techniques.'),
(99, 'Amenagements de bureaux et assimiles', '235', 'Ce compte enregistre la valeur des amenagements specifiques realises dans des bureaux ou des locaux assimiles. Il peut inclure des amenagements tels que des cloisons, des equipements de bureau, des installations electriques ou des systemes de communication.'),
(99, 'Autres installations et agencements', '238', 'Ce compte represente la valeur des installations et agencements detenus par l''entite qui ne sont pas specifiquement couverts par d''autres comptes. Il peut s''agir, par exemple, d''installations de loisirs, d''agencements paysagers ou d''autres equipements.'),
(99, 'Batiments, amenagements, agencements et installations techniques en cours', '239', 'Ce compte enregistre la valeur des batiments, amenagements, agencements et installations techniques en cours de developpement ou de construction par l''entite. Il peut inclure des projets de construction de nouveaux batiments, d''amenagements interieurs ou d''installations techniques.'),
(100, 'Materiel et outillage industriel et commercial', '241', 'Ce compte enregistre la valeur du materiel et de l''outillage utilises dans les activites industrielles et commerciales de l''entite. Il peut inclure des machines, des equipements de production, des outils et d''autres equipements utilises dans le processus de fabrication ou de vente.'),
(100, 'Materiel et outillage agricole', '242', 'Ce compte represente la valeur du materiel et de l''outillage utilises dans les activites agricoles de l''entite. Il peut inclure des tracteurs, des equipements de labour, des outils agricoles et d''autres equipements utilises dans la production agricole.'),
(100, 'Materiel d''emballage recuperable et identifiable', '243', 'Ce compte enregistre la valeur du materiel d''emballage qui peut etre recupere et identifie apres utilisation. Il peut inclure des emballages reutilisables tels que des caisses en bois, des palettes ou des conteneurs.'),
(100, 'Materiel et mobilier', '244', 'Ce compte represente la valeur du materiel et du mobilier utilises dans les bureaux et les locaux de l''entite. Il peut inclure des bureaux, des chaises, des armoires, des etageres, des ordinateurs et d''autres equipements de bureau.'),
(100, 'Materiel de transport', '245', 'Ce compte enregistre la valeur du materiel de transport detenu par l''entite. Il peut inclure des vehicules automobiles, des camions, des avions, des navires ou d''autres moyens de transport utilises dans les operations commerciales.'),
(100, 'Actifs biologiques', '246', 'Ce compte represente la valeur des actifs biologiques detenus par l''entite, tels que des cultures vivrieres, des plantations d''arbres fruitiers ou des troupeaux d''animaux. Ces actifs sont utilises dans les activites agricoles ou de production alimentaire.'),
(100, 'Agencements, amenagements du materiel et des actifs biologiques', '247', 'Ce compte enregistre la valeur des agencements et des amenagements realises sur le materiel et les actifs biologiques detenus par l''entite. Il peut inclure des modifications apportees aux equipements pour ameliorer leur fonctionnement ou leur efficacite.'),
(100, 'Autres materiels et mobilier', '248', 'Ce compte represente la valeur d''autres materiels et mobilier detenus par l''entite qui ne sont pas specifiquement couverts par d''autres comptes. Il peut inclure divers equipements ou outils utilises dans les operations commerciales.'),
(100, 'Materiels et actifs biologiques en cours', '249', 'Ce compte enregistre la valeur des materiels et actifs biologiques en cours d''acquisition ou de developpement par l''entite. Il peut inclure des equipements commandes mais non encore livres, ainsi que des cultures ou des troupeaux en cours de croissance.'),
(101, 'Avances et acomptes verses sur immobilisations incorporelles', '251', 'Ce compte enregistre les avances et acomptes verses pour l''acquisition ou la construction d''immobilisations incorporelles, telles que des brevets, des licences ou des logiciels. Ces avances sont comptabilisees avant la reception ou la finalisation de l''immobilisation correspondante.'),
(101, 'Avances et acomptes verses sur immobilisations corporelles', '252', 'Ce compte represente les avances et acomptes verses pour l''acquisition ou la construction d''immobilisations corporelles, telles que des terrains, des batiments ou des equipements. Ces avances sont comptabilisees avant la reception ou la finalisation de l''immobilisation correspondante.'),
(102, 'Titres de participation', '261', 'Ce compte enregistre la valeur des titres detenus par l''entreprise et representant une participation dans le capital d''autres societes. Ces participations sont generalement detenues dans une perspective de controle ou d''influence significative sur les activites de ces societes.'),
(102, 'Participations dans des organismes professionnels', '265', 'Ce compte concerne les participations detenues par l''entreprise dans des organismes professionnels, tels que des chambres de commerce, des associations professionnelles ou des syndicats. Ces participations peuvent etre detenues pour representer les interets de l''entreprise dans ces organismes ou pour beneficier de certains avantages.'),
(102, 'Parts dans les groupements d''interet economique (G.I.E)', '266', 'Ce compte concerne les parts detenues par l''entreprise dans les groupements d''interet economique (G.I.E). Les G.I.E sont des structures juridiques qui permettent a des entreprises de collaborer dans le cadre de projets specifiques tout en conservant leur autonomie juridique.'),
(102, 'Autres titres de participation', '268', 'Ce compte regroupe les autres titres de participation detenus par l''entreprise qui ne rentrent pas dans les categories precedentes. Il peut s''agir de participations dans des societes non cotees en bourse ou dans des entites dont l''activite principale ne correspond pas aux autres categories de participations.'),
(103, 'Prets et creances', '271', 'Ce compte enregistre les prets et creances consentis par l''entreprise a des tiers. Il inclut notamment les sommes dues par des clients, des fournisseurs ou d''autres debiteurs en raison de transactions commerciales ou financieres.'),
(103, 'Prets au personnel', '272', 'Ce compte concerne les prets consentis par l''entreprise a son personnel. Ces prets peuvent etre accordes a des conditions avantageuses ou a des fins specifiques, telles que l''acquisition de logement ou le financement d''etudes.'),
(103, 'Creances sur l''Etat', '273', 'Ce compte regroupe les creances detenues par l''entreprise sur l''Etat, notamment les montants dus au titre de taxes, impots, subventions ou autres creances resultant de transactions avec les administrations publiques.'),
(103, 'Titres immobilises', '274', 'Ce compte concerne les titres detenus par l''entreprise a des fins d''investissement a long terme. Il peut s''agir notamment de titres de participation, d''obligations ou d''actions de societes.'),
(103, 'Depots et cautionnements verses', '275', 'Ce compte enregistre les sommes versees par l''entreprise a titre de depots ou de cautionnements, par exemple pour garantir l''execution d''un contrat ou pour obtenir un permis ou une licence.'),
(103, 'Interets courus', '276', 'Ce compte concerne les interets qui sont dus mais qui n''ont pas encore ete payes ou recus a la date de cloture de l''exercice. Il s''agit des interets qui ont ete generes mais qui n''ont pas encore ete comptabilises.'),
(103, 'Immobilisations financieres diverse', '278', 'Ce compte regroupe les autres immobilisations financieres detenues par l''entreprise qui ne rentrent pas dans les categories precedentes. Il peut s''agir par exemple de prets consentis a des filiales ou a des partenaires commerciaux.'),
(104, 'Amortissements d''usufruit temporaire', '280', 'Ce compte enregistre les amortissements comptables lies a l''usufruit temporaire sur des actifs detenus par l''entreprise. L''usufruit temporaire est un droit reel conferant a son titulaire le droit d''user et de jouir de l''actif pour une duree limitee.'),
(104, 'Amortissements des immobilisations incorporelles', '281', 'Ce compte concerne les amortissements comptables appliques aux immobilisations incorporelles detenues par l''entreprise. Les immobilisations incorporelles comprennent notamment les brevets, les licences, les marques et les logiciels.'),
(104, 'Amortissements des terrains', '282', 'Ce compte enregistre les amortissements comptables applicables aux terrains detenus par l''entreprise. Contrairement aux autres types d''immobilisations, les terrains ne sont pas amortissables selon les principes comptables generalement admis.'),
(104, 'Amortissements des batiments, installations techniques et agencements', '283', 'Ce compte concerne les amortissements comptables relatifs aux batiments, aux installations techniques et aux agencements detenus par l''entreprise. Ces amortissements representent la depreciation progressive de la valeur de ces actifs.'),
(104, 'Amortissements du materiel', '284', 'Ce compte enregistre les amortissements comptables appliques au materiel detenu par l''entreprise. Le materiel comprend notamment les equipements, les machines, les outils et les vehicules utilises dans le cadre de l''activite commerciale ou industrielle de l''entreprise.'),
(105, 'Depreciations des immobilisations destinees a la vente provenant de dons et legs non encore recus et d''usufruit temporaire', '290', 'Ce compte enregistre les depreciations comptables appliquees aux immobilisations destinees a la vente qui proviennent de dons et legs non encore recus ainsi que d''usufruit temporaire. Ces depreciations refletent la diminution de la valeur de ces actifs par rapport a leur valeur comptable initiale.'),
(105, 'Depreciations des immobilisations incorporelles', '291', 'Ce compte concerne les depreciations comptables appliquees aux immobilisations incorporelles detenues par l''entreprise. Les depreciations sont constatees lorsque la valeur recouvrable d''une immobilisation est estimee etre inferieure a sa valeur comptable nette.'),
(105, 'Depreciations des terrains', '292', 'Ce compte enregistre les depreciations comptables appliquees aux terrains detenus par l''entreprise. Les depreciations sont constatees lorsque la valeur recouvrable d''un terrain est estimee etre inferieure a sa valeur comptable nette.'),
(105, 'Depreciations des batiments, installations techniques et agencements', '293', 'Ce compte concerne les depreciations comptables appliquees aux batiments, installations techniques et agencements detenus par l''entreprise. Les depreciations sont constatees lorsque la valeur recouvrable de ces actifs est estimee etre inferieure a leur valeur comptable nette.'),
(105, 'Depreciations de materiel, du mobilier et de l''actif biologique', '294', 'Ce compte enregistre les depreciations comptables appliquees au materiel, au mobilier et a l''actif biologique detenus par l''entreprise. Les depreciations sont constatees lorsque la valeur recouvrable de ces actifs est estimee etre inferieure a leur valeur comptable nette.'),
(105, 'Depreciations des avances et acomptes verses sur immobilisations', '295', 'Ce compte concerne les depreciations comptables appliquees aux avances et acomptes verses sur immobilisations. Les depreciations sont constatees lorsque la valeur recouvrable des immobilisations acquises a l''aide de ces avances et acomptes est estimee etre inferieure a leur valeur comptable nette.'),
(105, 'Depreciations des titres de participations', '296', 'Ce compte enregistre les depreciations comptables appliquees aux titres de participations detenus par l''entreprise. Les depreciations sont constatees lorsque la valeur recouvrable de ces titres est estimee etre inferieure a leur valeur comptable nette.'),
(105, 'Depreciations des autres immobilisations financieres', '297', 'Ce compte concerne les depreciations comptables appliquees aux autres immobilisations financieres detenues par l''entreprise. Les depreciations sont constatees lorsque la valeur recouvrable de ces actifs est estimee etre inferieure a leur valeur comptable nette.'),
(106, 'Biens lies a l''activite A', '311', 'Cette categorie de comptes concerne les biens et les actifs specifiquement utilises dans le cadre de l''activite A de l''entreprise, tels que les equipements, les materiels, ou les locaux dedies a cette activite.'),
(106, 'Biens lies a l''activite B', '312', 'Cette categorie de comptes concerne les biens et les actifs specifiquement utilises dans le cadre de l''activite B de l''entreprise, tels que les equipements, les materiels, ou les locaux dedies a cette activite.'),
(107, 'Marchandises A', '321', 'Ce compte represente les marchandises specifiquement liees a l''activite A de l''entreprise, generalement destinees a la revente.'),
(107, 'Marchandises B', '322', 'Ce compte represente les marchandises specifiquement liees a l''activite B de l''entreprise, generalement destinees a la revente.'),
(107, 'Matieres A', '323', 'Ce compte represente les matieres premieres specifiquement utilisees dans le cadre de l''activite A de l''entreprise, avant leur transformation.'),
(107, 'Matieres B', '324', 'Ce compte represente les matieres premieres specifiquement utilisees dans le cadre de l''activite B de l''entreprise, avant leur transformation.'),
(107, 'Fournitures liees (A, B)', '325', 'Ce compte represente les fournitures qui sont utilisees a la fois dans le cadre de l''activite A et de l''activite B de l''entreprise.'),
(108, 'Matieres consommables', '331', 'Ce compte represente les matieres qui sont utilisees dans le processus de production et qui sont entierement consommees.'),
(108, 'Fournitures de magasin', '333', 'Ce compte represente les fournitures utilisees dans les magasins de l''entreprise.'),
(108, 'Fournitures de bureau', '334', 'Ce compte represente les fournitures utilisees dans les bureaux de l''entreprise, telles que le papier, les stylos, etc.'),
(108, 'Emballages', '335', 'Ce compte represente les materiaux d''emballage utilises pour conditionner les produits finis de l''entreprise.'),
(108, 'Autres matieres', '338', 'Ce compte represente d''autres types de matieres utilisees dans l''entreprise qui ne sont pas specifiquement categorisees ailleurs.'),
(109, 'Dons en nature non affectes', '341', 'Ce compte represente les dons recus sous forme de biens materiels qui ne sont pas specifiquement destines a un projet ou une activite particuliere de l''entreprise.'),
(109, 'Dons en nature affectes', '345', 'Ce compte represente les dons recus sous forme de biens materiels qui sont specifiquement destines a un projet ou une activite particuliere de l''entreprise.'),
(111, 'Actifs biologiques', '363', 'Ce compte represente les actifs biologiques detenus par l''entreprise, tels que les cultures, le betail ou les arbres destines a la production agricole ou forestiere.'),
(112, 'Biens lies a l''activite en cours de route', '371', 'Ce compte concerne les biens en transit ou en cours de transport lies a l''activite de l''entreprise, qui n''ont pas encore atteint leur destination finale.'),
(112, 'Marchandises, matieres premieres et fournitures lies en cours de route', '372', 'Ce compte englobe les marchandises, les matieres premieres et les fournitures qui sont en cours de transport vers l''entreprise mais n''ont pas encore ete receptionnees.'),
(112, 'Autres approvisionnements en cours de route', '373', 'Il s''agit d''autres types d''approvisionnements ou de biens en cours de transport vers l''entreprise, tels que les equipements ou les fournitures non directement lies a la production.'),
(112, 'Produits finis en cours de route', '376', 'Ce compte concerne les produits finis qui sont en cours de transport vers les clients ou les points de vente, mais qui n''ont pas encore ete livres.'),
(112, 'Stock en consignation ou en depot', '377', 'Ce compte represente les biens detenus en consignation ou en depot par l''entreprise pour le compte de tiers, mais qui ne sont pas encore en sa possession definitive.'),
(112, 'Stock provenant d''immobilisations mises hors services ou au rebut', '378', 'Il s''agit de biens provenant d''immobilisations (materiels, machines, equipements) qui ont ete retires du service ou mis au rebut et sont en attente de leur disposition finale.'),
(113, 'non affectes', '381', 'Ce compte concerne les dons en nature qui sont recus par l''entreprise sans affectation specifique a un projet ou une activite particuliere.'),
(113, 'Affectes', '385', 'Il s''agit des dons en nature qui sont recus par l''entreprise avec une affectation specifique a un projet ou une activite determinee.'),
(114, 'Depreciations des stocks de biens lies a l''activite', '391', 'Ce compte enregistre les depreciations des stocks de biens directement lies a l''activite principale de l''entreprise, tels que les marchandises, les matieres premieres et les fournitures liees.'),
(114, 'Depreciations des stocks de marchandises, et matieres premieres et fournitures liees', '392', 'Il enregistre les depreciations specifiquement sur les stocks de marchandises, matieres premieres et fournitures liees.'),
(114, 'Depreciations des stocks d''autres approvisionnements', '393', 'Ce compte concerne les depreciations des stocks d''autres approvisionnements, distincts des marchandises, matieres premieres et fournitures liees.'),
(114, 'Depreciations des stocks de produits finis, produits intermediaires et residuels', '396', 'Il enregistre les depreciations specifiquement sur les stocks de produits finis, produits intermediaires et residuels.'),
(114, 'Depreciations des stocks en cours de route, en consignation ou en depot', '397', 'Ce compte enregistre les depreciations des stocks en cours de route, en consignation ou en depot, c''est-a-dire ceux qui ne sont pas encore integres au stock principal de l''entreprise.'),
(115, 'Fournisseurs, dettes en compte', '401', 'Ce compte enregistre les dettes dues aux fournisseurs pour lesquelles aucune traite ou effet a payer n''est emis.'),
(115, 'Fournisseurs, effets a payer', '402', 'Il enregistre les dettes dues aux fournisseurs pour lesquelles des traites ou effets de commerce sont emis.'),
(115, 'Fournisseurs, factures non parvenues', '408', 'Ce compte concerne les charges deja engagees mais non encore facturees par les fournisseurs a la date de cloture de l''exercice.'),
(115, 'Fournisseurs debiteurs', '409', 'Ce compte enregistre les montants dus par les fournisseurs a l''entreprise en cas de retours de marchandises ou d''avantages accordes par ces fournisseurs.'),
(116, 'Adherents', '411', 'Ce compte enregistre les montants dus par les membres ou adherents de l''entreprise, tels que des cotisations ou des contributions.'),
(116, 'Clients-usagers', '412', 'Il enregistre les montants dus par les clients ou usagers pour des biens ou services fournis par l''entreprise.'),
(116, 'Adherents, clients-usagers, cheques, effets et autres valeurs impayes', '413', 'Ce compte concerne les montants dus par les membres, clients ou usagers de l''entreprise qui n''ont pas encore ete encaisses, soit en raison de cheques ou d''effets non encore honores, soit pour d''autres valeurs impayees.'),
(116, 'Creances, adherents, clients-usagers litigieuses ou douteuses', '416', 'Il enregistre les montants dus par les membres, clients ou usagers dont le recouvrement est incertain en raison de litiges ou de doutes sur leur solvabilite.'),
(116, 'Adherents, Clients-usagers crediteurs', '419', 'Ce compte concerne les montants que les membres, clients ou usagers doivent a l''entreprise en cas de remboursement, de retour de marchandises ou d''autres credits.'),
(117, 'Personnel, avances et acomptes', '421', 'Ce compte enregistre les avances et les acomptes verses au personnel pour des depenses futures ou des salaires anticipes.'),
(117, 'Personnel, remunerations dues', '422', 'Il enregistre les salaires et les autres remunerations qui sont dus au personnel mais qui n''ont pas encore ete payes.'),
(117, 'Personnel, oppositions, saisies arrets', '423', 'Ce compte concerne les montants saisis sur les salaires ou les autres remunerations du personnel en raison d''oppositions ou de saisies arrets.'),
(117, 'Personnel, œuvres, sociales internes', '424', 'Il enregistre les montants alloues par l''entreprise pour financer des œuvres sociales internes au profit du personnel, telles que des activites culturelles, sportives ou de loisirs.'),
(117, 'Representants du personnel', '425', 'Ce compte concerne les montants alloues aux representants du personnel pour leurs activites au sein de l''entreprise, comme les delegues du personnel ou les membres du comite d''entreprise.'),
(117, 'Personnel-depots', '427', 'Il enregistre les montants deposes par le personnel a titre de caution ou de garantie, tels que des depots de cles ou des cautionnements.'),
(117, 'Personnel, charges a payer et produits a recevoir', '428', 'Ce compte enregistre les charges sociales dues par l''entreprise sur les salaires et autres remunerations du personnel, ainsi que les produits a recevoir correspondants.'),
(118, 'Securite sociale', '431', 'Ce compte concerne les montants dus a la securite sociale pour les cotisations sociales prelevees sur les salaires et autres remunerations versees par l''entreprise.'),
(118, 'Caisses de retraite', '432', 'Il enregistre les montants dus aux caisses de retraite pour les cotisations de retraite prelevees sur les salaires et autres remunerations versees par l''entreprise.'),
(118, 'Autres organismes sociaux', '433', 'Ce compte concerne les montants dus a d''autres organismes sociaux pour des cotisations ou contributions obligatoires autres que celles relatives a la securite sociale ou aux caisses de retraite.'),
(118, 'Organismes sociaux, charges a payer et produits a recevoir', '438', 'Ce compte enregistre les charges sociales dues par l''entreprise sur les salaires et autres remunerations du personnel, ainsi que les produits a recevoir correspondants pour les sommes encaissees d''avance.'),
(119, 'Etat, autres impots et taxes', '442', 'Ce compte concerne les montants dus a l''Etat pour d''autres impots et taxes que ceux specifiquement lies a la TVA ou a la retenue a la source.'),
(119, 'Etat, T.V.A. Facturee', '443', 'Il enregistre le montant de la TVA facturee par l''entreprise sur ses ventes de biens ou de services.'),
(119, 'Etat, T.V.A. due ou credit de T.V.A.', '444', 'Ce compte enregistre la TVA due a l''Etat sur les ventes (TVA collectee) ou le credit de TVA a reporter sur la periode suivante (TVA deductible).'),
(119, 'Etat, T.V.A. Recuperable', '445', 'Il enregistre le montant de la TVA que l''entreprise peut recuperer sur ses achats de biens et services, c''est-a-dire la TVA payee sur ses depenses (TVA deductible).'),
(119, 'Etat, autres taxes sur le chiffre d''affaires', '446', 'Ce compte concerne les montants dus a l''Etat pour d''autres taxes directement liees au chiffre d''affaires de l''entreprise.'),
(119, 'Etat, impots retenus a la source', '447', 'Il enregistre les montants d''impots preleves a la source sur les revenus verses par l''entreprise, tels que l''impot sur le revenu ou la contribution sociale generalisee (CSG).'),
(119, 'ETat, charges a payer produits a recevoir', '448', 'Ce compte enregistre les charges fiscales dues par l''entreprise et les produits fiscaux a recevoir correspondants pour les sommes encaissees d''avance.'),
(119, 'Etat, creances et dettes diverses', '449', 'Ce compte concerne les creances et dettes de l''entreprise envers l''Etat qui ne sont pas liees a des impots ou taxes specifiques, telles que des penalites, des amendes ou des remboursements.'),
(120, 'Association et assimilees', '451', 'Ce compte concerne les montants dus par l''entreprise a des associations ou organisations a but non lucratif.'),
(120, 'Fondations et assimilees', '452', 'Il enregistre les montants dus par l''entreprise a des fondations ou organisations similaires a but non lucratif.'),
(120, 'Ordres professionnels', '453', 'Ce compte concerne les montants dus par l''entreprise a des ordres professionnels, tels que les barreaux d''avocats ou les chambres de commerce.'),
(120, 'Organisations politiques', '454', 'Il enregistre les montants dus par l''entreprise a des organisations politiques.'),
(120, 'Organisations religieuses, apporteurs', '456', 'Ce compte concerne les montants dus par l''entreprise a des organisations religieuses ou a des contributeurs financiers de ces organisations.'),
(120, 'Mecenes, benevoles et assimiles', '457', 'Il enregistre les montants dus par l''entreprise a des mecenes, benevoles ou autres contributeurs a ses activites.'),
(120, 'Autres fondateurs et apporteurs', '458', 'Ce compte concerne les montants dus par l''entreprise a d''autres fondateurs ou apporteurs de fonds.'),
(121, 'Bailleurs-projet de developpement, Fonds d''administration', '462', 'Ce compte enregistre les montants dus par l''entreprise aux bailleurs de fonds dans le cadre de projets de developpement, destines a l''administration de ces fonds.'),
(121, 'Etat-projet de developpement, Fonds d''administration', '463', 'Il enregistre les montants dus par l''entreprise a l''Etat dans le cadre de projets de developpement, destines a l''administration de ces fonds.'),
(121, 'Autres tiers ou organismes de financement assimiles projet de developpement, Fond d''administration', '464', 'Ce compte concerne les montants dus par l''entreprise a d''autres tiers ou organismes de financement assimiles dans le cadre de projets de developpement, destines a l''administration de ces fonds.'),
(121, 'Fonds d''administration a recevoir', '469', 'Il enregistre les montants a recevoir par l''entreprise au titre des fonds d''administration dans le cadre de projets de developpement.'),
(121, 'Ecarts de conversion-passif', '479', 'Ce compte enregistre les ecarts de conversion passifs qui resultent de la traduction en monnaie de presentation d''elements du bilan exprimes dans une monnaie etrangere.'),
(122, 'Debiteurs et crediteurs divers', '471', 'Ce compte enregistre les creances et les dettes de l''entreprise qui ne sont pas classees dans d''autres categories specifiques.'),
(122, 'Creances et dettes sur titres de placement', '472', 'Il concerne les creances et les dettes de l''entreprise liees a des titres de placement.'),
